INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(1, 2, '', 0, '', '0', 'Thiên Minh Nơi Quần Hùng Hội Tụ', 'thien-minh-noi-quan-hung-hoi-tu', '<p><strong><em>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</em></strong></p>\n\n<p>Tr&ecirc;n con đường thống nhất giang sơn của những anh h&ugrave;ng th&igrave; &quot; Ti&ecirc;n Minh &quot; l&agrave; phần kh&ocirc;ng thể thiếu. C&oacute; thể n&oacute;i n&oacute; l&agrave; nơi tập trung anh em, bằng hữu c&ugrave;ng chung ch&iacute; hướng tạo dựng một cơ đồ b&aacute; chủ giang sơn .</p>\n\n<p><img alt="" src="/static/uploads/editor/tienmin.png" style="height:489px; width:987px" /></p>\n\n<p>Trong<strong> &quot; Ti&ecirc;n Minh &quot; </strong>sẽ c&oacute; v&ocirc; số t&iacute;nh năng v&agrave; hoạt động cũng như kỹ năng tăng lực chiến cho to&agrave;n thể anh h&ugrave;ng tham gia v&agrave;o n&oacute;.</p>\n\n<p><em>Ch&uacute;c c&aacute;c bằng hữu tr&atilde;i nghiệm Truy Mộng vui vẻ.</em></p>\n\n<p>&nbsp;</p>\n', 'Trên con đường thống nhất giang sơn của những anh hùng thì " Tiên Minh " ', '0', 1, '0', '1970-01-01', '', '2014-11-27 17:05:34', '2014-11-26 16:41:27', '0', ''),
(59, 1, '', 0, '', '0', 'Tưng Bừng Khuyến Mãi KNB Cuối Năm', 'tung-bu-ng-khuye-n-ma-i-knb-cuo-i-nam', '<p>Th&acirc;n chào quý bằng hữu.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/503x2709.png" style="height:271px; width:503px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp;Nh&acirc;n dịp cu&ocirc;́i năm chu&acirc;̉n bị đón 1 năm mới tràn đ&acirc;y ni&ecirc;̀m vui, BQT tạo th&ecirc;m đi&ecirc;̀u ki&ecirc;̣n trải nghi&ecirc;̣m và khám phá game bằng Event Khuy&ecirc;́n mãi 50% b&acirc;́t kì m&ecirc;̣nh giá Card, kèm theo đó sẽ có 1 s&ocirc;́ món quà h&acirc;́p d&acirc;̃n trong thời gian.... bí m&acirc;̣</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:#FFFF00">Thời gian </span>:</strong> 9h30 Ngày 29/12/2014 đ&ecirc;́n 23h59 Ngày 31/12/2014</p>\n\n<p><strong><span style="color:#FFFF00">Phạm Vi :</span></strong> T&acirc;́t cả máy chủ Truy m&ocirc;̣ng</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><span style="font-size:16px"><strong>Lưu Ý </strong>: Kèm theo đó là BQT xin đóng Event &quot;&nbsp;H&agrave;o Hoa Ngất Trời &quot; và sẽ ti&ecirc;́p tục trong thời gian sau&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFFFF"><span style="font-size:16px">Chúc quý bằng hữu vui vẻ.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'Thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2014-12-29 09:18:50', '2014-12-29 09:17:48', '0', ''),
(58, 1, '', 0, '', '0', 'Sự Kiện Tích Lũy Nhận Lì Xì Khủng Cuối Năm', 'su-kien-tich-luy-nhan-li-xi-khung-cuoi-nam', '<p>Th&acirc;n chào quý bằng hữu.</p>\n\n<p>&nbsp; &nbsp; &nbsp;Nh&acirc;n dịp cu&ocirc;́i năm BQT ra mắt Event&nbsp;&nbsp;&quot;&nbsp;Sự Kiện T&iacute;ch Lũy Nhận L&igrave; X&igrave; Khủng Cuối Năm &quot; nhằm tạo th&ecirc;m hành trang cho quý bằng hữu c&acirc;̀n thi&ecirc;́t tr&ecirc;n con đường hành đạo, với món trang bị Th&acirc;̀n hi&ecirc;́m mới trong Event này.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/L%C3%AC%20X%C3%AC.jpg" style="height:271px; width:503px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><span style="color:#FFFF00">Thời gian :</span></span> <span style="color:#FFFFFF">10h30&nbsp;&nbsp;</span>Ngày <span style="color:#FFFFFF">28/12/2014</span> đ&ecirc;́n <span style="color:#FFFF00">10h</span><span style="color:#FFFFFF">00</span>&nbsp;Ngày <span style="color:#FFFF00">4</span><span style="color:#FFFFFF">/1/2015</span></p>\n\n<p><span style="font-size:14px"><span style="color:#FFFF00">Đ&ocirc;́i tượng tham gia :</span></span> t&acirc;́t cả máy chủ Truy M&ocirc;̣ng</p>\n\n<p><span style="font-size:14px"><span style="color:#FFFF00">N&ocirc;̣i Dung :</span></span>&nbsp;Trong thời gian hoạt động, Qu&yacute; bằng hữu chuyển KNB v&agrave;o game &nbsp;c&oacute; thể nhận được phần thưởng tương ứng c&aacute;c mốc<span style="color:#FFFF00"> [ t&iacute;ch lũy thẻ nạp ]</span></p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px">Lưu ý :&nbsp;</span></span></p>\n\n<p>* Mỗi mốc qu&agrave; chỉ nhận đc 1 lần xuy&ecirc;n suốt đến hết Event</p>\n\n<p>* Đảm bảo chỗ trống t&uacute;i đồ trước khi nhận thưởng</p>\n\n<p>* Lưu &yacute; thời gian hoạt động<span style="color:#FFFF00"> [ Kh&ocirc;ng tính khuy&ecirc;́n mãi 100% chỉ tính m&ecirc;̣nh giá ti&ecirc;̀n nạp ]</span></p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/lixi1.png" style="height:746px; width:582px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/lixi2.png" style="height:565px; width:582px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/lixi3.png" style="height:753px; width:582px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>Chúc quý bằng hữu vui vẻ.</p>\n\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n', 'Thân chào quý bằng hữu', '0', 0, '0', '1970-01-01', '', '2015-01-04 08:19:17', '2014-12-27 11:56:24', '0', ''),
(4, 1, '', 0, '', '0', 'Sự Kiện Server Mới', 'su-kien-server-moi', '<p><em><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</span></span></strong></em></p>\n\n<p><span style="font-family:arial,helvetica,sans-serif; font-size:12px">&nbsp;Đ&aacute;p ứng nhu cầu tất yếu của to&agrave;n thể bằng hữu tham gia</span><strong> &quot; Truy Mộng &quot;</strong><span style="font-family:arial,helvetica,sans-serif; font-size:12px"> , ch&uacute;ng t&ocirc;i xin gửi đến những sự kiện Khai Mở Server hấp dẫn v&agrave; l&ocirc;i cuốn gồm những phần thưởng qu&yacute; gi&aacute; kh&ocirc;ng thể thiếu tr&ecirc;n con đường h&agrave;nh tẩu của những anh h&ugrave;ng.</span></p>\n\n<p style="text-align:center"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><img alt="" src="/static/uploads/editor/503x2707.png" style="height:271px; width:503px" /></span></span></p>\n\n<p style="text-align:center"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp;Sự Kiện Khai Mở Sever Truy Mộng 01</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="color:#FF0000">&nbsp;Nội Dung :</span></span></span></p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><u>Gồm những sự kiện ingame với những phần qu&agrave; hấp dẫn như :</u></span></span></strong></p>\n\n<table border="1" cellpadding="1" cellspacing="1" style="width:500px">\n	<tbody>\n		<tr>\n			<td colspan="2" style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Sự Kiện Khai Mở Sever</span></span></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">8 ng&agrave;y Cho Server Mới</span></span></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đầu Tư</span></span></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đăng Nhập 7 Ng&agrave;y&nbsp;</span></span></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Qu&agrave; T&acirc;n Thủ</span></span></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Qu&agrave; Nạp Lần Đầu</span></span></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Nạp Mỗi Ng&agrave;y</span></span></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">V&ograve;ng Quay&nbsp;</span></span></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">T&iacute;ch Lũy Nạp</span></span></span></h4>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Để nhanh ch&oacute;ng gia tăng thực lực qu&yacute; bằng hữu n&ecirc;n nhanh ch&oacute;ng nắm bắt những sự kiện n&agrave;y nh&eacute; .</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="color:#FF0000"><strong>* Lưu &Yacute; </strong></span>: thao khảo trước những sự kiện khai mở để dễ nhận được phần thưởng</span></span></p>\n\n<p><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></p>\n', 'Đáp ứng nhu cầu tất yếu của toàn thể bằng hữu tham gia " Truy Mộng " ,', '0', 0, '0', '1970-01-01', '', '2014-11-27 16:56:48', '2014-11-27 11:19:49', '0', ''),
(5, 1, '', 0, '', '0', 'Quà Tặng Nạp Thẻ Lần Đầu', 'qua-tang-nap-the-lan-dau', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute;&nbsp;bằng hữu</strong></em>, &nbsp; &nbsp;</p>\n\n<p>Để ch&agrave;o đ&oacute;n cụm m&aacute;y chủ mới <strong>Truy Mộng 01</strong>, cũng như tiếp sức cho c&aacute;c chư vị bằng hữu tham gia v&agrave;o chuỗi sự kiện cực</p>\n\n<p>khủng của cụm m&aacute;y chủ mới n&agrave;y đặc biệt l&agrave; c&aacute;c sự kiện đua Top,&nbsp;<span style="color:rgb(0, 0, 255)"><strong>BQT Truy Mộng</strong></span>&nbsp;xin gửi đến chư vị bằng hữu &quot;<span style="color:rgb(178, 34, 34)"><strong>Qu&agrave;</strong></span></p>\n\n<p><span style="color:rgb(178, 34, 34)"><strong>Tặng Nạp Thẻ Lần Đầu&quot;.</strong></span></p>\n\n<p style="text-align: center;"><span style="font-size:16px"><span style="color:rgb(178, 34, 34)"><strong><img alt="" src="/static/uploads/editor/503x2703.png" style="height:271px; width:503px" /></strong></span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="color:#FF0000"><strong>1. Đối Tượng Tham Gia:</strong></span></span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">- Tất cả c&aacute;c t&agrave;i khoản tại m&aacute;y chủ <strong>Truy Mộng 01</strong></span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="color:#FF0000"><strong>2. Nội Dung:</strong></span></span></span></p>\n\n<p>&nbsp;- Tất cả Đồng đạo nạp bất k&igrave; mệnh gi&aacute; đều c&oacute; thể nhận đc qu&agrave; nạp lần đầu gồm nhiều vật phẩm hữu &iacute;ch&nbsp;k&egrave;m theo qu&agrave; tặng 100% Nguy&ecirc;n Bảo Kh&oacute;a .</p>\n\n<p style="text-align: center;"><span style="font-size:16px"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/ney_zpsa6f8b05b.png" /></span></p>\n\n<p><span style="font-family:arial,helvetica,sans-serif; font-size:12px">- Hy vọng g&oacute;i qu&agrave; n&agrave;y sẽ c&oacute; &iacute;ch cho c&aacute;c chư vị bằng hữu tr&ecirc;n con đường b&ocirc;n tẩu&nbsp;giang hồ</span></p>\n\n<p><strong><span style="color:#FF0000">&nbsp;-&nbsp;Lưu &yacute; :</span><em> Tối đa nhận đc l&agrave; 10.000 Nguy&ecirc;n bảo kh&oacute;a&nbsp;</em></strong></p>\n\n<p><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></p>\n', 'Để chào đón cụm máy chủ mới Truy Mộng 01, cũng như tiếp sức cho các chư vị bằng hữu tham gia vào', '0', 1, '0', '1970-01-01', '', '2014-11-27 17:05:17', '2014-11-27 11:36:04', '0', ''),
(6, 1, '', 0, '', '0', 'Quà Tặng 7 Ngày', 'qua-tang-7-ngay', '<p><span style="font-size:14px">Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.&nbsp;</span><span style="font-size:14px">&nbsp;</span></p>\n\n<p><span style="font-size:14px">Để ch&agrave;o mừng m&aacute;y chủ mới của <strong>Truy Mộng</strong>&nbsp;,<strong><span style="color:#0000FF"> BQT</span></strong> gửi đến qu&yacute;&nbsp;bằng&nbsp;hữu sự&nbsp;kiện qu&agrave; tặng mỗi&nbsp;ng&agrave;y.Hy&nbsp;vọng&nbsp;sự kiện n&agrave;y sẽ gi&uacute;p c&aacute;c bạn phần n&agrave;o giải tr&iacute; c&ugrave;ng<strong> Truy Mộng.</strong><span style="color:rgb(255, 0, 0)"><strong>&nbsp;</strong> &nbsp;</span></span></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/503x2704.png" style="height:271px; width:503px" /></p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px">Thời gian&nbsp;:</span></span></p>\n\n<p><span style="font-size:14px">- 7 ng&agrave;y đầu ti&ecirc;n ra m&aacute;y chủ mới &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;</span></p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px">Đối tượng tham gia&nbsp;:</span></span></p>\n\n<p><span style="font-size:14px">- Tất cả qu&yacute; bằng hữu tham gia game <strong>Truy Mộng&nbsp;&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px">Phần Thưởng:&nbsp; &nbsp; &nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px"><img alt="" src="/static/uploads/editor/7ngay.png" style="height:552px; width:898px" /></span></p>\n\n<p><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:18:02', '2014-11-27 11:37:36', '0', ''),
(7, 1, '', 0, '', '0', 'Tích Lũy Thẻ Nạp Máy Chủ Truy Mộng S2', 'tich-luy-the-nap-may-chu-truy-mong-s2', '<p style="text-align:center">&nbsp;</p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px"><strong><span style="font-family:inherit">I. Giới thiệu :</span>&nbsp;&nbsp;</strong></span></span></p>\n\n<p><span style="font-size:14px"><span style="font-family:inherit">Nh&acirc;n dịp ra mắt m&aacute;y chủ Truy Mộng S2&nbsp;BQT game <strong><span style="color:#FF0000">Truy Mộng</span></strong>&nbsp;xin gửi đến mọi người chơi tr&ecirc;n sever Truy Mộng S2&nbsp;sự kiện&nbsp;<span style="color:#FF0000"><strong>T&iacute;ch Lũy Thẻ Nạp</strong>&nbsp;</span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px"><strong><span style="font-family:inherit">II. Đối tượng tham gia :</span>&nbsp;&nbsp;</strong></span></span></p>\n\n<p><span style="font-size:14px">Tất cả người chơi <span style="color:#FF0000"><strong>Truy Mộng</strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><span style="font-size:14px"><strong><span style="font-family:inherit">III. Nội dung sự kiện</span></strong><span style="font-family:inherit"><span style="font-family:tahoma,helvetica,simsun,sans-serif">&nbsp;:</span></span></span></span></p>\n\n<p><span style="font-size:14px"><span style="font-family:inherit"><span style="font-family:tahoma,helvetica,simsun,sans-serif">&nbsp;V</span><span style="font-family:tahoma,helvetica,simsun,sans-serif">ới mỗi người chơi khi chuyển đổi Nguy&ecirc;n Bảo v&agrave;o game theo 1 mốc nhất định sẽ nhận được 1 phần thường cực k&igrave; c&oacute; gi&aacute; trị tương ứng với c&aacute;c Mốc Nguy&ecirc;n Bảo&nbsp;</span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:14px"><span style="font-family:inherit"><span style="font-family:tahoma,helvetica,simsun,sans-serif"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/a_zps04467260.png" /></span></span></span></p>\n\n<p style="text-align:center"><span style="font-size:14px">Phần thưởng l&agrave; cực k&igrave; lớn cho c&aacute;c bạn gồm c&oacute; cả&nbsp;<strong>Vũ Kh&iacute; Ho&agrave;ng Kim&nbsp;v&agrave; Th&uacute; Cưỡi&nbsp;Tử Kim Kỳ L&acirc;n</strong></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:14px"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/a1_zps92401520.png" /></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:14px"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/a2_zps561bedf8.png" /></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:14px"><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></span></p>\n', 'Nhân dịp ra mắt máy chủ Truy Mộng 01 BQT game Truy Mộng xin gửi đến mọi người chơi trên sever Truy Mộng 01', '0', 0, '0', '1970-01-01', '', '2014-12-05 09:11:40', '2014-11-27 11:49:51', '0', ''),
(8, 1, '', 0, '', '0', 'Gift Code Server Mới', 'gift-code-server-moi', '<p>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</p>\n\n<p>Nhằm th&uacute;c đẩy cũng như tăng th&ecirc;m ưu đ&atilde;i cho qu&yacute; bằng hữu tham gia v&agrave;o Truy Mộng ,<strong><span style="color:#0000FF"> </span><span style="color:#FFFF00">BQT Truy Mộng </span></strong>xin gửi tới qu&yacute; bằng hữu bộ giftcode gi&aacute; trị miễn ph&iacute; .</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/503x2701.png" style="height:271px; width:503px" /></p>\n\n<p><span style="color:#FFFF00"><span style="font-size:16px">Gift Code Hấp Dẫn</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>- Gồm nhiều&nbsp;phần qu&agrave; v&agrave; KNB kh&ocirc;ng thể thiếu trong t&uacute;i h&agrave;nh trang bu&ocirc;n tẩu của qu&yacute; bằng hữu .</p>\n\n<p>- <span style="color:#FFFF00">Gift code plugin chrome</span> ( th&ocirc;ng qua c&agrave;i đặt plugin )</p>\n\n<p>- <span style="color:#FFFF00">Gift code SMS</span> ( th&ocirc;ng qua x&aacute;c nhận số điện thoại )</p>\n\n<p>- <span style="color:#FFFF00">Gift code mail </span>( th&ocirc;ng qua x&aacute;c nhận mail )</p>\n\n<p>-<span style="color:#FFFF00"> Gift code like + fanpage</span> ( th&ocirc;ng qua like facebook )</p>\n\n<p>&nbsp;</p>\n', 'Với bộ Gift code hấp dẫn giá trị cao', '0', 0, '0', '1970-01-01', '', '2014-11-27 18:17:36', '2014-11-27 11:58:44', '0', ''),
(9, 2, '', 0, '', '0', 'Hệ Thống Tiên Nữ', 'he-thong-tien-nu', '<p style="text-align:center"><span style="color:#FFFF00"><span style="font-size:24px"><span style="font-family:arial,helvetica,sans-serif"><strong>Hệ Thống Ti&ecirc;n Nữ</strong></span></span></span></p>\n\n<p>Ti&ecirc;n Nữ l&agrave; 1 người bạn đồng h&agrave;nh thứ 2 sau Pet đi theo nh&acirc;n vật để chiến đấu v&agrave; cũng c&oacute; kỹ năng ri&ecirc;ng biệt</p>\n\n<p><span style="font-size:16px"><strong><span style="color:#FF0000">Lưu Luyến :</span></strong></span></p>\n\n<p>&nbsp;- C&oacute; tổng cộng 7 Ti&ecirc;n Nữ m&agrave; người chơi c&oacute; thể mở được trong game</p>\n\n<p>&nbsp;- Mỗi Ti&ecirc;n Nữ tối đa 20 cấp,7 lần Th&acirc;u Hương sẽ gi&uacute;p &nbsp;tăng 1 cấp. Để Th&acirc;u Hương chỉ cần c&oacute; được Tinh Nguy&ecirc;n</p>\n\n<p>&nbsp;- Tăng Cấp Ti&ecirc;n Nữ sẽ gi&uacute;p tăng trực tiếp chỉ số cho nh&acirc;n vật, v&igrave; vậy đ&acirc;y l&agrave; nguồn thu Lực Chiến rất lớn</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b_zpse46a9c21.png" style="height:569px; width:721px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:16px"><strong><span style="color:#FF0000">Thần Giao :</span></strong></span></p>\n\n<p>&nbsp;- L&agrave; hệ thống gi&uacute;p tăng trực tiếp 1 loại chỉ số bằng c&aacute;ch tăng cấp cho mỗi vi&ecirc;n Linh Ch&acirc;u tương ứng như C&ocirc;ng K&iacute;ch, Bạo K&iacute;ch, Ch&iacute;nh X&aacute;c .v..v</p>\n\n<p>&nbsp;- Linh Ch&acirc;u cấp c&agrave;ng cao th&igrave; điểm cộng c&agrave;ng nhi&ecirc;u nhưng tỉ lệ th&agrave;nh c&ocirc;ng để thăng cấp cũng nhỏ hơn. Nếu thất bại th&igrave; cấp của Linh Ch&acirc;u sẽ bị giảm xuống 1 bậc.</p>\n\n<p>&nbsp;- Khi tất cả c&aacute;c Linh Ch&acirc;u đạt mốc +5 , +10, +15 sẽ mở <span style="color:#0000FF">Bảo Hộ Cấp</span>. khi đ&oacute; th&igrave; d&ugrave; thất bại nhưng vẫn chỉ giảm tới cấp tối thiểu của mức bảo hộ.</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b1_zps4e5a84aa.png" style="height:569px; width:712px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:16px"><strong><span style="color:#FF0000">Hợp Thể :</span></strong></span></p>\n\n<p>&nbsp;- Ở mục n&agrave;y người chơi c&oacute; thể chọn ti&ecirc;n nữ để đồng h&agrave;nh, xem th&ocirc;ng tin kỹ năng v&agrave; tăng c&aacute;c điểm t&iacute;nh c&aacute;ch gi&uacute;p tăng chỉ số cho Ti&ecirc;n Nữ đồng h&agrave;nh.</p>\n\n<p>&nbsp;- Mỗi ti&ecirc;n nữ đều c&oacute; chung 10 điểm t&iacute;nh c&aacute;nh, sau khi n&acirc;ng đủ 10 điểm sẽ tiến h&agrave;nh thăng l&ecirc;n 1 cấp.</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b2_zpsfbd1092a.png" style="height:574px; width:735px" /></p>\n\n<p><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></p>\n', 'Tiên Nữ là 1 người bạn đồng hành thứ 2 sau Pet đi theo nhân vật để chiến đấu và cũng có kỹ năng riêng biệt', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:17:30', '2014-11-27 14:14:49', '0', ''),
(10, 2, '', 0, '', '0', 'Hệ Thống Cường Hóa [ Rèn ]', 'he-thong-cuong-hoa-ren-', '<p style="text-align:center"><span style="color:#FFFF00"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><strong><span style="font-size:24px">Hệ Thống Cường H&oacute;a [ R&egrave;n ]</span></strong></span></span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Trang bị trong <strong>&quot; Truy Mộng </strong>&quot; c&oacute; rất nhiều c&aacute;ch để tăng sức mạnh cho n&oacute;. <strong>Hệ Thống Cường H&oacute;a</strong> l&agrave; một hệ thống phức tạp với nhiều t&iacute;nh năng kh&aacute;c nhau.</span></span></p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">Cường H&oacute;a Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- Rất đơn giản v&agrave; th&ocirc;ng dụng, Cường H&oacute;a sẽ gi&uacute;p tăng trực tiếp chỉ số cho trang bị. Cấp cường h&oacute;a c&agrave;ng cao điểm tăng c&agrave;ng lớn. Để cường h&oacute;a cũng cần ngọc Cường H&oacute;a.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- Lưu &yacute; rằng mỗi m&oacute;n trang bị, t&ugrave;y v&agrave;o phẩm chất m&agrave; cho giới hạn mức độ Cường H&oacute;a kh&aacute;c nhau, đồ c&agrave;ng xịn c&agrave;ng c&oacute; thể cấp giới hạn cường h&oacute;a cao.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b3_zpsbf16c4c4.png" style="height:538px; width:888px" /></span></span></p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">Tẩy Luyện Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- L&agrave; chức năng để luyện c&aacute;c chỉ số cộng th&ecirc;m của mỗi trang bị nhằm đạt được chỉ số mong muốn hoặc được cao điểm hơn.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- Mỗi lần tốn một đ&aacute; Tẩy Luyện, v&agrave; người chơi c&oacute; thể kh&oacute;a lại c&aacute;c d&ograve;ng chỉ số vừa &yacute; bằng Kh&oacute;a Tẩy Luyện, những d&ograve;ng n&agrave;y&nbsp;sẽ kh&ocirc;ng bị thay đổi ở lần tẩy kế tiếp.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">Thăng Cấp Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- Người chơi c&oacute; thể tiến h&agrave;nh cải tạo vật phẩm cấp thấp l&ecirc;n vật phẩm cấp cao bằng chức năng n&agrave;y. Chỉ cần &nbsp;một số nguy&ecirc;n liệu y&ecirc;u cầu v&agrave; Đồng</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- Vật phẩm sau khi chế tạo mới đều giữ nguy&ecirc;n cấp cường h&oacute;a, phẩm chất v&agrave; c&aacute;c d&ograve;ng chỉ số cộng th&ecirc;m. Đ&acirc;y l&agrave; chức năng gi&uacute;p người chơi&nbsp;&nbsp;thoải m&aacute;i đầu tư trang bị m&agrave; kh&ocirc;ng sợ bị lỗi thời về sau.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">Tinh Luyện Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp;- Mỗi trang bị đều c&oacute; thể được tinh luyện th&agrave;nh loại Ch&acirc;n. Mức n&agrave;y sẽ gi&uacute;p tăng trực tiếp chỉ số cơ bản của trang bị&nbsp;đồng thời cũng tăng hiệu ứng khi trang bị đủ bộ.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">Phẩm Chất Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp;- Trang bị được chia th&agrave;nh c&aacute;c phẩm chất [1], [2], [3]&hellip;&hellip; Phẩm chất cao th&igrave; sức mạnh cơ bản c&agrave;ng cao.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp;- Người chơi c&oacute; thể tăng trực tiếp phẩm chất n&agrave;y nếu thu thập đủ lượng Cương Chi Hồn y&ecirc;u cầu</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">Kế Thừa Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- L&agrave; chức năng thừa kế lại to&agrave;n bộ điểm cường h&oacute;a của những trang bị c&ugrave;ng loại.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- V&iacute; dụ bạn c&oacute; 1 Gi&aacute;p cấp 50 đ&atilde; +20 điểm cường h&oacute;a, v&agrave; sau đ&oacute; bạn lượm được 1 m&oacute;n cấp 60. Bạn c&oacute; thể tiến h&agrave;nh kế thừa&nbsp;giữa 2 bộ gi&aacute;p n&agrave;y để c&oacute; được bộ gi&aacute;p 60 +20 cường h&oacute;a.</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; - Để thực hiện chức năng n&agrave;y chỉ cần bạn tốn Đồng</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:14px"><span style="color:#FF0000">T&aacute;ch Trang Bị :</span></span></span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">&nbsp; &nbsp;- Bạn c&oacute; thể ph&acirc;n r&atilde; c&aacute;c trang bị kh&ocirc;ng sử dụng để lấy c&aacute;c nguy&ecirc;n liệu cần thiết kh&aacute;c bằng hệ thống Ph&acirc;n R&atilde;.</span></span></p>\n\n<p><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng.</em></p>\n', 'Trang bị trong " Truy Mộng " có rất nhiều cách để tăng sức mạnh cho nó. Hệ Thống Cường Hóa là một hệ thống phức tạp với nhiều tính năng khác nhau.', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:17:05', '2014-11-27 14:26:51', '0', ''),
(11, 2, '', 0, '', '0', 'Đặc Quyền VIP', 'dac-quyen-vip', '<p><span style="color:#FFFFFF"><span style="font-size:14px">Th&acirc;n ch&agrave;o&nbsp;qu&yacute; bằng hữu.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:14px">VIP l&agrave; 1 chức năng gi&uacute;p người chơi c&oacute; th&ecirc;m nhiều lợi &iacute;ch c&aacute; nh&acirc;n khi tham gia tr&ograve; chơi cũng như c&aacute;c hoạt động Event. Để c&oacute; đặc quyền VIP, người chơi chỉ cần mua 1 trong 3 loại VIP t&iacute;nh theo thời gian</span></span></p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vip1.jpg" style="height:141px; width:208px" /></p>\n\n<p style="text-align:justify"><span style="color:#FFFF00"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:arial"><strong>1/ L&agrave;m thế n&agrave;o để trở th&agrave;nh VIP ?</strong></span></span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Để trở th&agrave;nh Vip, người chơi cần mua&nbsp; 1 trong 3 loại thẻ Vip sau đ&acirc;y:</span></span></p>\n\n<p style="text-align:center"><span style="color:#FFFFFF"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vip1.png" style="height:98px; width:537px" /></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>VIP Tuần:</strong>&nbsp; duy tr&igrave; Vip trong 7 ng&agrave;y v&agrave; sẽ đạt cấp VIP 1 ngay khi d&ugrave;ng</span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>VIP Th&aacute;ng:</strong>&nbsp;duy tr&igrave; VIP trong 30 ng&agrave;y v&agrave; sẽ đạt cấp VIP 2 ngay khi d&ugrave;ng</span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>VIP Nửa Năm:</strong>&nbsp;duy tr&igrave; Vip trong 180 ng&agrave;y v&agrave; sẽ đạt cấp VIP 4 ngay khi d&ugrave;ng</span></span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify"><span style="color:#FFFF00"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:arial"><strong>2/ L&agrave;m thế n&agrave;o để tăng cấp VIP ?</strong></span></span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Sau khi trở th&agrave;nh VIP</em></strong>, &nbsp;người chơi cần phải thu thập điểm t&iacute;ch lũy VIP đạt tới mốc quy định để thăng cấp, gồm những c&aacute;ch sau:</span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">+ Đăng nhập mỗi ng&agrave;y t&iacute;ch điểm (cấp VIP c&agrave;ng cao điểm t&iacute;ch c&agrave;ng nhiều)</span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">+ Online&nbsp; t&iacute;ch lũy mỗi ng&agrave;y 2h</span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">+ Ti&ecirc;u Ph&iacute; NB&nbsp; ( 1 NB sẽ t&iacute;ch được 1 điểm, kh&ocirc;ng t&iacute;nh gửi thư, giao dịch hay đấu gi&aacute; )</span></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">+ Người chơi kh&ocirc;ng phải VIP th&igrave; mỗi ng&agrave;y sẽ mất 5 điểm t&iacute;ch lũy.</span></span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify"><span style="color:#FFFF00"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:arial"><strong>3/ Quyền Lợi VIP:</strong></span></span></span></p>\n\n<p><img alt="" src="/static/uploads/editor/vip.png" style="height:945px; width:716px" /></p>\n\n<p style="text-align:justify"><span style="color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>&nbsp;</strong></span></p>\n\n<p style="text-align:justify"><span style="color:#FFFFFF"><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></span></p>\n\n<p>&nbsp;</p>\n', 'Ưu đãi VIP', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:16:47', '2014-11-27 14:48:26', '0', ''),
(12, 2, '', 0, '', '0', 'Sủng Vật- Thú Nuôi', 'sung-vat-thu-nuoi', '<p style="text-align:center"><span style="color:#FFFF00"><span style="font-size:24px"><strong>Sủng Vật- Th&uacute; Nu&ocirc;i</strong></span></span></p>\n\n<p>Th&uacute; nu&ocirc;i hay c&ograve;n gọi l&agrave; <span style="color:#FFFF00"><strong>&quot;Sủng&quot;</strong></span> l&agrave; những người bạn đồng h&agrave;nh v&agrave; trợ thủ đắc lực cho mỗi nh&acirc;n vật. C&oacute; khả năng chiến đấu độc lập với kỹ năng ri&ecirc;ng biệt.&nbsp;Pet c&oacute; bảng kỹ năng, trang bị v&agrave; chiến đấu ri&ecirc;ng biệt đối với nh&acirc;n vật.</p>\n\n<p><span style="font-size:14px"><span style="color:#FF0000"><strong>Th&ocirc;ng Tin :</strong></span></span></p>\n\n<p>&nbsp; &nbsp;- Người chơi c&oacute; thể xem tất cả th&ocirc;ng tin những PET m&igrave;nh đang sở hữu tại đ&acirc;y. Lưu &yacute; rằng Thuộc T&iacute;nh Lực Chiến của PET sẽ dựa tr&ecirc;n sức mạnh gốc của nh&acirc;n vật&nbsp;tức l&agrave; nh&acirc;n vật của bạn c&agrave;ng mạnh, Pet cũng sẽ c&agrave;ng mạnh.</p>\n\n<p>&nbsp; &nbsp;- Mỗi PET c&oacute; điểm Tinh Lực ri&ecirc;ng, khi xuất chiến Tinh Lực sẽ giảm, nếu bằng 0 th&igrave; th&uacute; kh&ocirc;ng thể xuất Chiến. C&oacute; thể tăng Tinh Lực bằng c&aacute;ch cho ăn</p>\n\n<p>&nbsp; &nbsp;- Tại đ&acirc;y bạn cũng c&oacute; thể đổi t&ecirc;n cho Th&uacute; Nu&ocirc;i bằng c&aacute;ch ấn v&agrave;o k&iacute; hiệu c&acirc;y b&uacute;t b&ecirc;n cạnh t&ecirc;n.</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b4_zps3e59891b.png" style="height:456px; width:748px" /></p>\n\n<p><span style="font-size:14px"><span style="color:#FF0000"><strong>Trưởng Th&agrave;nh :</strong></span></span></p>\n\n<p>&nbsp;&nbsp;- Người chơi c&oacute; thể tăng mức độ Trưởng Th&agrave;nh của Pet. Chỉ số của Pet tăng dần theo mức độ trưởng th&agrave;nh.</p>\n\n<p>&nbsp;- Mỗi ng&agrave;y người chơi c&oacute; 1 lần miễn ph&iacute; tăng hoặc d&ugrave;ng [Trưởng Th&agrave;nh Pet Đan]. Người chơi cũng c&oacute; thể dung NB để tăng trực tiếp cho Pet.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:#FF0000">Kỹ Năng :</span></strong></span></p>\n\n<p>&nbsp;-Kỹ năng của Th&uacute; chia ra l&agrave;m 2 loại: Chủ Động v&agrave; Bị Động</p>\n\n<p>&nbsp;-Mỗi Kỹ Năng c&oacute; tổng cộng 4 cấp, để n&acirc;ng kỹ năng l&ecirc;n cấp cao hơn, bạn cần phải n&acirc;ng đủ sao của cấp trước đ&oacute;. Sau đ&oacute; dung [S&aacute;ch Kỹ Năng PET]&nbsp;mua trong Tiệm Thần B&iacute;.</p>\n\n<p>&nbsp;- Khi l&ecirc;n cấp 50, PET sẽ c&oacute; th&ecirc;m kỹ năng Chủ Động.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:#FF0000">Linh Huyệt :</span></strong></span></p>\n\n<p>&nbsp;- Pet c&oacute; tổng cộng 8 &ocirc; trang bị, những trang bị của Pet c&oacute; thể t&igrave;m được tại [Ti&ecirc;n Hồn Th&aacute;p].</p>\n\n<p>- Ngo&agrave;i ra, c&oacute; thể dung [Nội Đan Pet] để n&acirc;ng cấp trang bị Pet</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:14px"><span style="color:#FF0000">Dung Hợp :</span></span></strong></p>\n\n<p>&nbsp; &nbsp;- Người chơi c&oacute; thể Dung hợp 2 Pet với nhau để Pet A c&oacute; thể thừa hưởng sức mạnh từ Pet B.</p>\n\n<p>&nbsp; &nbsp;- Pet ở vị tr&iacute; B sẽ biến mất, v&agrave; tất cả được dồn về cho Pet A</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><span style="color:#FF0000"><strong>Ảo H&oacute;a :</strong></span></span></p>\n\n<p>&nbsp; &nbsp;- L&agrave; một dạng h&oacute;a h&igrave;nh Pet nhằm tăng lực chiến. Bằng c&aacute;ch sử dụng [Thẻ Ảo H&oacute;a Pet] &nbsp;hoặc trứng của Pet&nbsp;bạn sẽ mở kh&oacute;a một h&igrave;nh ảnh Pet tương ứng v&agrave; tăng sức mạnh cơ bản cho pet.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:#FF0000">Chuyển Sinh :</span></strong></span></p>\n\n<p>&nbsp; &nbsp;- Mỗi Pet sau khi đạt tới cấp 65 c&oacute; thể tiến h&agrave;nh Chuyển Sinh lần đầu. C&agrave;ng về sau, y&ecirc;u cầu chuyển sinh c&agrave;ng cao</p>\n\n<p>&nbsp; &nbsp;- Pet sau khi Chuyển Sinh sẽ trở về Lv.1 v&agrave; giữ nguy&ecirc;n thuộc t&iacute;nh cơ bản trước khi Chuyển Sinh. Tuy nhi&ecirc;n mỗi lần tăng cấp thuộc t&iacute;nh cộng th&ecirc;m cũng nhiều hơn</p>\n\n<p>&nbsp; &nbsp;- Số lần Chuyển Sinh tới một mức &nbsp;nhất định sẽ mở ra được th&ecirc;m nhiều kỹ năng Chủ Động.</p>\n', 'Thú nuôi hay còn gọi là "Sủng" là những người bạn đồng hành và trợ thủ đắc lực cho mỗi nhân vật. ', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:16:39', '2014-11-27 14:50:31', '0', ''),
(13, 2, '', 0, '', '0', 'Thú Cưỡi- Tọa Kỵ', 'thu-cuoi-toa-ky', '<p style="text-align:center"><span style="color:#FFFF00"><span style="font-size:24px"><strong>Th&uacute; Cưỡi- Tọa Kỵ</strong></span></span></p>\n\n<p>Hệ thống Tọa kỵ kh&ocirc;ng chỉ c&oacute; thể gi&uacute;p bạn tật tr&igrave; đại địa, c&ograve;n c&oacute; thuộc t&iacute;nh gia tăng sức mạnh v&ocirc; thượng tr&ecirc;n con đường tu luyện</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b5_zps923875cc.png" style="height:89px; width:371px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:14px"><span style="color:#FF0000"><strong>Giới Thiệu :</strong></span></span></p>\n\n<p>&nbsp; &nbsp;- Khi đạt cấp 18 nh&acirc;n vật sẽ mở t&iacute;nh năng tọa kỵ, c&oacute; thể nhấp chọn biểu tượng Tọa kỵ hoặc đơn giản l&agrave; ấn ph&iacute;m tắt H, để mở giao diện xem th&ocirc;ng tin tọa kỵ.</p>\n\n<p>Giao diện tọa kỵ</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/b6_zpsfac20754.png" style="height:496px; width:825px" /></p>\n\n<p><span style="font-size:14px"><strong><span style="color:#FF0000">Cường H&oacute;a Tọa Kỵ :</span></strong></span></p>\n\n<p>&nbsp; &nbsp;- Tọa kỵ c&oacute; thể cường h&oacute;a giống như cường h&oacute;a trang bị,&nbsp;sử dụng Đ&aacute; cường h&oacute;a c&oacute; cấp tương ứng v&agrave; tiền đồng mới c&oacute; thể tiến h&agrave;nh cường h&oacute;a.</p>\n\n<p>&nbsp; &nbsp;- Đồng thời c&oacute; thể th&ocirc;ng qua cấp VIP, ph&ugrave; may mắn n&acirc;ng cao tỷ lệ cường h&oacute;a th&agrave;nh c&ocirc;ng,&nbsp;Sau khi cường h&oacute;a th&agrave;nh c&ocirc;ng c&oacute; thể n&acirc;ng cao nhiều thuộc t&iacute;nh tọa kỵ</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:#FF0000">Tiến Cấp Tọa Kỵ :</span></strong></span></p>\n\n<p>&nbsp;- Ngo&agrave;i cường h&oacute;a tọa kỵ,&nbsp;bạn c&ograve;n c&oacute; thể sử dụng chức năng tiến cấp tọa kỵ để tọa kỵ tiến h&oacute;a. Mỗi lần tiến cấp tọa kỵ đều phải ti&ecirc;u hao tọa kỵ tiến cấp đan v&agrave; tiền đồng</p>\n\n<p>&nbsp;- Mỗi lần tiến cấp đều c&oacute; thể nhận điểm ch&uacute;c ph&uacute;c, điểm ch&uacute;c ph&uacute;c c&agrave;ng cao, tọa kỵ tiến cấp tỷ lệ th&agrave;nh c&ocirc;ng c&agrave;ng cao.</p>\n\n<p>- Sau khi tọa kỵ tiến cấp c&oacute; thể thể nhận được nhiều thuộc t&iacute;nh, v&agrave; nhận được h&igrave;nh dạng cấp cao hơn, người c&oacute; h&igrave;nh dạng tọa kỵ cao nhất tuyệt đối l&agrave; người mạnh nhất.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:#FF0000">Tư Chất Tọa Kỵ :</span></strong></span></p>\n\n<p>&nbsp;- Cần mời c&aacute;c ti&ecirc;n nữ [Liệp đan] để n&acirc;ng cấp tư chất cho th&uacute; cưỡi. Cấp ti&ecirc;n nữ c&agrave;ng cao, t&iacute;nh chất th&uacute; cưỡi tăng c&agrave;ng nhiều</p>\n\n<p>&nbsp;- Cần mời ti&ecirc;n nữ theo thứ tự như trong h&igrave;nh để đạt được ti&ecirc;n nữ cao cấp nhất.</p>\n\n<p>&nbsp;- Ngo&agrave;i ra để đột ph&aacute; giới hạn chỉ số cho th&uacute; cưỡi, bạn cần phải c&oacute; [đột ph&aacute; đan]. [Đột ph&aacute; đan] c&oacute; thể mua được trong thương th&agrave;nh</p>\n\n<p>&nbsp;</p>\n', 'Hệ thống Tọa kỵ không chỉ có thể giúp bạn tật trì đại địa, còn có thuộc tính gia tăng sức mạnh vô thượng trên con đường tu luyện', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:16:23', '2014-11-27 15:20:19', '0', ''),
(57, 1, '', 0, '', '0', 'Kết Thúc Sự Kiện Noel', 'ket-thuc-su-kien-noel', '<p>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</p>\n\n<h1><img alt="" src="/static/uploads/editor/%C4%90%E1%BB%81n%20B%C3%B9.png" style="height:441px; width:514px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>Sau 2 ng&agrave;y diễn ra Sự kiện Noel, k&egrave;m khuyến m&atilde;i gi&aacute; trị nạp card 100% cho to&agrave;n bộ 4 Cụm m&aacute;y chủ,<span style="color:#FFFF00">BQT</span> thấy sự tham gia nhiệt t&igrave;nh của Qu&yacute; bằng hữu, kh&ocirc;ng c&oacute; bữa tiệc n&agrave;o l&agrave; kh&ocirc;ng t&agrave;n.</p>\n\n<p>h&ocirc;m nay <span style="color:#FFFF00">10h00 ng&agrave;y 26/12 BQT</span> kết th&uacute;c Event noel v&agrave; 100% gi&aacute; trị nạp thẻ của 3 Cụm m&aacute;y chủ S1, S2, S3 [ <span style="color:#FFFF00">S4 vẫn c&ograve;n khuyến m&atilde;i 100% đến hết ng&agrave;y 28/12 ]</span></p>\n\n<p>&nbsp;</p>\n\n<p>Ch&uacute;c qu&yacute; bằng hữu vui vẻ.</p>\n', 'thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2014-12-26 10:11:43', '2014-12-26 10:10:56', '0', ''),
(24, 2, '', 0, '', '0', 'Hướng Dẫn Nạp Thẻ', 'huong-dan-nap-the', '<h1><span style="font-size:26px"><strong>HƯỚNG DẪN NẠP THẺ</strong></span></h1>\n\n<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><strong><em>Phần Trang chủ sẽ c&oacute; phần nạp thẻ&nbsp;</em></strong></p>\n\n<p><strong><em><img alt="" src="/static/uploads/editor/nap%20the.png" style="height:384px; width:647px" /></em></strong></p>\n\n<p><strong><em>Hoặc qu&yacute; bằng hữu c&oacute; thể đăng nhập rồi b&ecirc;n tr&ecirc;n cũng sẽ c&oacute; phần nap thẻ :</em></strong></p>\n\n<p><strong><em><img alt="" src="/static/uploads/editor/nap%20the%201.png" style="height:117px; width:743px" /></em></strong></p>\n\n<p><strong><em>Sau Khi v&agrave;o phần nạp thẻ sẽ dẫn qu&yacute; bằng hữu đến trang nap thẻ :</em></strong></p>\n\n<p><strong><em><img alt="" src="/static/uploads/editor/Nap%20Th%E1%BA%BB.png" style="height:410px; width:925px" /></em></strong></p>\n\n<p><strong><em>Ch&uacute;c Qu&yacute; Bằng Hữu Tr&atilde;i Nghiệm Game Vui Vẻ.</em></strong></p>\n', 'Phần Trang chủ sẽ có phần nạp thẻ ', '0', 1, '0', '1970-01-01', '', '2014-11-30 10:04:28', '2014-11-30 10:04:28', '0', ''),
(15, 2, '', 0, '', '0', 'Hệ Thống Ma Bài', 'he-thong-ma-bai', '<p style="text-align:center"><span style="color:#FF0000"><span style="font-size:24px"><strong>Hệ Thống Ma B&agrave;i</strong></span></span></p>\n\n<p>Thẻ B&agrave;i l&agrave; hệ thống đặc biệt mới lạ của<span style="color:#FF0000"> <strong>&quot; Truy Mộng&quot;</strong> </span>, n&oacute; l&agrave; nguồn năng lực l&agrave;m cho mỗi nh&acirc;n vật trở n&ecirc;n đặc biệt ngay cả trong c&ugrave;ng một lớp nh&acirc;n vật.</p>\n\n<p>Việc kết hợp hay sử dụng c&aacute;c l&aacute; b&agrave;i th&ocirc;ng minh trong mỗi t&igrave;nh huống l&agrave; ch&igrave;a kh&oacute;a để gi&uacute;p đ&aacute;nh bại nhiều kẻ địch mạnh hơn</p>\n\n<p><strong><span style="color:#FFFF00"><span style="font-size:12px">Thẻ B&agrave;i Phụ Th&acirc;n :</span></span></strong></p>\n\n<p>&nbsp;- Bạn c&oacute; thể lắp tối đa 4 l&aacute; b&agrave;i v&agrave;o 1 bộ. Bạn c&oacute; thể tạo ra tối đa 4 bộ để lu&acirc;n chuyển sử dụng.</p>\n\n<p>&nbsp;- Mỗi l&aacute; b&agrave;i c&oacute; năng lực được ghi r&otilde; tr&ecirc;n thẻ b&agrave;i. Ngo&agrave;i ra khi n&oacute; đi chung với 1 l&aacute; b&agrave;i kh&aacute;c sẽ sử dụng được những năng lực đặc biệt kh&aacute;c</p>\n\n<p>&nbsp;- Lắp B&agrave;i chia ra 4 &ocirc; gồm: Nh&acirc;n Vật, Bảo Kh&iacute;, Dị Th&uacute; v&agrave; C&ocirc;ng Ph&aacute;p . Mỗi &ocirc; chỉ lắp được loại thẻ b&agrave;i tương ứng . V&iacute; dụ &ocirc; lắp Nh&acirc;n vật&nbsp;chỉ c&oacute; thể lắp những l&aacute; thuộc loại Nh&acirc;n vật, bạn c&oacute; thể xem ở mục Thư Viện.</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c_zps878e7e17.png" style="height:512px; width:845px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>Thuộc t&iacute;nh hiện tại : &nbsp;Đ&acirc;y l&agrave; những chỉ số cộng th&ecirc;m trực tiếp v&agrave;o Nh&acirc;n vật</p>\n\n<p>Kỹ năng : Để thi triển mục n&agrave;y th&igrave; cần những l&aacute; b&agrave;i tương ứng lắp chung v&agrave;o bộ , ở đ&acirc;y l&agrave; Linh X&agrave; Trượng hoặc Dũng Cảm</p>\n\n<p><strong><span style="color:#FFFF00">Hiệu Ứng QTE :&nbsp;</span></strong></p>\n\n<p>&nbsp; L&agrave; một kỹ năng chủ động chỉ c&oacute; thể thi triển khi c&oacute; đủ bộ 4 l&aacute; như y&ecirc;u cầu (như ở đ&acirc;y l&agrave; Linh Nhi, Linh X&agrave; Trượng, Dũng Cảm,</p>\n\n<p>Phượng Ho&agrave;ng Hỏa Diệm) . Khi xuất hiện, hệ thống sẽ y&ecirc;u cầu bạn bấm 1 trong c&aacute;c ph&iacute;m Q,T,E, R để k&iacute;ch hoạt.</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c1_zps0f892d14.png" style="height:591px; width:244px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Tăng Cấp Thể B&agrave;i :</strong></span></p>\n\n<p>&nbsp;- K&iacute; hiệu ở g&oacute;c dưới b&ecirc;n tr&aacute;i mỗi l&aacute; b&agrave;i thể hiện cho cấp độ của n&oacute;. B&agrave;i cấp c&agrave;ng cao th&igrave; chỉ số thuộc t&iacute;nh c&agrave;ng nhiều.</p>\n\n<p>&nbsp;- Để tăng cấp cho L&aacute; b&agrave;i th&igrave; phải dung c&aacute;c l&aacute; b&agrave;i kh&aacute;c để hiến tế, mỗi l&aacute; b&agrave;i t&ugrave;y thuộc mạnh yếu sẽ tăng một lượng EXP nhất định</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c3_zpse9d29927.png" style="height:515px; width:857px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Nhận Ma Hồn :</strong></span></p>\n\n<p>&nbsp;- Gọi Hồn l&agrave; hệ thống quay hồn để lấy c&aacute;c thẻ b&agrave;i mạnh hay c&aacute;c hồn.</p>\n\n<p>&nbsp;- Mỗi lần quay sẽ ti&ecirc;u tốn một lượng NB. Đổi lại bạn sẽ c&oacute; cơ hội lấy được c&aacute;c thẻ b&agrave;i mạnh hoặc c&aacute;c Linh Hồn Lam,&nbsp;T&iacute;m, V&agrave;ng.Thu thập một lượng những linh hồn n&agrave;y c&oacute; thể v&agrave;o tiệm Linh Hồn để đổi lấy c&aacute;c thẻ b&agrave;i</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c4_zps9855d385.png" style="height:533px; width:872px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:#FFFF00">Đổi Thẻ Ma :</span></strong></p>\n\n<p>&nbsp;- Sử dụng c&aacute;c ma hồn lam, t&iacute;m, cam để trao đổi c&aacute;c thẻ b&agrave;i m&agrave; m&igrave;nh mong muốn hoặc đổi thẻ EXP tăng cấp Ma B&agrave;i</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c5_zpsf93bf08a.png" style="height:529px; width:860px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:#FFFF00">QTE Thẻ Ma :</span></strong></p>\n\n<p>&nbsp;- Mục n&agrave;y sẽ liệt k&ecirc; tất cả những kỹ năng QTE v&agrave; những bộ l&aacute; b&agrave;i của n&oacute;. Như trong h&igrave;nh l&agrave; bộ gồm 4 l&aacute; b&agrave;i m&agrave; khi tập hợp đủ&nbsp;sẽ c&oacute; thể k&iacute;ch hoạt kỹ năng QTE.</p>\n\n<p>&nbsp;- VD : Hiệu ứng trong h&igrave;nh nếu k&iacute;ch hoạt được sẽ tăng 30% lực c&ocirc;ng k&iacute;ch tự th&acirc;n, duy tr&igrave; khi hết hiệu ứng QTE</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c7_zpse8646fbb.png" style="height:529px; width:867px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:#FFFF00">Thẻ Ma Đồ Gi&aacute;m ( Thư Viện ) :</span></strong></p>\n\n<p>&nbsp;- Mục n&agrave;y liệt k&ecirc; to&agrave;n bộ những l&aacute; b&agrave;i trong game. Bạn c&oacute; thể v&agrave;o đ&acirc;y xem mọi th&ocirc;ng tin về c&aacute;c l&aacute; b&agrave;i đ&atilde; hoặc chưa sở hữu.</p>\n\n<p style="text-align:center"><img alt="" src="http://i1272.photobucket.com/albums/y387/trinhleminhphuc/c8_zps1cf6af79.png" style="height:512px; width:853px" /></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', '', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:15:55', '2014-11-27 15:45:46', '0', '');
INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(17, 2, '', 0, '', '0', 'Nap Một Được Hai', 'nap-mot-duoc-hai', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><strong>Đ&aacute;p ứng nhu cầu tăng lực chiến cũng như bu&ocirc;n tẩu của qu&yacute; bằng hữu <em><span style="color:#FFFF00">BQT Truy Mộng </span></em>sẽ ch&iacute;nh thức lấy mức chuyển đổi KNB c&oacute; tỷ lệ l&agrave;<span style="color:#FFFF00"> &quot; 1 : 2 &quot; </span>,tương đương v&iacute; dụ&nbsp;20.000 VND = 400 KNB .</strong></p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/851x315.png" /></p>\n\n<p>&nbsp;</p>\n\n<p><em><strong>Với mọi mệnh gi&aacute; thẻ nạp đều được t&iacute;nh tỷ lệ<span style="color:#FFFF00"> 1 : 2 .</span></strong></em></p>\n', 'Đáp ứng nhu cầu tăng lực chiến cũng như buôn tẩu của quý bằng hữu BQT Truy Mộng', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:14:30', '2014-11-27 16:41:14', '0', ''),
(16, 1, '', 0, '', '0', 'Vòng Quay 7 Ngày', 'vong-quay-7-ngay', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p>&nbsp;</p>\n\n<p>Đ&uacute;ng như t&ecirc;n gọi v&ograve;ng quay 7 ng&agrave;y m&agrave; <span style="color:#FFFF00"><strong>BQT Truy Mộng</strong></span> gửi đến qu&yacute; bằng hữu&nbsp;nh&acirc;n ng&agrave;y server Truy Mộng S1 ra mắt sẽ c&oacute; nhiều phần thưởng v&agrave; vật phẩm qu&yacute; gi&aacute; m&agrave; t&uacute;i h&agrave;nh trang của qu&yacute; bằng hữu kh&ocirc;ng thể thiếu.</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/Game.png" /></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Thời Gian :&nbsp;</strong></span></p>\n\n<p>- 7 ng&agrave;y khi mở server.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Nội Dung :</strong></span></p>\n\n<p>- Với một &iacute;t KNB 1&nbsp;lần quay qu&yacute; bằng hữu sẽ c&oacute; cơ hội nhận nhiều vật phẩm hấp dẫn.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Lưu &Yacute; :</strong></span></p>\n\n<p>V&ograve;ng quay chỉ tồn tại trong 7 ng&agrave;y từ l&uacute;c server mở.</p>\n', 'Đúng như tên gọi vòng quay 7 ngày mà BQT Truy Mộng gửi đến quý bằng hữu nhân ngày server Truy Mộng S1', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:14:40', '2014-11-27 16:17:13', '0', ''),
(18, 2, '', 0, '', '0', 'Phó Bản & Hoat Động Ngày', 'pho-ban-hoat-dong-ngay', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p>Ph&oacute; bản v&agrave; hoạt động ng&agrave;y l&agrave; t&iacute;nh năng đặc sắc của <strong>Truy Mộng</strong> gồm nhiều Ph&oacute; bản đơn v&agrave; đồng đội với phần thưởng kinh nghiệm v&agrave; vật phẩm phong ph&uacute; chờ c&aacute;c bằng hữu kh&aacute;m ph&aacute;.</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/phoban.png" style="height:522px; width:745px" /></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Thời Gian :</strong></span></p>\n\n<p>- Được th&ocirc;ng b&aacute;o cũng như th&iacute;ch hợp trong game.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Nội Dung :</strong></span></p>\n\n<p>- T&ugrave;y cấp độ cũng như điều kiện tham gia m&agrave; qu&yacute; bằng hữu c&oacute; thể tham gia c&aacute;c hoạt động cũng như ph&oacute; bản phong ph&uacute; trong<strong> Truy Mộng.</strong></p>\n', 'Phó bản và hoạt động ngày là tính năng đặc sắc của Truy Mộng gồm nhiều Phó bản đơn và đồng đội ', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:14:22', '2014-11-27 17:20:56', '0', ''),
(19, 2, '', 0, '', '0', 'Hướng Dẫn Đăng Ký', 'huong-dan-dang-ky', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><span style="font-size:14px"><em><strong>- Với t&iacute;nh năng đăng k&yacute; dễ d&agrave;ng v&agrave; nhiều phần qu&agrave; hấp dẫn khi x&aacute;c nhận đăng k&yacute; sẽ mang đến h&agrave;nh trang gồm những vật phẩm phong ph&uacute; cho qu&yacute; bằng hữu h&agrave;nh tẩu giang hồ.</strong></em></span></p>\n\n<p><span style="font-size:14px"><em><strong>- C&oacute; 2 c&aacute;ch đăng k&yacute; :</strong></em></span></p>\n\n<p><span style="color:#FFFF00"><strong>1. Bấm v&agrave;o Icon Chơi Ngay .</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/game%203.png" style="height:367px; width:611px" /></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>2. Bảng đăng k&yacute; nằm ngo&agrave;i trang chủ.</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/game%204.png" style="height:316px; width:267px" /></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px">- Khi ho&agrave;n tất việc đăng k&yacute; qu&yacute; bằng hữu h&atilde;y x&aacute;c nhận <strong>Mail</strong>,<strong> Số Điện Thoại</strong>, v&agrave; <strong>c&agrave;i plugin</strong> để nhận phần thưởng bộ 3 Gift Code hấp dẫn.</span></p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/game%205.png" style="height:368px; width:919px" /></p>\n\n<p><em><strong>Ch&uacute;c qu&yacute; bằng hữu tr&atilde;i nghiệm game vui vẻ.</strong></em></p>\n', ' Với tính năng đăng ký dễ dàng và nhiều phần quà hấp dẫn khi xác nhận đăng ký sẽ mang đến hành trang gồm những vật phẩm phong phú cho quý bằng hữu hành tẩu giang hồ.', '0', 1, '0', '1970-01-01', '', '2014-11-27 18:13:50', '2014-11-27 17:32:34', '0', ''),
(65, 1, '', 0, '', '0', 'Khóa Acc Tài Khoản Mạo Danh GM', 'kho-a-acc-ta-i-khoa-n-ma-o-danh-gm', '<p>Th&acirc;n chào quý bằng hữu.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/NhungNeoDuongTa.gif" style="height:317px; width:564px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="line-height:1.6em">- &nbsp; Sau thời gian Admin tìm hi&ecirc;̉u 1 s&ocirc;́ tài khoản lợi dụng sự tin tưởng của các quý bằng hữu đã Mạo Danh Gm ingame với mưu đ&ocirc;̀ chu&ocirc;̣c lợi cá nh&acirc;n nay <span style="color:#FFFF00">BQT khóa acc Vĩnh Vi&ecirc;̃n tài khoản :&nbsp;</span></span><span style="color:#FFFF00"><strong style="line-height:1.6em">ngocsanhdh1210&nbsp;</strong></span></p>\n\n<p>- &nbsp; Khóa acc Vĩnh Vi&ecirc;̃n tài khoản : <span style="color:#FFFF00">luomvanluom1 [ Mạo Danh GM S3 ]</span></p>\n\n<p>- &nbsp; Tài khoản này server S3 có t&ecirc;n là :<span style="color:#FFFF00">&nbsp;๖ۣۜPep๖ۣۜSi&nbsp;</span></p>\n\n<p>- &nbsp; Đã từng 1 l&acirc;̀n mạo danh GM đ&ecirc;̉ x&acirc;m nh&acirc;̣p vào Tài Khoản nhằm chi&ecirc;́m đoạt tài sản</p>\n\n<p>&nbsp;</p>\n\n<p>Nay Admin Game thay mặt <span style="color:#FFFF00">BQT Khóa acc Vĩnh Vi&ecirc;̃n và phong tỏa toàn b&ocirc;̣ tài sản</span> của 2 tài khoản này.</p>\n\n<p>&nbsp;</p>\n\n<p>Chúc quý bằng hữu y&ecirc;n t&acirc;m vào Admin .</p>\n', 'thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-01-04 13:13:55', '2015-01-04 13:13:55', '0', ''),
(20, 2, '', 0, '', '0', 'Hướng Dẫn Nhập Code', 'huong-dan-nhap-code', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,&nbsp;</strong></em></p>\n\n<p><em><strong>* Gift Code lu&ocirc;n c&oacute; những vật phẩm phong ph&uacute; v&agrave; cần thiết đối với qu&yacute; bằng hữu sau đ&acirc;y<span style="color:#FFFF00"> BQT Truy Mộng</span> sẽ hướng dẫn qu&yacute; bằng hữu nhập Gift code để lấy được những phần qu&agrave; hấp dẫn đ&oacute;.</strong></em></p>\n\n<p>&nbsp;</p>\n\n<p><em><strong>- Click v&agrave;o =&gt;</strong></em>&nbsp;<a href="http://truymong.com/gift-code"><span style="color:#FF0000"><u><em><strong>Nhập Giftcode</strong></em></u></span>&nbsp;</a><em><strong>&nbsp;</strong></em></p>\n\n<p><em><strong>- H&atilde;y chọn server v&agrave; M&atilde; giftcode m&igrave;nh c&oacute; điền v&agrave;o chỗ trống.</strong></em></p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/game%206.png" style="height:486px; line-height:1.6em; width:1019px" /></p>\n\n<p>&nbsp;</p>\n', 'Gift Code luôn có những vật phẩm phong phú và cần thiết đối với quý bằng hữu sau đây BQT Truy Mộng sẽ hướng dẫn quý bằng hữu nhập Gift code', '0', 1, '0', '1970-01-01', '', '2014-11-28 10:11:20', '2014-11-28 10:05:10', '0', ''),
(25, 1, '', 0, '', '0', 'Dò Số May Mắn Nhận KNB Khóa Từ Tiên Nữ', 'do-so-may-man-nhan-knb-khoa-tu-tien-nu', '<p><strong><em>Th&acirc;n&nbsp;ch&agrave;o qu&yacute; bằng hữu.</em></strong></p>\n\n<p>&nbsp; &nbsp;- &nbsp; Sau ng&agrave;y khai mở sever th&agrave;nh c&ocirc;ng, BQT muốn tạo một&nbsp;s&acirc;n chơi cho qu&yacute; bằng hữu giải tr&iacute; sau những gi&acirc;y ph&uacute;t ngao du ti&ecirc;n cảnh v&agrave;&nbsp;kh&aacute;m ph&aacute; t&iacute;nh năng của Truy Mộng.&nbsp;<span style="font-size:13px">Event mang t&ecirc;n<span style="color:#FFFF00"><strong> &quot;</strong></span></span><span style="color:#FFFF00"><strong><span style="font-size:13px">D&ograve; số may mắn nhận KNB kh&oacute;a từ Ti&ecirc;n Nữ&quot;</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<h1><span style="font-size:13px"><img alt="" src="/static/uploads/editor/_thumbs/ti%C3%AAn%20n%E1%BB%AF.jpg" style="height:379px; width:542px" /></span></h1>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp;-<strong> <span style="color:#FFFF00">Thời gian diễn ra sự kiện bắt đầu</span></strong><span style="color:#FFFF00"> </span>: <em><strong>Ng&agrave;y 1/12 đến ng&agrave;y 3/12 [ chốt thời gian l&agrave; 16h c&ugrave;ng ng&agrave;y ]</strong></em></p>\n\n<p>&nbsp; &nbsp;-&nbsp;<span style="color:#FFFF00"><strong>&nbsp;Nội Dung Thể Lệ :</strong></span><em><strong>&nbsp;<span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">C&aacute;c bằng hữu h&atilde;y ra sức d&ugrave;ng &ldquo;Tinh Nguy&ecirc;n&rdquo; xem trộm c&aacute;c Ti&ecirc;n Nữ, chụp ảnh gửi h&igrave;nh ti&ecirc;n nữ của m</span></strong><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px"><strong>&igrave;nh khoe tại Facebook</strong>&nbsp;</span></em></p>\n\n<p><a href="http://www.facebook.com/truymongvn"><span style="color:#FF0000"><strong>www.facebook.com/truymongvn</strong></span></a></p>\n\n<p>&nbsp; <strong>&nbsp; - <span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">Chỉ chụp ảnh Lực chiến Ti&ecirc;n nữ.</span><br />\n<span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">&nbsp; &nbsp;- B&aacute;o danh : t&ecirc;n t&agrave;i khoản&nbsp;</span><br />\n<span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">&nbsp; &nbsp;- T&ecirc;n nh&acirc;n vật của bạn</span><br />\n<span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">&nbsp; &nbsp;- Tag 5 bạn b&egrave; v&agrave;o</span></strong></p>\n\n<p><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">*</span><span style="color:#FFFF00"><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px"><strong> X&aacute;c định người chiến thắng:</strong></span></span><em><strong><span style="color:rgb(20, 24, 35); font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px"> </span><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">Dựa tr&ecirc;n 2 số cuối của H&igrave;nh Ti&ecirc;n nữ , nếu tr&ugrave;ng với kết quả Xổ số Kiến thiết m&agrave; admin lựa chọn trong ng&agrave;y th&igrave; sẽ d&agrave;nh được phần qu&agrave;.</span></strong></em></p>\n\n<p><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">* </span><strong><span style="color:#FFFF00"><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">Xổ số kiến thiết giải t&aacute;m</span></span></strong><span style="color:rgb(20, 24, 35); font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">&nbsp;</span></p>\n\n<p>*<span style="color:#FFFF00"><strong> Lưu &Yacute;:</strong></span><br />\n<span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">&nbsp;- Phần Thưởng: Sau khi sự kiện kết th&uacute;c trong 1 ng&agrave;y ad sẽ trao qua thư trong game.</span></p>\n\n<p><em><strong><span style="font-family:helvetica,arial,lucida grande,tahoma,verdana,arial,sans-serif; font-size:14px">Ch&uacute;c qu&yacute; bằng hữu tham gia vui vẻ.</span></strong></em></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'Thần chào quý bằng hữu.\n', '0', 0, '0', '1970-01-01', '', '2014-12-01 15:38:18', '2014-12-01 14:41:15', '0', ''),
(26, 1, '', 0, '', '0', 'Công Thành Khai Chiến Dự Đoán Kết Quả', 'cong-thanh-khai-chien-du-doan-ket-qua', '<p><span style="color:#FFFFFF">Thần ch&agrave;o qu&yacute; bằng hữu.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFFFF">C&ocirc;ng th&agrave;nh chiến l&agrave; 1 trong những hoạt động Ti&ecirc;n Minh h&agrave;o hừng nhất ingame, để chứng minh 1 Ti&ecirc;n Minh c&oacute; thực lực nhất sever đại diện để đ&aacute;nh li&ecirc;n server.&nbsp;</span></p>\n\n<p><span style="color:#FFFFFF">Kh&ocirc;ng những thế m&agrave; c&ograve;n thể hiện sự l&atilde;nh đạo, uy quyền của Minh Chủ c&oacute; thật sự đủ tố chất để dẫn dắt Ti&ecirc;n Minh của m&igrave;nh bước đến bục vinh quang nhất.&nbsp;</span></p>\n\n<p><span style="color:#FFFFFF">C&oacute; thể tạo đc l&ograve;ng tin của tất cả mọi người trong Ti&ecirc;n Minh v&agrave; trong cả Server hay kh&ocirc;ng.</span></p>\n\n<h1>&nbsp;</h1>\n\n<h1><span style="color:#FFFFFF"><img alt="" src="/static/uploads/editor/_thumbs/Images/bannerTruyMong.jpg" style="height:240px; width:630px" /></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Nội dung </strong>:</span> <span style="color:#FFFFFF">Dự đo&aacute;n ch&iacute;nh x&aacute;c Ti&ecirc;n Minh n&agrave;o chiến thắng&nbsp;</span></p>\n\n<p><span style="color:#FFFF00"><strong>Thời gian diễn ra sự kiện</strong> :</span> <span style="color:#FFFFFF">2/12/2014 [ bắt đầu từ l&uacute;c 12h00 ng&agrave;y 2/12 v&agrave;&nbsp;chốt danh s&aacute;ch v&agrave;o l&uacute;c 18h ng&agrave;y 2/12&nbsp;]</span></p>\n\n<p><strong><span style="color:#FFFF00">Thể lệ b&aacute;o danh :</span></strong><span style="color:#FFFFFF">&nbsp;tại&nbsp;</span><a href="http://www.facebook.com/truymongvn">http://www.facebook.com/truymongvn</a></p>\n\n<p><span style="color:#FFFFFF"><span style="font-size:14px">- T&ecirc;n t&agrave;i khoản :</span></span></p>\n\n<p><span style="color:#FFFFFF"><span style="font-size:14px">- T&ecirc;n nh&acirc;n vật :</span></span></p>\n\n<p><span style="color:#FFFFFF"><span style="font-size:14px">- T&ecirc;n&nbsp;Ti&ecirc;n Minh chiến thắng :</span></span></p>\n\n<p><span style="color:#FFFFFF"><span style="font-size:14px">- T&ecirc;n Minh Chủ :</span></span></p>\n\n<p><span style="color:#FFFFFF">- Số người dự đo&aacute;n giống bạn :</span></p>\n\n<p><span style="color:#FFFFFF">- Tag 5 người bạn</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>Phần thưởng người chiến thắng :</strong></span> <span style="color:#FFFFFF">2000 KNB kh&oacute;a</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFFFF">X&aacute;c định người chiến thắng : dựa tr&ecirc;n c&acirc;u trả lời Ti&ecirc;n Minh chiến thắng v&agrave; c&oacute; số dự đo&aacute;n gần đ&uacute;ng nhất&nbsp;</span></p>\n\n<p><span style="color:#FFFF00"><strong>Lưu &Yacute; </strong>:</span> <span style="color:#FFFFFF">Phần qu&agrave; sẽ trao sau khi sự kiện kết th&uacute;c, trễ nhất l&agrave; 3 ng&agrave;y.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFFFF">Ch&uacute;c Qu&yacute; bằng hữu tham gia vui vẻ.</span></p>\n', 'thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2014-12-02 11:28:50', '2014-12-02 09:13:04', '0', ''),
(67, 2, '', 0, '', '0', 'Hào Hoa Ngất  Trời ( Đợt 2 )', 'hao-hoa-ngat-troi-do-t-2-', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Sau khi Event &quot; Hào Hoa Ng&acirc;́t Trời ( đợt 1 ) &quot; BQT đã nh&acirc;̣n được những phản h&ocirc;̀i t&ocirc;́t v&ecirc;̀ Event này, n&ecirc;n BQT sẽ mở lại Event &nbsp;</strong>H&agrave;o Hoa Ngất ( Trời Đợt 2 ) nhằm tạo cho quý bằng hữu th&ecirc;m hành trang tr&ecirc;n con đường hành t&acirc;̉u giang h&ocirc;̀</p>\n\n<p>&nbsp;</p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/H%C3%A0o%20hoa.jpg" style="height:271px; width:503px" /></strong></h1>\n\n<p>&nbsp;</p>\n\n<p><strong>* Thời Gian : Từ 10:00 Ngày&nbsp;6/1/2015&nbsp;đến 10:00&nbsp;Ngày&nbsp;9/1/2015</strong></p>\n\n<p><strong>* &Aacute;p Dụng To&agrave;n Bộ Server .</strong></p>\n\n<p><strong>* Gồm 2 Sự Kiện :</strong></p>\n\n<p style="text-align:center"><span style="font-size:22px"><u><strong><span style="color:rgb(255, 255, 0)">H&agrave;o Hoa Phong Lưu :&nbsp;</span></strong></u></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>Trong thời gian sự kiện&nbsp;, mỗi ng&agrave;y qu&yacute; bằng hữu nạp &nbsp;KNB &nbsp;&nbsp;tới&nbsp;mốc 1000 KNB hoặc 2000 KNB sẽ nhận được qu&agrave; &nbsp;H&agrave;o Hoa Phong Lưu.( Chỉ nhận được 1 mốc 1 ng&agrave;y v&agrave; c&oacute; t&iacute;ch lũy trong ng&agrave;y ).</strong></p>\n\n<p>&nbsp;</p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%20hoa%201.png" style="height:360px; width:503px" /></strong></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>- Lưu &yacute;:</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>. Với 2 mốc thưởng chỉ nhận được 1 mốc 1 ng&agrave;y v&agrave; c&oacute; t&iacute;ch lũy trong ng&agrave;y&nbsp;.</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>. Qua ng&agrave;y sẽ reset .</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:22px"><u><strong><span style="color:rgb(255, 255, 0)">H&agrave;o Hoa Đại Lễ :</span>&nbsp;</strong></u></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>Trong thời gian sự kiện, mỗi ng&agrave;y qu&yacute; bằng hữu nạp mệnh gi&aacute; thẻ đ&uacute;ng những mốc KNB trong sự kiện sẽ được nhận thưởng . ( Chỉ nhận 1 mốc 1 ng&agrave;y v&agrave; khộng t&iacute;ch lũy ).</strong></p>\n\n<p>&nbsp;</p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%202.png" style="height:493px; width:503px" /></strong></h1>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%203.png" style="height:519px; width:503px" /></strong></h1>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%204.png" style="height:566px; width:503px" /></strong></h1>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%205.png" style="height:332px; width:504px" /></strong></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>- Lưu &yacute; :</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>. Nạp đ&uacute;ng mệnh gi&aacute; mốc KNB mới nhận thưởng v&agrave; kh&ocirc;ng t&iacute;ch lũy .</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>. Mỗi ng&agrave;y nhận 1 mốc / ng&agrave;y .</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong><span style="font-size:13.3333330154419px">. Qua ng&agrave;y sẽ reset .</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 255, 0)"><strong><span style="font-size:13.3333330154419px">* Cả 2 sự kiện chạy song song nghĩa l&agrave; qu&yacute; vị bằng hữu sẽ nhận được cả 2 phần thưởng trong mỗi ng&agrave;y nếu đủ điều kiện.</span></strong></span></p>\n', 'Thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-02-24 10:45:56', '2015-01-06 08:58:34', '0', ''),
(33, 1, '', 0, '', '0', 'Bảo Trì Đột Xuất', 'bao-tri-dot-xuat', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><strong>BQT Truy Mộng xin th&ocirc;ng b&aacute;o<span style="color:#FFFF00"> Bảo Tr&igrave; Đột Xuất </span>server Truy mộng S1 để khắc phục hiện trạng nhận vật phẩm tăng cấp qu&aacute; nhiều, Mong qu&yacute; bằng hữu th&ocirc;ng cảm v&agrave; c&ugrave;ng BQT Truy Mộng vượt qua sự cố n&agrave;y . Về sự mất m&aacute;t t&agrave;i sản v&agrave; tinh thần của qu&yacute; đồng đạo BQT Truy Mộng sẽ đền b&ugrave; một c&aacute;ch thỏa đ&aacute;ng nhất . Một lần nữa mong qu&yacute; đồng đạo h&atilde;y c&ugrave;ng BQT vượt qua &nbsp;sự cố n&agrave;y.</strong></p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="/static/uploads/editor/IMG_07122014_140121.png" style="height:245px; width:447px" /></p>\n\n<p>&nbsp;</p>\n\n<p><strong>* Thời gian Bảo Tr&igrave; :<span style="color:#FFFF00"> 10:00 09/12 đến 15:00 09/12/2014</span></strong></p>\n\n<p><strong>* Những mất m&aacute;t về vật phẩm cũng như KNB qu&yacute; đồng đạo h&atilde;y ghi nhận tại link :</strong><em><strong><a href="https://www.facebook.com/truymongvn/photos/a.687046204736614.1073741828.687029048071663/693798540728047/?type=1"><span style="color:#FF0000">&nbsp;https://www.facebook.com/truymongvn/photos/a.687046204736614.1073741828.687029048071663/693798540728047/?type=1</span></a></strong></em></p>\n\n<p><em><strong>* <span style="color:#FFFF00">C&oacute; thể BQT sẽ cho rollback lại thời điểm 15:00 ng&agrave;y 08/12/2014 v&agrave; trả v&agrave;ng lại cho những đồng đạo bị thất tho&aacute;t.</span><span style="color:#FF0000">&nbsp;</span></strong></em></p>\n\n<p><em><strong>H&atilde;y C&ugrave;ng Vượt Qua Sự Cố N&agrave;y C&ugrave;ng BQT Nh&eacute; Qu&yacute; Đồng Đạo</strong></em></p>\n', 'BQT Truy Mộng xin thông báo bảo trì đột xuất server S1', '0', 0, '0', '1970-01-01', '', '2014-12-09 09:58:12', '2014-12-07 14:19:43', '0', ''),
(66, 1, '', 0, '', '0', 'Đề Phòng Kẻ Gian Lợi Dụng Mạo Danh GM INGAME', 'de-pho-ng-ke-gian-lo-i-du-ng-ma-o-danh-gm-ingame', '<p>Th&acirc;n chào quý bằng hữu.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp;Sau 1 thời gian &ocirc;̉n định của 5 Cụm máy chủ Truy M&ocirc;̣ng cùng với lòng tin và sự tin tưởng của quý bằng hữu của 5 Cụm máy chủ. Vì th&ecirc;́ đã có Kẻ Lạc L&ocirc;́i&nbsp;lợi dụng lòng tin đó đ&ecirc;̉ x&acirc;m nh&acirc;̣p vào tài khoản nhằm chu&ocirc;̣c lợi bản th&acirc;n, nay BQT vi&ecirc;́t bài này nhằm l&ecirc;n án và cảnh báo quý bằng hữu, hãy c&acirc;̉n th&acirc;̣n đ&ecirc;̉ bảo v&ecirc;̣ tài sản của mình.</p>\n\n<p>&nbsp; &nbsp; &nbsp;BQT sẽ dùng bi&ecirc;̣n pháp mạnh nh&acirc;́t đ&ocirc;́i với những Kẻ Lạc L&ocirc;́i này là [ Khóa Acc Vĩnh Vi&ecirc;̃n Đi&ecirc;̀u Tra IP Và Banned Toàn B&ocirc;̣ ]</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FAEBD7"><span style="font-size:16px"><u><strong>Những đi&ecirc;̀u c&acirc;̀n lưu ý khi giao ti&ecirc;́p với GM ingame:</strong></u></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 255, 0)">&nbsp; 1/ [ Quý bằng hữu ko giao ID và Pass cho b&acirc;́t kì 1 ai mạo nh&acirc;̣n là GM và Pm thẳng qua chủ acc ]</span></p>\n\n<p><span style="color:rgb(255, 255, 0)">&nbsp; 2/ [ GM ko bao giờ hỏi ID của chủ acc ]</span></p>\n\n<p><span style="color:rgb(255, 255, 0)">&nbsp; 3/ [ Gm ingame sẽ có 1 Set thời trang, Danh Hi&ecirc;̣u và Tọa Kỵ Ảo Hóa Hi&ecirc;́m ]</span></p>\n\n<p><span style="color:rgb(255, 255, 0)">&nbsp; 4/ [ N&ecirc;́u Quý bằng hữu nào đã bị kẻ gian lợi dụng lòng tin đ&ecirc;̉ chi&ecirc;́m đoạt tài sản xin vui lòng báo g&acirc;́p cho Ad Face đ&ecirc;̉ xử lý ]</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:16px"><strong><u><span style="color:#FAEBD7">Hình ảnh GM ingame :</span></u></strong></span></p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/gmtruymong.png" style="height:498px; width:1068px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><u><strong><span style="font-size:16px"><span style="color:#FAEBD7">Cách ph&acirc;n bi&ecirc;̣t GM&nbsp;th&acirc;̣t và GM Mạo Danh :</span></span></strong></u></p>\n\n<p>&nbsp;</p>\n\n<p>- Kh&ocirc;ng bao giờ pm cho GM khi có th&ocirc;ng tin là&nbsp;nh&acirc;̣n quà hay CODE [ vì những th&ocirc;ng tin quà hay CODE đ&ecirc;̀u sẽ có tr&ecirc;n trang chủ ]</p>\n\n<p>- Kh&ocirc;ng bao giờ cung c&acirc;́p ID &amp; Pass cho Gm ingame .</p>\n\n<p>- Vị trí GM đứng ngay giữa quãng trường.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/ingame/GMINGAME3.png" style="height:497px; width:565px" /></h1>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/ingame/GMINGAME4.png" style="height:480px; width:539px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>Chúc quý bằng hữu vui vẻ và cảnh giác kẻ gian nhé.&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-01-05 14:48:05', '2015-01-05 14:37:50', '0', ''),
(64, 1, '', 0, '', '0', 'Khai Mở Máy Chủ Mới Truy Mộng S5', 'khai-mo-ma-y-chu-mo-i-truy-mo-ng-s5', '<p>Th&acirc;n Chào quý bằng hữu.</p>\n\n<p>&nbsp; &nbsp; &nbsp;Hơn 1 tháng sau khi Truy M&ocirc;̣ng S1 Khải mở, BQT Đã nh&acirc;̣n đc phản h&ocirc;̀i t&ocirc;́t v&ecirc;̀ game từ những quý bằng hữu tham gia game nay BQT Khai mở Máy Chủ Truy M&ocirc;̣ng S6 nhằm tạo ra 1 s&acirc;n chơi mới cho các bằng hữu đã lỡ chuy&ecirc;́n ngao du của những l&acirc;̀n trước, những tr&acirc;̣n đánh lớn, những phó bản c&acirc;̀n sự góp sức của bằng hữu, t&acirc;́t cả sẽ có trong Máy Chũ Truy M&ocirc;̣ng S6</p>\n\n<p>&nbsp;</p>\n\n<h1>&nbsp;</h1>\n\n<p>&nbsp;</p>\n\n<p>Thời gian khai mở :&nbsp;<span style="color:rgb(255, 255, 0)">10h00 - 10h15</span>&nbsp;Ng&agrave;y &nbsp;<span style="color:#FFFF00">9/1/2015</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:rgb(255, 255, 0)"><span style="font-family:arial,helvetica,sans-serif">Nội Dung :</span></span></strong></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Tưng b&ugrave;ng khuyến m&atilde;i với những sự kiện khai mở mang đến những phần qu&agrave; qu&yacute; gi&aacute; v&agrave; hấp dẫn gi&uacute;p qu&yacute; b&agrave;ng hữu kh&ocirc;ng &iacute;t tr&ecirc;n con đường h&agrave;nh hiệp</span></span></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Gồm những sự kiện ti&ecirc;u biểu như:&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 0, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">-&nbsp;Nạp card nhận mốc khuyến m&atilde;i</span></span></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">-&nbsp;Nạp card x100% KNB</span></span></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">-&nbsp;Ti&ecirc;u Ph&iacute; nhận thưởng</span></span></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">-&nbsp;Qu&agrave; t&acirc;n thủ</span></span></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">- Tặng Hoa Ng&agrave;y Cưới</span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="color:rgb(255, 255, 0)"><span style="font-family:arial,helvetica,sans-serif">Những sự kiện ingame :</span></span></strong></span></p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Lưu &Yacute; :</span></span></strong></p>\n\n<p><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Để nhanh ch&oacute;ng tiếp cận v&agrave; dễ d&agrave;ng h&agrave;nh hiệp th&igrave; qu&yacute; bằng hữu h&atilde;y tham khảo qua những hướng dẫn cẩm nang giới thiệu t&iacute;nh năng :</span></span></p>\n\n<p>&nbsp;</p>\n\n<table border="1" cellpadding="1" cellspacing="1" style="line-height:20.7999992370605px; width:500px">\n	<tbody>\n		<tr>\n			<td style="text-align:center"><span style="color:rgb(255, 255, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Hướng dẫn chơi game</span></span></span></td>\n			<td style="text-align:center"><span style="color:rgb(255, 255, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Hướng dẫn Cường H&oacute;a</span></span></span></td>\n		</tr>\n		<tr>\n			<td style="text-align:center"><span style="color:rgb(255, 255, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Hướng dẫn Nạp Thẻ</span></span></span></td>\n			<td style="text-align:center"><span style="color:rgb(255, 255, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Hướng dẫn nhập gift code</span></span></span></td>\n		</tr>\n		<tr>\n			<td style="text-align:center"><span style="color:rgb(255, 255, 0)"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đặc Quyền Vip</span></span></span></td>\n			<td>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p>Chúc quý bằng hữu tham gia Truy M&ocirc;̣ng S6&nbsp;vui vẻ</p>\n', 'Thân Chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-01-08 17:44:28', '2015-01-03 11:03:08', '0', ''),
(39, 1, '', 0, '', '0', 'Tặng Hoa Ngày Cưới', 'tang-hoa-ngay-cuoi', '<p>th&acirc;n ch&agrave;o qu&yacute; bằng hữu</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp; Tr&ecirc;n con đường ngao du ti&ecirc;n cảnh, h&agrave;nh hiệp giang hồ đa phần c&aacute;c hảo h&aacute;n đều muốn độc bước chinh phục tất cả những điều b&iacute; ẩn. nhưng cũng sẽ c&oacute; 1 số hảo h&aacute;n muốn c&ugrave;ng tri kỉ</p>\n\n<p>kh&aacute;m ph&aacute; những chốn ti&ecirc;n bồng, ảo diệu của tạo h&oacute;a nay BQT muốn tạo th&ecirc;m sự gắn kết những đ&ocirc;i uy&ecirc;n ương bằng 1 Event &quot; Tặng Hoa Ng&agrave;y Cưới &quot; kết nối y&ecirc;u thương.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Tang%20hoa%20ng%C3%A0y%20c%C6%B0%E1%BB%9Bi.jpg" style="height:408px; width:636px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><u><strong><span style="color:#FFFFFF">Nội Dung : Event sẽ t&iacute;nh dựa tr&ecirc;n :</span></strong></u></span></p>\n\n<p>&nbsp;</p>\n\n<p>-&nbsp;<span style="color:#FFFF00">Top 10 người</span> c&oacute;&nbsp;số điểm tổng kết cao nhất&nbsp;ở Khuynh Quốc</p>\n\n<p>-&nbsp;<span style="color:#FFFF00">Top 10 người&nbsp;</span>c&oacute;&nbsp;số điểm&nbsp;tổng kết cao nhất&nbsp;ở So&aacute;i Kh&iacute;&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="font-size:20px"><span style="color:#FFFF00">&nbsp; &nbsp; [ H&igrave;nh ảnh v&iacute; dụ ]</span></span></p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/tanghoangaycuoi2.png" style="height:352px; width:408px" /></h1>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/tanghoangaycuoi1.bmp" style="height:540px; width:714px" /></h1>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/tanghoangaycuoi3.bmp" style="height:540px; width:713px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><u><strong><span style="color:#FFFFFF">Thời gian :</span></strong></u></span> bắt đầu <span style="color:#FFFF00">19h30</span> Ng&agrave;y <span style="color:#FFFF00">12/12/2014</span> v&agrave; kết th&uacute;c v&agrave;o l&uacute;c <span style="color:#FFFF00">12h00</span> ng&agrave;y <span style="color:#FFFF00">17/12/2014</span></p>\n\n<p><span style="font-size:14px"><u><strong><span style="color:#FFFFFF">Phần Thưởng </span></strong></u></span>: Top của So&aacute;i Kh&iacute; v&agrave; Khuynh Quốc tương đương</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/toptanghoanguoithuong.png" style="height:733px; width:418px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>Ch&uacute;c qu&yacute; bằng hữu S3 vui vẻ</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'thân chào quý bằng hữu', '0', 0, '0', '1970-01-01', '', '2014-12-11 17:13:31', '2014-12-11 16:50:54', '0', ''),
(42, 1, '', 0, '', '0', 'Nhận Đồ Thần Với Vòng Quay May Mắn', 'nhan-do-than-voi-vong-quay-may-man', '<p><em><strong>Than ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><strong>Sự khan hiếm về vật phẩm cũng như đồ &quot; Thần &quot; trong game m&agrave; chỉ những đại gia v&agrave; cường giả nhận&nbsp;nay sẽ được&nbsp;BQT Truy Mộng đưa đến với tất cả mọi người th&ocirc;ng qua sự kiện &quot; V&ograve;ng Quay May Mắn &quot; gồm nhiều vật phẩm gi&aacute; trị cũng như hiếm c&oacute; trong game .</strong></p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/V%C3%B2ng%20quay.png" style="height:593px; width:451px" /></strong></h1>\n\n<p><strong>* Thời Gian : 20:00 ng&agrave;y&nbsp;12/12/2014 đến 10:00 Ng&agrave;y 10/01/2015</strong></p>\n\n<p><strong>*<span style="color:#FFFF00"> &Aacute;p Dụng Cho Tất Cả Server&nbsp;</span></strong></p>\n\n<p><strong>* Nội Dung : Với mỗi lần nạp 20.000 VND sẽ nhận miễn ph&iacute; 1 lượt quay (<span style="color:#FFFF00"> kh&ocirc;ng mất KNB của bạn </span>)</strong></p>\n\n<p><strong>* <span style="color:#FFFF00">Click V&agrave;o D&acirc;y Để Quay </span>:&nbsp;<a class="bt-vongquay" href="javascript:;"><img alt="" src="/static/uploads/editor/n%C3%BAt%20quay.JPG" style="height:20px; width:100px" /></a></strong></p>\n\n<p><strong>* Với t&agrave;i khoản mới đăng k&yacute; c&aacute;c bạn h&atilde;y x&aacute;c nhận Mail, Số ĐT , Plugin sẽ nhận được 3 v&ograve;ng quay miễn ph&iacute; ( Click v&agrave;o Quay May Mắn )&nbsp;:</strong></p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/vong%20quay%202.JPG" style="height:271px; width:503px" /></strong></h1>\n\n<p><strong><span style="color:#FF0000"><em>Lưu &Yacute; :</em></span></strong></p>\n\n<p><strong><span style="color:#FF0000"><em>- Kh&ocirc;ng hạn chế lượt quay&nbsp;</em></span></strong></p>\n\n<p><strong><span style="color:#FF0000"><em>- Được cộng dồn lượt quay ( 20.000/1 lượt )</em></span></strong></p>\n\n<p><strong><span style="color:#FF0000"><em>- V&ograve;ng quay miễn ph&iacute; kh&ocirc;ng trừ KNB của bạn.</em></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'BQT Truy Mộng xin gửi đến các bạn Vòng Quay May Mắn .....', '0', 0, '0', '1970-01-01', '', '2015-01-01 14:18:37', '2014-12-12 10:46:59', '0', ''),
(43, 1, '', 0, '', '0', 'Truy Tìm Và Diệt GM Nhận Quà Hấp Dẫn', 'truy-tim-va-diet-gm-nhan-qua-hap-dan', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><em><strong>Để th&ecirc;m phần hấp dẫn v&agrave; mang đến vật phẩm đến tay qu&yacute; đồng đạo nhiều hơn BQT Truy Mộng sẽ mở event Truy T&igrave;m GM ở to&agrave;n server S1 ,S2 ,S3 v&agrave;o những mốc thời gian ở từng server sẽ xuất hiện GM v&agrave; để c&aacute;c qu&yacute; bằng hữu truy t&igrave;m.</strong></em></p>\n\n<h1><em><strong><img alt="" src="/static/uploads/editor/Truy%20T%C3%ACm%20GM.jpg" style="height:271px; width:503px" /></strong></em></h1>\n\n<p><em><strong>* &Aacute;p Dụng To&agrave;n Server</strong></em></p>\n\n<p><em><strong>* Thời Gian: 15/12/2014 - 22/12/2014 <span style="color:#FFFF00">( V&agrave;o Mốc Giờ Cố Định )</span></strong></em></p>\n\n<p><span style="color:#FFFF00"><em><strong>- S1 : 17:00 - 17:15&nbsp;</strong></em></span></p>\n\n<p><span style="color:#FFFF00"><em><strong>- S2 : 17:15 - 17:30</strong></em></span></p>\n\n<p><span style="color:#FFFF00"><em><strong>- S3 : 17:30 - 17:45</strong></em></span></p>\n\n<p><em><strong>- H&igrave;nh Ảnh GM :</strong></em></p>\n\n<h1><em><strong><img alt="" src="/static/uploads/editor/Truy%20T%C3%ACm%20GM.png" style="height:363px; width:554px" /></strong></em></h1>\n\n<p><em><strong>* Nội Dung: Sau khi xuất hiện GM sẽ th&ocirc;ng b&aacute;o cho qu&yacute; đồng đạo , h&atilde;y nhanh ch&oacute;ng truy t&igrave;m v&agrave; ai l&agrave; người giết được GM đầu ti&ecirc;n sẽ nhận được phần thưởng hấp dẫn. <span style="color:#FF0000">( 1 Server 1 người / ng&agrave;y )</span></strong></em></p>\n\n<p><em><strong>* Phần Thưởng : 1000 KNB (Kh&ocirc;ng kh&oacute;a) , 1 T&uacute;i Bảo Thạch 3 .</strong></em></p>\n\n<p><em><strong>* Lưu &Yacute;:</strong></em></p>\n\n<p><span style="color:#FF0000"><em><strong>- Một server chỉ xuất hiện 1 GM v&agrave;o thời điểm th&ocirc;ng b&aacute;o v&agrave; giết được GM sẽ được phần thưởng ( 1 lần /ng&agrave;y )</strong></em></span></p>\n\n<p><span style="color:#FF0000"><em><strong>- Gm sẽ th&ocirc;ng b&aacute;o sự xuất hiện của m&igrave;nh nhưng kh&ocirc;ng th&ocirc;ng b&aacute;o Map qu&yacute; đồng đạo phải tự t&igrave;m.</strong></em></span></p>\n\n<p><span style="color:#FF0000"><em><strong>- Ai giết được Gm sẽ được Gm th&ocirc;ng b&aacute;o v&agrave; kết th&uacute;c SK ng&agrave;y, ng&agrave;y tiếp theo sẽ xuất hiện tiếp.</strong></em></span></p>\n', 'Góp phần vào sự kiện ingame BQT Truy Mộng xin gửi đến quý .....', '0', 0, '0', '1970-01-01', '', '2014-12-15 16:00:19', '2014-12-15 16:00:19', '0', ''),
(44, 1, '', 0, '', '0', 'Truy Tìm Và Diệt GM Nhận Quà', 'truy-tim-va-diet-gm-nhan-qua', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><em><strong>Để th&ecirc;m phần hấp dẫn v&agrave; mang đến vật phẩm đến tay qu&yacute; đồng đạo nhiều hơn BQT Truy Mộng sẽ mở event Truy T&igrave;m GM ở to&agrave;n server S1 ,S2 ,S3 v&agrave;o những mốc thời gian ở từng server sẽ xuất hiện GM v&agrave; để c&aacute;c qu&yacute; bằng hữu truy t&igrave;m.</strong></em></p>\n\n<h1><em><strong><img alt="" src="/static/uploads/editor/Truy%20T%C3%ACm%20GM.jpg" style="height:271px; width:503px" /></strong></em></h1>\n\n<p><em><strong>* &Aacute;p Dụng To&agrave;n Server</strong></em></p>\n\n<p><em><strong>* Thời Gian: 15/12/2014 - 22/12/2014 <span style="color:#FFFF00">( V&agrave;o Mốc Giờ Cố Định )</span></strong></em></p>\n\n<p><span style="color:#FFFF00"><em><strong>- S1 : 17:00 - 17:15&nbsp;</strong></em></span></p>\n\n<p><span style="color:#FFFF00"><em><strong>- S2 : 17:15 - 17:30</strong></em></span></p>\n\n<p><span style="color:#FFFF00"><em><strong>- S3 : 17:30 - 17:45</strong></em></span></p>\n\n<p><em><strong>- H&igrave;nh Ảnh GM :</strong></em></p>\n\n<h1><em><strong><img alt="" src="/static/uploads/editor/Truy%20T%C3%ACm%20GM.png" style="height:363px; width:554px" /></strong></em></h1>\n\n<p><em><strong>* Nội Dung: Sau khi xuất hiện GM sẽ th&ocirc;ng b&aacute;o cho qu&yacute; đồng đạo , h&atilde;y nhanh ch&oacute;ng truy t&igrave;m v&agrave; ai l&agrave; người giết được GM đầu ti&ecirc;n sẽ nhận được phần thưởng hấp dẫn. <span style="color:#FF0000">( 1 Server 1 người / ng&agrave;y )</span></strong></em></p>\n\n<p><em><strong>* Phần Thưởng : 1000 KNB (Kh&ocirc;ng kh&oacute;a) , 1 T&uacute;i Bảo Thạch 3 .</strong></em></p>\n\n<p><em><strong>* Lưu &Yacute;:</strong></em></p>\n\n<p><span style="color:#FF0000"><em><strong>- Một server chỉ xuất hiện 1 GM v&agrave;o thời điểm th&ocirc;ng b&aacute;o v&agrave; giết được GM sẽ được phần thưởng ( 1 lần /ng&agrave;y )</strong></em></span></p>\n\n<p><span style="color:#FF0000"><em><strong>- Gm sẽ th&ocirc;ng b&aacute;o sự xuất hiện của m&igrave;nh nhưng kh&ocirc;ng th&ocirc;ng b&aacute;o Map qu&yacute; đồng đạo phải tự t&igrave;m.</strong></em></span></p>\n\n<p><span style="color:#FF0000"><em><strong>- Ai giết được Gm sẽ được Gm th&ocirc;ng b&aacute;o v&agrave; kết th&uacute;c SK ng&agrave;y, ng&agrave;y tiếp theo sẽ xuất hiện tiếp.</strong></em></span></p>\n', 'Góp phần vào sự kiện ingame BQT Truy Mộng xin gửi đến quý .....', '0', 0, '0', '1970-01-01', '', '2014-12-15 16:00:31', '2014-12-15 16:00:31', '0', ''),
(48, 1, '', 0, '', '0', 'Hào Hoa Ngất Trời', 'hao-hoa-ngat-troi', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu,</strong></em></p>\n\n<p><strong>Sự khan hiếm của những tr&acirc;n bảo v&agrave; vật phẩm trong con đường bu&ocirc;n tẩu của qu&yacute; bằng hữu sẽ được BQT Truy Mộng mang đến cho qu&yacute; bằng hữu th&ocirc;ng qua bộ đ&ocirc;i Sự Kiện H&agrave;o Hoa Ngất Trời .</strong></p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/H%C3%A0o%20hoa.jpg" style="height:271px; width:503px" /></strong></h1>\n\n<p><strong>* Thời Gian : Từ 10:00 19/12/2014 đến 23:59 22/12/2014</strong></p>\n\n<p><strong>* &Aacute;p Dụng To&agrave;n Bộ Server .</strong></p>\n\n<p><strong>* Gồm 2 Sự Kiện :</strong></p>\n\n<p><strong><span style="color:#FFFF00">- H&agrave;o Hoa Phong Lưu :&nbsp;</span>Trong thời gian sự kiện&nbsp;, mỗi ng&agrave;y qu&yacute; bằng hữu nạp &nbsp;KNB &nbsp;&nbsp;tới&nbsp;mốc 1000 KNB hoặc 2000 KNB sẽ nhận được qu&agrave; &nbsp;H&agrave;o Hoa Phong Lưu.( Chỉ nhận được 1 mốc 1 ng&agrave;y v&agrave; c&oacute; t&iacute;ch lũy trong ng&agrave;y ).</strong></p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%20hoa%201.png" style="height:360px; width:503px" /></strong></h1>\n\n<p><span style="color:#FF0000"><strong>- Lưu &yacute;:</strong></span></p>\n\n<p><span style="color:#FF0000"><strong>. Với 2 mốc thưởng chỉ nhận được 1 mốc 1 ng&agrave;y v&agrave; c&oacute; t&iacute;ch lũy trong ng&agrave;y&nbsp;.</strong></span></p>\n\n<p><span style="color:#FF0000"><strong>. Qua ng&agrave;y sẽ reset .</strong></span></p>\n\n<p><strong><span style="color:#FFFF00">- H&agrave;o Hoa Đại Lễ :</span> Trong thời gian sự kiện, mỗi ng&agrave;y qu&yacute; bằng hữu nạp mệnh gi&aacute; thẻ đ&uacute;ng những mốc KNB trong sự kiện sẽ được nhận thưởng . ( Chỉ nhận 1 mốc 1 ng&agrave;y v&agrave; khộng t&iacute;ch lũy ).</strong></p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%202.png" style="height:493px; width:503px" /></strong></h1>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%203.png" style="height:519px; width:503px" /></strong></h1>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%204.png" style="height:566px; width:503px" /></strong></h1>\n\n<h1><strong><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/Images/TopS1%20%2B%20S2/qu%C3%A0%20h%C3%A0o%20hoa/hoa%205.png" style="height:332px; width:504px" /></strong></h1>\n\n<p><span style="color:#FF0000"><strong>- Lưu &yacute; :</strong></span></p>\n\n<p><span style="color:#FF0000"><strong>. Nạp đ&uacute;ng mệnh gi&aacute; mốc KNB mới nhận thưởng v&agrave; kh&ocirc;ng t&iacute;ch lũy .</strong></span></p>\n\n<p><span style="color:#FF0000"><strong>. Mỗi ng&agrave;y nhận 1 mốc / ng&agrave;y .</strong></span></p>\n\n<p><span style="color:#FF0000"><strong><span style="font-size:13.3333330154419px">. Qua ng&agrave;y sẽ reset .</span></strong></span></p>\n\n<p><span style="color:#FFFF00"><strong><span style="font-size:13.3333330154419px">* Cả 2 sự kiện chạy song song nghĩa l&agrave; qu&yacute; vị bằng hữu sẽ nhận được cả 2 phần thưởng trong mỗi ng&agrave;y nếu đủ điều kiện.</span></strong></span></p>\n', 'Sự khan hiếm của những trân bảo và vật phẩm trong con đường buôn tẩu của quý bằng hữu sẽ được BQT Truy Mộng ....', '0', 0, '0', '1970-01-01', '', '2014-12-18 18:12:55', '2014-12-18 17:03:03', '0', '');
INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(148, 3, '', 0, '', '0', 'Hướng dẫn vượt Phó Bản - Thần Ma Chi Tinh', 'huong-dan-vuot-pho-ban-than-ma-chi-tinh', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Hướng dẫn vượt Ph&oacute; Bản - Thần Ma Chi Tinh</div>\n\n<div class="content" style="margin: 0px; padding: 0px;">\n<div id="post_message_573" style="margin: 0px; padding: 0px;">\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium"><span style="font-size:xx-large">C</span>h&agrave;o c&aacute;c bạn của Truy mộng</span></span><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">Phụ bản Thần Ma Chi Tỉnh chắc hẳn đ&atilde; l&agrave;m c&aacute;c bạn phải suy nghĩ đủ mọi c&aacute;ch đặt thủ vệ sao cho hợp l&yacute; để vượt được đến tầng cuối c&ugrave;ng - tầng 60&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/145.gif" style="border:medium none; margin:0px; padding:0px" title="145" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">Sau đ&acirc;y m&igrave;nh sẽ hướng dẫn cho c&aacute;c bạn chi tiết c&aacute;ch sắp xếp c&aacute;c Thủ Vệ đạt hiệu quả cao nhất&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/34.gif" style="border:medium none; margin:0px; padding:0px" title="34" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/34.gif" style="border:medium none; margin:0px; padding:0px" title="34" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">C&aacute;c bạn lưu &yacute; dame của Thủ Vệ chịu ảnh hưởng trực tiếp từ Nh&acirc;n Vật, lực chiến của nh&acirc;n vật cao th&igrave; damage của thủ vệ cao&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/34.gif" style="border:medium none; margin:0px; padding:0px" title="34" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/34.gif" style="border:medium none; margin:0px; padding:0px" title="34" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">Để đến được tầng 60:&nbsp;<span style="font-size:large">kiến nghị lực chiến nh&acirc;n vật phải đạt 65k trở l&ecirc;n</span>&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/193.gif" style="border:medium none; margin:0px; padding:0px" title="193" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/193.gif" style="border:medium none; margin:0px; padding:0px" title="193" /><span style="font-family:tahoma"><span style="font-size:medium">&nbsp;<br />\n<br />\nBắt đầu diễn th&ocirc;i c&aacute;c bạn quan s&aacute;t v&agrave; l&agrave;m theo những h&igrave;nh ảnh dưới đ&acirc;y nh&eacute;&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/7.gif" style="border:medium none; margin:0px; padding:0px" title="7" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/7.gif" style="border:medium none; margin:0px; padding:0px" title="7" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium"><strong><u><em>Đầu ti&ecirc;n</em></u></strong>&nbsp;với 3 thủ vệ cấp-1 lv-1</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/fsk217N.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium"><strong><u><em>Bước 2</em></u>:</strong>&nbsp;C&aacute;c bạn cần tăng cấp 3 thủ vệ l&ecirc;n cấp-2 lv-1&nbsp;<br />\n1- Ph&aacute; gi&aacute;p ch&uacute; sư&nbsp;<br />\n2- Ph&aacute;p thuật thủ vệ&nbsp;<br />\n3- Cung tiễn thủ vệ&nbsp;</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/C1rLisj.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium"><strong><u><em>Bước 3</em></u>:</strong>&nbsp;C&aacute;c bạn cần tăng cấp Ph&aacute;p Thuật Thủ Vệ th&agrave;nh Hấp Hồn Sư Thủ Vệ (đ&aacute;nh chậm, dame cao, mỗi lần đ&aacute;nh +10 t&iacute;ch điểm &quot;lv 1+10 -&gt; lv 2+15 -&gt; lv 3+20 &quot;), về sau c&aacute;c bạn chỉ cần tăng đến lv-2 l&agrave; được, khi c&aacute;c Thủ Vệ kh&aacute;c đ&atilde; đạt cấp-3 lv-3 th&igrave; c&aacute;c bạn x&oacute;a Thủ Vệ n&agrave;y đi chuyển th&agrave;nh Tất Phong Thủ vệ ), d&agrave;nh t&iacute;ch điểm m&agrave; l&ecirc;n Thủ Vệ Hấp Hồn Sư trước nh&eacute;&nbsp;</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/yOwzXvE.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium"><strong><u><em>Bước 4</em></u>:</strong>&nbsp;Tăng cấp Cung Tiễn Thủ Vệ th&agrave;nh Tất Phong Thủ Vệ (đ&aacute;nh nhanh, dame cao)&nbsp;</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/Bwjltbl.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium"><strong><u><em>Bước 5</em></u>:</strong>&nbsp;Tăng cấp Ph&aacute; Gi&aacute;p Ch&uacute; Sư th&agrave;nh &Ocirc;n Dịch Ch&uacute; Sư (ph&aacute; gi&aacute;p nh&oacute;m, giảm ph&ograve;ng ngự nh&oacute;m)&nbsp;</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/xPI2OTO.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium"><strong><u><em>Bước 6</em></u>:</strong>&nbsp;C&aacute;c bạn đặt Thủ Vệ v&agrave; tăng cấp l&ecirc;n Tất Phong Thủ Vệ theo thứ tự như những h&igrave;nh dưới đ&acirc;y nh&eacute;</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/0pC8WuH.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<img alt="" src="http://i.imgur.com/t9fDcD3.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<img alt="" src="http://i.imgur.com/GekBQ53.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<img alt="" src="http://i.imgur.com/qXzBkpa.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<img alt="" src="http://i.imgur.com/qBKcscn.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">Tiếp theo c&aacute;c bạn tăng to&agrave;n bộ Thủ Về l&ecirc;n lv-2 trước ti&ecirc;n l&agrave; tăng &Ocirc;n Dịch Ch&uacute; Sư với Hấp Hồn Ch&uacute; Sư l&ecirc;n lv-2 nh&eacute;</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/ZBHi2wX.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">Sau khi to&agrave;n bộ Thủ Vệ đ&atilde; đạt lv-2, c&aacute;c bạn tăng &Ocirc;n Dịch Ch&uacute; Sư l&ecirc;n lv-3 v&agrave; 2 Tất Phong Thủ Vệ h&agrave;ng dưới l&ecirc;n lv-3 tiếp theo l&agrave; h&agrave;ng tr&ecirc;n, sau khi Hấp H&ocirc;n Sư đ&atilde; ho&agrave;n th&agrave;nh sứ mệnh t&iacute;ch điểm xong c&aacute;c bạn thay Tất Phong Thủ Vệ v&agrave;o vị tr&iacute; của Hấp H&ocirc;n Sư nh&eacute;.</span></span><br />\n<br />\n<img alt="" src="http://i.imgur.com/kJxFnaC.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<img alt="" src="http://i.imgur.com/7seVbcF.jpg" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<br />\n<span style="font-family:tahoma"><span style="font-size:medium">Với đội h&igrave;nh mạnh mẽ như tr&ecirc;n c&aacute;c bạn c&oacute; Lực chiến cao thoải m&aacute;i l&agrave;m việc kh&aacute;c c&ograve;n lại đến tầng 60 th&igrave; đ&atilde; c&oacute; Thủ Vệ lo, c&ograve;n c&aacute;c bạn m&agrave; c&oacute; LC 75k trở xuống h&atilde;y để &yacute; những lượt qu&aacute;i c&oacute; độ ph&ograve;ng ngự v&agrave; HP cao ( ồ tất nhi&ecirc;n l&agrave; tốc độ của ch&uacute;ng rất chậm, lượt qu&aacute;i tiếp theo c&oacute; độ di chuyển nhanh ) h&atilde;y gi&uacute;p đỡ Thủ Vệ 1 tay bằng c&aacute;ch, ti&ecirc;u hao KNB l&agrave;m cho&aacute;ng lượt qu&aacute;i đ&oacute; trong 5 gi&acirc;y để c&aacute;c Thủ Vệ đủ thời gian ti&ecirc;u diệt ch&uacute;ng, kh&ocirc;ng cho ch&uacute;ng đến được trụ của bạn. Với c&aacute;c Boss lượt cuối c&aacute;c bạn n&ecirc;n d&ugrave;ng skill m&agrave; phụ bạn c&oacute; để đ&aacute;nh v&agrave; l&agrave;m chậm tốc độ di chuyển của boss đi trong 2 gi&acirc;y nh&eacute;.&nbsp;<br />\n<br />\nCh&uacute;c c&aacute;c bạn chơi game vui vẻ v&agrave; th&agrave;nh c&ocirc;ng vượt qua tầng 60 của Thần Ma Chi Tỉnh...........</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/3.gif" style="border:medium none; margin:0px; padding:0px" title="3" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/3.gif" style="border:medium none; margin:0px; padding:0px" title="3" /></span></blockquote>\n</div>\n</div>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:16:37', '2015-02-25 16:15:10', '0', ''),
(62, 1, '', 0, '', '0', 'Hái Lộc Đâu Năm Lì Xì May Mắn', 'ha-i-lo-c-dau-nam-li-xi-may-ma-n', '<p>Th&acirc;n chào quý bằng hữu.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/503x2709(1).png" style="height:271px; width:503px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; <span style="font-size:14px">&nbsp;Lời mỡ đ&acirc;̀u BQT xin ch&acirc;n thành cám ơn sự ủng h&ocirc;̣ nhi&ecirc;̣t tình của Quý bằng hữu trong thời gian vừa qua, sau 1 s&ocirc;́ sai sót trong thời gian qua BQT sẽ c&ocirc;́ gắng khắc phục tri&ecirc;̣t đ&ecirc;̉. Sẽ tạo 1 s&acirc;n chơi giao lưu dành cho Quý bằng hữu.</span></p>\n\n<p><span style="font-size:14px">&nbsp; &nbsp; BQT Chúc Quý bằng hữu 1 năm mới :&nbsp;</span></p>\n\n<p>&nbsp;</p>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Đong cho đầy hạnh ph&uacute;c. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">G&oacute;i cho trọn lộc t&agrave;i. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Giữ cho m&atilde;i an khang. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Thắt cho chặt ph&uacute; qu&yacute;. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">C&ugrave;ng ch&uacute;c nhau như &yacute;. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Hứng cho tr&ograve;n an khang. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Ch&uacute;c năm mới b&igrave;nh an. </span></span></span></h1>\n\n<h1><span style="color:#FFFF00"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Cả nh&agrave; đều sung t&uacute;c</span></span></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFFFF"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Nội Dung : N</span></span></span>h&acirc;n&nbsp;dịp đầu năm mới BQT Khuyến m&atilde;i 100% bất k&igrave; mệnh gi&aacute; card</p>\n\n<p>Phạm Vi : Tất cả 4 cụm m&aacute;y chủ Truy Mộng</p>\n\n<p>Thời Gian&nbsp;: 1h15 Ng&agrave;y 1/1/2015 &nbsp;đến 23h59 ng&agrave;y 2/1/2015</p>\n\n<p>&nbsp;</p>\n\n<p>Ch&uacute;c Qu&yacute; Bằng Hữu&nbsp;Năm Mới Vui Vẻ.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'Thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-01-01 13:07:08', '2015-01-01 13:06:11', '0', ''),
(63, 1, '', 0, '', '0', 'GIFTCODE Năm Mới & Cảnh Giác Trong Năm Mới', 'giftcode-nam-mo-i-ca-nh-gia-c-trong-nam-mo-i', '<p>Th&acirc;n chào quý bằng hữu</p>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/2015.png" style="height:432px; width:475px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp;Năm cũ đã qua cùng với sự hoạt đ&ocirc;̣ng của Game Truy M&ocirc;̣ng, Năm 2014 vừa qua&nbsp;&nbsp;đã có nhi&ecirc;̀u sự c&ocirc;́ ko đáng đã xảy ra cũng đã làm 1 s&ocirc;́ Quý bằng hữu ko hài lòng trong năm trước vì th&ecirc;́ trong năm mới 2015 này BQT sẽ c&ocirc;́ gắng hạn ch&ecirc;́ những l&ocirc;̃i ảnh hưởng đ&ecirc;́n Game và ảnh hưởng đ&ecirc;́n Quý bằng hữu 1 cách tri&ecirc;̣t đ&ecirc;̉ và khắc phục nhanh nh&acirc;́t đ&ecirc;̉ tạo lòng tin ti&ecirc;́p tục hành hi&ecirc;̣p tr&ecirc;n con đường mang t&ecirc;n Truy M&ocirc;̣ng.</p>\n\n<p>&nbsp; &nbsp; &nbsp;BQT gởi 1 GiftCode năm mới thay lời Tri &Acirc;n đ&ecirc;́n t&acirc;́t cả Quý bằng hữu đã tham gia Game Truy M&ocirc;̣ng. Chúc Quý Bằng Hữu 1 năm mới :&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<h1><span style="color:#FAEBD7"><span style="font-size:16px"><span style="font-family:arial,tahoma,helvetica,sans-serif">T&acirc;n ni&ecirc;n sung t&uacute;c, lắm ph&uacute;c nhiều duy&ecirc;n, trong t&uacute;i nhiều tiền, t&acirc;m hồn vui sướng.</span></span></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><u><strong>Mã GftCode : &nbsp; &nbsp;&nbsp;</strong></u>&nbsp;GCNMC153B53</span></p>\n\n<p><span style="color:#FFFF00"><u><strong>Phạm Vi :</strong></u> T&acirc;́t cả quý bằng hữu tham gia game Truy M&ocirc;̣ng</span></p>\n\n<p>&nbsp;</p>\n\n<p><u><strong><span style="font-size:14px"><span style="color:#FF0000">Cách Rèn Th&acirc;̀n Binh :</span></span></strong></u><span style="font-size:14px"><span style="color:#FF0000">&nbsp;</span><span style="color:#FFFF00">hình ảnh mang tính ch&acirc;́t minh họa&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/thanbinh.png" style="height:576px; width:1155px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FF0000"><u><strong><span style="font-size:16px">Cảnh Giác Trong Năm Mới&nbsp;</span></strong></u></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp;Vừa qua BQT đã nh&acirc;̣n đc th&ocirc;ng tin có 1 s&ocirc;́ gamer th&acirc;́y sự h&ocirc;̃ trợ của GM ingame n&ecirc;n đã ko ki&ecirc;̀m đc lòng tham, mạo danh GM ingame đ&ecirc;̉ x&acirc;m &nbsp;nh&acirc;̣p vào tài khoản của 1 s&ocirc;́ quý bằng hữu ko cảnh giác nhằm chi&ecirc;́m đoạt và phá hoại tài sản của chủ sở hữu. BQT xin th&ocirc;ng báo đ&ecirc;̉ quý bằng hữu đ&ecirc;̀ phòng :</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00">&nbsp; 1/ [ Quý bằng hữu ko giao ID và Pass cho b&acirc;́t kì 1 ai mạo nh&acirc;̣n là GM và Pm thẳng qua chủ acc ]</span></p>\n\n<p><span style="color:#FFFF00">&nbsp; 2/ [ GM ko bao giờ hỏi ID của chủ acc ]</span></p>\n\n<p><span style="color:#FFFF00">&nbsp; 3/ [ Gm ingame sẽ có 1 Set thời trang, Danh Hi&ecirc;̣u và Tọa Kỵ Ảo Hóa Hi&ecirc;́m ]</span></p>\n\n<p><span style="color:#FFFF00">&nbsp; 4/ [ N&ecirc;́u Quý bằng hữu nào đã bị kẻ gian lợi dụng lòng tin đ&ecirc;̉ chi&ecirc;́m đoạt tài sản xin vui lòng báo g&acirc;́p cho Ad Face đ&ecirc;̉ xử lý ]</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><u><strong><span style="color:#FF0000">Hình ảnh của Gm ingame :</span></strong></u></span></p>\n\n<p>&nbsp;</p>\n\n<h1><span style="font-size:14px"><u><strong><span style="color:#FF0000"><img alt="" src="/static/uploads/editor/TopS1%20%2B%20S2/gmtruymong.png" style="height:498px; width:1068px" /></span></strong></u></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><span style="color:#FFFF00">Chúc quý bằng hữu năm mới vui vẻ và đ&ecirc;̀ phòng kẻ gian lợi dụng lòng tin đ&ecirc;̉ lừa đảo.&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'Thân chào quý bằng hữu', '0', 0, '0', '1970-01-01', '', '2015-01-02 09:59:26', '2015-01-02 09:17:33', '0', ''),
(149, 3, '', 0, '', '0', 'Hướng dẫn Công Thành Chiến', 'huong-dan-cong-thanh-chien', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Hướng dẫn C&ocirc;ng Th&agrave;nh Chiến<img alt="" src="http://i.imgur.com/nBxxUo9.png" style="background-color:rgb(255, 255, 255); border:medium none; color:rgb(51, 51, 51); font-family:tahoma,geneva,sans-serif; font-size:12px; line-height:1.6em; margin:0px; padding:0px" /></div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;">\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<div><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Ch&agrave;o c&aacute;c ace!!!&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/36.gif" style="border:medium none; margin:0px; padding:0px" title="36" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/36.gif" style="border:medium none; margin:0px; padding:0px" title="36" /><br />\nC&ocirc;ng th&agrave;nh chiến một hoạt động kh&ocirc;ng thể thiếu trong game, cuộc chiến lớn nhất tranh gi&agrave;nh Vương Th&agrave;nh giữa 3 nước C&ocirc;n L&ocirc;n, Thục Sơn v&agrave; Bồng Lai. H&ocirc;m nay m&igrave;nh giới thiệu tổng quan về hoạt động n&agrave;y c&oacute; thể gi&uacute;p c&aacute;c bạn c&oacute; được sự chuẩn bị tốt cho c&ocirc;ng cuộc b&aacute; chủ thi&ecirc;n địa nh&oacute;e&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/100.gif" style="border:medium none; margin:0px; padding:0px" title="100" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/100.gif" style="border:medium none; margin:0px; padding:0px" title="100" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/100.gif" style="border:medium none; margin:0px; padding:0px" title="100" /><br />\n<br />\nHoạt động c&ocirc;ng th&agrave;nh chiến diễn ra v&agrave;o thứ 4 v&agrave; Chủ Nhật hằng tuần, thời gian bắt đầu 20:40 v&agrave; kết th&uacute;c l&uacute;c 21:00 khi sự kiện bắt đầu bạn sẽ nhận được v&eacute; mời tham gia hoạt động</span></span></div>\n<br />\n&nbsp;</div>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">C&ocirc;ng th&agrave;nh sẽ gồm c&oacute; 2 nước c&ocirc;ng v&agrave; 1 nước thủ. Chi tiết ở dưới nh&eacute;!!!!<br />\nKhi v&agrave;o chiến trường 2 nước c&ocirc;ng sẽ c&oacute; 1 trụ đ&egrave;n biến h&igrave;nh</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/7jbXuGs.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">tại đ&acirc;y bạn sẽ lựa chọn biến th&acirc;n th&agrave;nh tứ tượng để c&ocirc;ng ph&aacute; cổng th&agrave;nh</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/gTOrqt6.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Mộc Gi&aacute;p Huyền Quy c&oacute; thể n&oacute;i tanker trong 4 tượng m&aacute;u cao ph&ograve;ng thủ t&ocirc;t v&agrave; n&oacute; khắc chế được Mộc Gi&aacute;p Văn Hồ, khi biến th&acirc;n bạn sẽ nhận được bộ kĩ năng ri&ecirc;ng của n&oacute; gồm</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">​<img alt="" src="http://i.imgur.com/FHs6xa7.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px">​</span></p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/9PBHqLV.png" style="border:medium none; margin:0px; padding:0px" /><img alt="" src="http://i.imgur.com/laBD5by.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Tiếp theo Mộc Gi&aacute;p Long</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/toIH7nv.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">L&agrave; tượng s&aacute;t thương theo tỉ lệ HP v&agrave; c&oacute; khả năng khắc chế được Mộc Gi&aacute;p Huyền Quy, bộ kĩ năng của n&oacute; cũng kh&aacute; mạnh</span></span></p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/CeoBUcx.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">&nbsp;</span></span></p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/NbGmMkq.png" style="border:medium none; margin:0px; padding:0px" /><img alt="" src="http://i.imgur.com/z41kfgt.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Kế đến l&agrave; Mộc Gi&aacute;p V&acirc;n Hồ</span></span><br />\n<br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/L9OT7MK.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">C&oacute; khả năng c&ocirc;ng k&iacute;ch cao, cơ động c&oacute; thể khắc chế Mộc Gi&aacute;p Lang Long, con n&agrave;y dồn dame đ&aacute;nh c&ocirc;ng th&agrave;nh rất tốt, kĩ năng của n&oacute; kh&aacute; cơ động v&agrave; kh&aacute; giống kĩ năng của ph&aacute;i Th&iacute;ch Kh&aacute;ch</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/zDEmAKy.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/p6CJ6Vy.png" style="border:medium none; margin:0px; padding:0px" /><img alt="" src="http://i.imgur.com/l8drLd7.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Cuối c&ugrave;ng trong tứ tượng l&agrave; Ph&aacute; Th&agrave;nh Cự Th&uacute;</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/PjbqgG2.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Đ&acirc;y l&agrave; qu&aacute;i vật c&ocirc;ng th&agrave;nh ch&iacute;nh hiệu vs bộ kĩ nằng chuy&ecirc;n dụng d&agrave;nh để c&ocirc;ng th&agrave;nh</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/M5GeIq5.png" style="border:medium none; margin:0px; padding:0px" /></span><br />\n&nbsp;</div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/Qh6iL5i.png" style="border:medium none; margin:0px; padding:0px" /><img alt="" src="http://i.imgur.com/W360EVg.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">C&aacute;c bạn c&oacute; thể t&ugrave;y theo đối phương thế n&agrave;o m&agrave; lựa chọn biến th&acirc;n cho hợp l&iacute;.<br />\nSau khi biến th&acirc;n bạn bắt đầu c&ocirc;ng th&agrave;nh mục ti&ecirc;u ch&iacute;nh l&agrave; cổng th&agrave;nh, v&igrave; c&oacute; 2 nước c&ocirc;ng n&ecirc;n sẽ t&iacute;nh lượng c&ocirc;ng k&iacute;ch theo % cho mỗi phe, c&ocirc;ng th&agrave;nh th&igrave; chưa quan trọng n&oacute; chỉ l&agrave; bước đầu ti&ecirc;n để v&agrave;o c&ocirc;ng ph&aacute; b&ecirc;n trong n&ecirc;n c&aacute;c bạn cố gắng đ&aacute;nh nhanh để v&agrave;o trong.</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/BE8XseX.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Sau khi đ&atilde; c&ocirc;ng ph&aacute; th&agrave;nh c&ocirc;ng cổng th&agrave;nh, mọi trạng th&aacute;i biến th&acirc;n th&agrave;nh tứ tượng sẽ biến mất khi đ&oacute; bạn sẽ tấn c&ocirc;ng bằng kĩ năng của nh&acirc;n vật. L&uacute;c n&agrave;y 3 phe sẽ s&aacute;p l&aacute; c&agrave; đ&aacute;nh nhau gồm 2 b&ecirc;n c&ocirc;ng v&agrave; 1 b&ecirc;n thủ<br />\n<br />\nB&ecirc;n c&ocirc;ng muốn chiếm được th&agrave;nh phải ph&aacute; cho được Long Trụ v&agrave; b&ecirc;n thủ c&oacute; nhiệm vụ phải bảo vệ cho bằng được Long Trụ đ&oacute;.</span></span><br />\n&nbsp;</p>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/UuEAOEO.png" style="border:medium none; margin:0px; padding:0px" /></span></div>\n\n<p style="text-align:justify"><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">B&ecirc;n c&ocirc;ng sẽ c&oacute; 2 nước n&ecirc;n tấn c&ocirc;ng Long trụ b&ecirc;n n&agrave;o dame nhiều phần trăm hơn sẽ d&agrave;nh được chiến thắng. Trong l&uacute;c c&ocirc;ng th&agrave;nh chiến 3 phe sẽ ở chế độ pk Trận n&ecirc;n sẽ đ&aacute;nh nhau ch&iacute; ch&oacute;e, sẽ kh&ocirc;ng c&oacute; việc li&ecirc;n minh giữa 2 nước c&ocirc;ng.<br />\n<br />\nTheo kinh nghiệm bản th&acirc;n th&igrave;, b&ecirc;n thủ nếu lực lượng đ&ocirc;ng th&igrave; tiến h&agrave;nh chặn đ&aacute;nh ngay từ cổng th&agrave;nh như thế sẽ k&eacute;o d&agrave;i được thời gian quan địch tiến v&agrave;o Long Trụ, chiến thuật tốt th&igrave; d&ugrave;ng Kiếm Linh v&agrave; Th&iacute;ch Kh&aacute;ch d&ugrave;ng tr&oacute;i v&agrave; k&eacute;o để kh&oacute;a top b&ecirc;n kia, Ph&aacute;p T&ocirc;n v&agrave; B&uacute;t Tiến đứng xa rỉa m&aacute;u l&agrave; tốt nhất.<br />\n<br />\nC&ograve;n b&ecirc;n c&ocirc;ng th&igrave; Th&iacute;ch Kh&aacute;ch vs Kiếm Linh phải c&oacute; kĩ năng khinh c&ocirc;ng cấp 2 trở l&ecirc;n để c&oacute; thể nhảy 2 lần v&ograve;ng ra ph&iacute;a sau lưng tấn c&ocirc;ng Ph&aacute;p T&ocirc;n v&agrave; B&uacute;t Ti&ecirc;n hạn ti&ecirc;u hao sinh lực cho b&ecirc;n m&igrave;nh. Tốt nhất b&ecirc;n c&ocirc;ng n&ecirc;n li&ecirc;n minh với nhau chia nhau ra chiếm th&agrave;nh v&igrave; 1 tuần c&oacute; 2 lần chiếm chia ra th&igrave; sẽ c&oacute; lợi hơn, nếu được th&igrave; n&ecirc;n c&oacute; chat voice để đưa ra chiến thuật hợp l&iacute; v&agrave; nhanh lẹ.<br />\n<br />\nBạn pk v&agrave; đ&aacute;nh cổng th&agrave;nh, Long trụ c&agrave;ng nhiều th&igrave; danh vọng c&agrave;ng cao chiến c&ocirc;ng c&agrave;ng cao kinh nghiệm v&agrave; đồng nhận được cần nhiều.</span></span></p>\n\n<div style="margin: 0px; padding: 0px;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/Oki9csq.png" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /><br />\n<img alt="" src="http://i.imgur.com/O95JCgY.png" style="border:medium none; display:block; margin:0px auto; padding:0px; width:650px" /></span></div>\n\n<p style="text-align:justify"><br />\n<br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium">Cảm ơn c&aacute;c bạn đ&atilde; đọc b&agrave;i viết nếu thấy hay th&igrave; bấm n&uacute;t thanks nha!!!!!</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:20:08', '2015-02-25 16:18:55', '0', ''),
(75, 1, '', 0, '', '0', 'Truy Tìm Mảnh Luyện Ngục Thần Bí [ Đợt 1 ]', 'truy-ti-m-ma-nh-luye-n-ngu-c-tha-n-bi-do-t-1-', '<p>Th&acirc;n chào quý bằng hữu.</p>\n\n<p>&nbsp; &nbsp; &nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp;Trong th&ecirc;́ giới truy m&ocirc;̣ng đã từng th&acirc;́t lạc những món thời trang cực kỳ hi&ecirc;́m và những đại hi&ecirc;̣p mu&ocirc;́n có đc những món thời trang &acirc;́y đã từng ...</p>\n\n<p>&nbsp; &nbsp; &nbsp;Li&ecirc;̣u những mãnh vỡ &acirc;́y có gì bí &acirc;̉n và những lời đ&ocirc;̀n đại những đại hi&ecirc;̣p nào gom đủ những mãnh vỡ &acirc;́y có tạo n&ecirc;n kỳ tích gì hay kh&ocirc;ng.</p>\n\n<p>&nbsp; &nbsp; &nbsp;BQT sẽ chờ và vinh danh những đại hi&ecirc;̣p tranh đoạt đc h&ecirc;́t những mãnh vỡ này .</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/eventthoitrang%5D/vitriGMTruyMong.png" style="height:328px; width:683px" /></h1>\n\n<p>Hình ảnh Set Thời Trang Luy&ecirc;̣n Ngục</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:#0000FF">N&ocirc;̣i Dung :</span></strong> Trong thời gian di&ecirc;̃n ra sự ki&ecirc;̣n quý bằng hữu nạp và tích lũy KNB đ&ecirc;̉ nh&acirc;̣n những ph&acirc;̀n quà tương ứng [ kh&ocirc;ng tính x100% thẻ ]</p>\n\n<p><strong><span style="color:#0000FF">Phạm Vi :</span></strong> T&acirc;́t cả máy chủ Truy M&ocirc;̣ng&nbsp;</p>\n\n<p><strong><span style="color:#0000FF">Thời gian di&ecirc;̃n ra sự ki&ecirc;̣n :&nbsp;&nbsp; </span></strong>10h ngày 9/1/2015 đ&ecirc;́n 10h ngày 14/1/2015</p>\n\n<p>&nbsp;</p>\n\n<h1><span style="font-size:18px"><span style="color:#0000FF">Ph&acirc;̀n quà tích lũy :&nbsp;</span></span></h1>\n\n<p>&nbsp;</p>\n\n<h1><span style="font-size:18px"><span style="color:#0000FF"><img alt="" src="/static/uploads/editor/eventthoitrang%5D/Thoitrang1.png" style="height:741px; width:458px" /></span></span></h1>\n\n<h1><span style="font-size:18px"><span style="color:#0000FF"><img alt="" src="/static/uploads/editor/eventthoitrang%5D/thoitrang2.png" style="height:649px; width:458px" /></span></span></h1>\n\n<h1><span style="font-size:18px"><span style="color:#0000FF"><img alt="" src="/static/uploads/editor/eventthoitrang%5D/thoitrang3.png" style="height:649px; width:458px" /></span></span></h1>\n\n<h1><span style="font-size:18px"><span style="color:#0000FF"><img alt="" src="/static/uploads/editor/eventthoitrang%5D/thoitrang4.png" style="height:325px; width:458px" /></span></span></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:18px"><span style="color:#0000FF">Chúc quý bằng hữu Truy M&ocirc;̣ng Tham Gia Event Vui Vẻ.</span></span></p>\n', 'Thân chào quý bằng hữu', '0', 0, '0', '1970-01-01', '', '2015-01-09 09:55:51', '2015-01-09 09:55:51', '0', ''),
(151, 3, '', 0, '', '0', 'Giới Thiệu Hoạt Động : Tầm Bảo Tiên Minh', 'gioi-thieu-hoat-dong-tam-bao-tien-minh', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Giới Thiệu Hoạt Động : Tầm Bảo Ti&ecirc;n Minh</div>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;<img alt="" src="/static/uploads/editor/bikiephuongdan/3.png" style="height:342px; width:523px" /></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:40px">L</span>&agrave; một hoạt động diễn ra mỗi ng&agrave;y nhưng Tầm bảo Ti&ecirc;n Minh&nbsp;kh&ocirc;ng hề mất đi độ &ldquo;n&oacute;ng&rdquo; của n&oacute; với gameplay độc đ&aacute;o v&agrave; đặc biệt l&agrave; c&ocirc;ng bằng với tất cả người chơi để đ&uacute;ng chất&rdquo;vui nhộn&rdquo; của n&oacute;, d&ugrave; l&agrave; cao thủ hay tập sự đều c&ugrave;ng chỉ c&oacute; 1 vạch xuất ph&aacute;t như nhau.</span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>C&aacute;ch chơi:</strong></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Người chơi ti&ecirc;u diệt c&aacute;c h&ograve;m rương tr&ecirc;n bản đồ, c&oacute; 3 loại Rương tương ứng với độ hiếm kh&aacute;c nhau. Thu thập Rương c&agrave;ng nhiều c&oacute; thể t&iacute;ch đổi qu&agrave; c&agrave;ng lớn</span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Mỗi người chơi sẽ c&oacute; 4 kỹ năng như nhau bao gồm :<strong>&nbsp;Đ&aacute;nh thường</strong>&nbsp;mất 100 HP,&nbsp;<strong>Đ&aacute;nh mạnh</strong>&nbsp;mất 1000 HP (thời gian chờ l&acirc;u),&nbsp;<strong>G&acirc;y cho&aacute;ng</strong>&nbsp;(đối phương mất điều khiển) v&agrave;&nbsp;<strong>Khinh C&ocirc;ng</strong>&nbsp;(nhảy tới v&agrave; g&acirc;y 100 damage l&ecirc;n khu vực xung quanh)</span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Bằng việc sử dụng tinh tế 4 kỹ năng n&agrave;y, người chơi c&oacute; thể kiếm được thật nhiều rương, b&iacute; quyết ở đ&acirc;y ch&iacute;nh l&agrave; khả năng&nbsp;<strong>last hit&nbsp;</strong>&nbsp;v&igrave; rương sẽ thuộc về người ra c&uacute; đ&aacute;nh kết liễu cuối c&ugrave;ng.</span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/tambao1.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Để t&igrave;m c&aacute;c Rương bảo vật qu&yacute;, người chơi c&oacute; thể ấn chữ M để xem bản đồ, c&aacute;c Rương bảo vật qu&yacute; c&oacute; k&iacute; hiệu m&agrave;u đỏ.</span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/tambao3.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Đặc biệt, sau khi ti&ecirc;u diệt hết tất cả c&aacute;c Rương qu&yacute; tr&ecirc;n bản đồ,&nbsp;<strong>4 Thần Long</strong>&nbsp;sẽ xuất hiện ở&nbsp; 4 phương. Ti&ecirc;u diệt Thần Long sẽ nhận được rất nhiều phần thưởng qu&yacute;.</span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/tambao2.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Bạn cũng c&oacute; thể ấn&nbsp;<strong>M</strong>&nbsp;để xem địa điểm c&aacute;c Thần Long khi n&oacute; xuất hiện</span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/tambao4.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Sau khi Thần Long bị ti&ecirc;u diệt, người last hit cuối c&ugrave;ng sẽ nhận được&nbsp;<strong>rương Thần Long</strong>. Ngo&agrave;i ra, mỗi con sẽ nhả ra&nbsp;<strong>2 Rương Thần Long</strong>&nbsp;ở khắp bản đồ. T&igrave;m v&agrave; ti&ecirc;u diệt c&aacute;c Rương n&agrave;y để c&oacute; cơ hội lấy rương qu&yacute;.</span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Sau khi Thần Lon chết, c&aacute;c rương qu&yacute; sẽ hồi phục lại v&agrave; Thần Long sẽ tiếp tục t&aacute;i sinh nếu ti&ecirc;u diệt c&aacute;c Rương.</span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>Phần Thưởng:</strong></span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Phần thưởng khi quy đổi sẽ được c&aacute;c mảnh Thời Trang. C&aacute;c bạn c&oacute; thể d&ugrave;ng n&oacute; để đổi lấy c&aacute;c Thời Trang cực độc v&agrave; qu&yacute; hiếm, vừa l&agrave;m đep cho bản th&acirc;n, vừa tăng th&ecirc;m lực chiến</span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">Mau đến với Tầm Bảo Ti&ecirc;n Minh&nbsp;để c&ugrave;ng vui, c&ugrave;ng chơi v&agrave; c&ugrave;ng nhận qu&agrave; nh&eacute; !!!</span></p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:51:15', '2015-02-25 16:28:07', '0', ''),
(152, 3, '', 0, '', '0', 'Giới thiệu Hoạt Động : Vận Tiêu', 'gioi-thieu-hoat-dong-van-tieu', '<p style="text-align:center"><span style="font-size:24pt"><strong>Vận ti&ecirc;u&nbsp;</strong></span></p>\n\n<p style="text-align:justify"><strong><span style="font-size:40px">V</span>ận Ti&ecirc;u</strong>&nbsp;hay c&ograve;n gọi l&agrave; Mỹ Nữ Đồng Du l&agrave; một n&eacute;t hoạt động đặc sắc của d&ograve;ng game nhập vai. Trong B&aacute; Thi&ecirc;n Địa, người chơi c&oacute; thể tham gia hoạt động n&agrave;y bằng c&aacute;ch chọn mục Hoạt Động, chọn tới mục&nbsp;<strong>Mỹ Nữ Đồng Du</strong>hoặc tới thẳng chỗ của&nbsp;<strong>Huề Mỹ Đồng Du</strong></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/bikiephuongdan/2.png" style="height:352px; width:513px" /></p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu2.png" style="border:medium none; margin:0px; padding:0px; width:500px" /></p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu3.png" style="border:medium none; margin:0px; padding:0px; width:500px" /></p>\n\n<p>- Người chơi mặc định sẽ chọn hộ tống Mỹ nữ đầu ti&ecirc;n, c&oacute; thể d&ugrave;ng Hộp Son hoặc Đồng để L&agrave;m Mới lại danh s&aacute;ch, gi&uacute;p c&oacute; quyền hộ tống Mỹ nữ cao hơn (số tiền v&agrave; EXP kiếm được nhiều hơn)</p>\n\n<p>- Thời gian để x2 phần thưởng Vận Ti&ecirc;u mỗi ng&agrave;y l&agrave; từ 20:05 đến 20:35</p>\n\n<p>- Trong qu&aacute; tr&igrave;nh Vận ti&ecirc;u nếu bị cướp ti&ecirc;u hoặc qu&aacute; giờ hộ tống th&igrave; sẽ bị mất 1/2 phần thưởng.</p>\n\n<p>Để thực hiện qu&aacute; tr&igrave;nh Vận Ti&ecirc;u, sau khi nhận nhiệm vụ c&aacute;c bạn phải băng qua Thanh Phong Giản để tới Bồng Lai</p>\n\n<p><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu4.png" style="border:medium none; display:block; margin:0px auto; padding:0px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p>Sau khi tới đ&oacute;, t&igrave;m v&agrave; n&oacute;i chuyện với&nbsp;<strong>Bồng Lai Sứ Giả</strong></p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu5.png" style="border:medium none; margin:0px; padding:0px; width:500px" /></p>\n\n<p>Sau đ&oacute;, bạn phải điều khiển quay trở lại chỗ&nbsp;<strong>Huề Mỹ Đồng Du</strong>, nếu bạn kh&ocirc;ng nhớ vị tr&iacute; th&igrave; đ&acirc;y l&agrave; tọa độ:</p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu6.png" style="border:medium none; margin:0px; padding:0px; width:500px" /></p>\n\n<p>Sau khi ho&agrave;n tất, bạn sẽ được r&uacute;t thăm lấy c&aacute;c phần thưởng</p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu7.png" style="border:medium none; margin:0px; padding:0px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:24pt"><strong>Cướp Ti&ecirc;u</strong></span></p>\n\n<p>Để Cướp Ti&ecirc;u, c&aacute;c bạn chỉ cần bật chế độ PK ở g&oacute;c tr&aacute;i m&agrave;n h&igrave;nh v&agrave; đứng ở Thanh Phong Giản:</p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/vantieu8.png" style="border:medium none; margin:0px; padding:0px" /></p>\n\n<p>Tấn c&ocirc;ng người chơi kh&aacute;c đang hộ tống v&agrave; nếu chiến thắng bạn sẽ cướp ti&ecirc;u th&agrave;nh c&ocirc;ng. Một số lưu &yacute; khi cướp ti&ecirc;u:</p>\n\n<p>+ Cướp ti&ecirc;u người chơi kh&ocirc;ng thua cấp độ bản th&acirc;n qu&aacute; 5 cấp th&igrave; mới được nhận thưởng. VD: Cấp 55 chỉ c&oacute; thể cướp ti&ecirc;u những ai kh&ocirc;ng dưới cấp 50 mới c&oacute; thưởng</p>\n\n<p>+ Cướp ti&ecirc;u người chơi c&ugrave;ng Li&ecirc;n Minh sẽ kh&ocirc;ng được nhận thưởng</p>\n\n<p>+ Mỗi ng&agrave;y chỉ nhận tối đa 3 lần thưởng Cướp Ti&ecirc;u</p>\n\n<p>+ Người vận ti&ecirc;u sẽ được cộng 19.000 HP khi Vận ti&ecirc;u.&nbsp;</p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:50:00', '2015-02-25 16:30:20', '0', '');
INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(153, 3, '', 0, '', '0', 'Kết Hôn', 'ket-hon', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Sầu Một Chữ T&igrave;nh</div>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>&nbsp;</strong></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>&nbsp;</strong></span></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/bikiephuongdan/1.png" style="height:257px; width:604px" /></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong>&nbsp;</strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Hỏi thế gian t&igrave;nh l&agrave; g&igrave;?</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>T&igrave;nh khiến sinh tử tương hứa.</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Trời nam đất bắc song phi nhạn.</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Cổ thụ mấy m&ugrave;a h&agrave;ng sơn. Hoan lạc th&uacute;, biệt ly sầu.</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Nỗi khổ chứa đầy t&igrave;nh tương tư.</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Ng&agrave;n lời muốn tỏ&hellip;</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Đều tr&ocirc;i theo &aacute;ng m&acirc;y.</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Thi&ecirc;n sơn kh&ocirc;ng băng gi&aacute;&hellip;</em></strong></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><em>Qu&acirc;n tử đi c&ugrave;ng ai</em></strong></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>K&iacute;nh ch&agrave;o c&aacute;c Long H&agrave;nh Giả!</strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">Kh&ocirc;ng c&oacute; g&igrave; nặng như chữ T&igrave;nh, cũng kh&ocirc;ng c&oacute; g&igrave; nhẹ hơn n&oacute;. D&ugrave; l&agrave; ai cũng vậy, từ những anh h&ugrave;ng kh&eacute;t tiếng chốn v&otilde; l&acirc;m cho đến những &aacute;c b&aacute; giết người kh&ocirc;ng gớm tay, tất cả đều kh&ocirc;ng tho&aacute;t khỏi lưới &ldquo;T&igrave;nh&rdquo;.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">&ldquo;T&igrave;nh&rdquo; ở&nbsp;</span><span style="font-size:10pt">đ&acirc;y xin được đề cập tới ch&iacute;nh l&agrave; t&igrave;nh y&ecirc;u đ&ocirc;i lứa, thứ t&igrave;nh cảm đặc biệt kết nối hai con người xa lạ, kh&ocirc;ng chung d&ograve;ng m&aacute;u hay gia tộc. Ch&uacute;ng ta c&oacute; thể bắt gặp rất nhiều c&acirc;u chuyện t&igrave;nh đấm nướ</span><span style="font-size:10pt">c mắt hay v&ocirc; c&ugrave;ng l&atilde;ng mạn xen lẫn trong những tiểu thuyết V&otilde; hiệp hay Ti&ecirc;n Hiệp, n&oacute; thổi một sức sống mới v&agrave;o c&acirc;u chuyện, v&agrave; để lại một ấn tượng s&acirc;u sắc hơn rất nhiều lần.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">B&ecirc;n cạnh cuộc chiến t&agrave; ph&aacute;i v&agrave; ch&iacute;nh ph&aacute;i, vẫn nổi bật l&ecirc;n đ&oacute; mối t&igrave;nh tay ba oan nghiệt của Trương V&ocirc; Kỵ - Triệu Mẫn &ndash; Chu Chi Nhược. Hay trong sự tranh gi&agrave;nh quyền lực của v&otilde; l&acirc;m trung nguy&ecirc;n, vẫn kh&ocirc;ng thể l&agrave;m lu mờ chữ T&igrave;nh của Ho&agrave;ng Dung v&agrave; Qu&aacute;ch Tĩnh. Thiếu mất chữ &ldquo;T&igrave;nh&rdquo;, giống như một bức tranh thiếu đi m&agrave;u sắc, nhạt nh&ograve;a v&agrave; đơn điệu.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">Vậy c&ograve;n chữ &ldquo;T&igrave;nh&rdquo; trong Truy Mộng? Nơi m&agrave; người chơi h&oacute;a th&acirc;n th&agrave;nh những nh&acirc;n vật trong thế giới ti&ecirc;n hiệp, &ldquo;h&oacute;a th&acirc;n&rdquo; &ndash; chứ kh&ocirc;ng phải l&agrave; điều khiển. Con người c&oacute; &ldquo;T&igrave;nh&rdquo;, v&agrave; nh&acirc;n vật h&oacute;a th&acirc;n cũng vậy, ch&uacute;ng ta c&oacute; thể bắt gặp một người chơi kh&aacute;c, c&ugrave;ng tr&ograve; chuyện, c&ugrave;ng chiến đấu, c&ugrave;ng t&acirc;m sự v&agrave; rồi c&ugrave;ng một nhịp đập. Thế giới c&oacute; thể l&agrave; ảo, nhưng t&igrave;nh cảm vẫn xuất ph&aacute;t từ ch&iacute;nh tr&aacute;i tim mỗi con người.</span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt"><img alt="" src="http://bathiendia.com/static/uploads/1.jpg" style="border:medium none; margin:0px; padding:0px; width:600px" /></span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt"><em><strong>Tặng hoa v&agrave; tận hưởng hiệu ứng tuyệt đẹp</strong></em></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">Truy Mộng&nbsp;&nbsp;đ&atilde; kh&eacute;o l&eacute;o thể hiện phần n&agrave;o đ&oacute; chữ &ldquo;T&igrave;nh&rdquo; n&agrave;y th&ocirc;ng qua t&iacute;nh năng Kết H&ocirc;n. Một c&aacute;ch thực tế người chơi sẽ phải trải qua khoảng thời gian cưa cẩm, tặng qu&agrave;, hẹn h&ograve; rồi cuối c&ugrave;ng l&agrave; một đ&aacute;m cưới vi&ecirc;n m&atilde;n y như cuộc sống thật th&ocirc;ng qua hệ thống Điểm Th&acirc;n Mật. Đ&acirc;y l&agrave; một đại lượng đo lường mức độ th&acirc;n thiết của người chơi, khi đạt tới một ngưỡng tương ứng, người chơi sẽ nhận được những danh hiệu th&acirc;n mật kh&aacute;c nhau. Để tăng điểm n&agrave;y th&igrave; người chơi c&oacute; thể c&ugrave;ng song tu, c&ugrave;ng party, l&agrave;m nhiệm vụ Ti&ecirc;n Lữ T&igrave;nh Duy&ecirc;n hay nhanh ch&oacute;ng nhất l&agrave; tặng Hoa. Đối với người bạn kh&aacute;c giới, khi đạt mức th&acirc;n mật cao nhất sẽ c&oacute; thể tới chỗ Hồng Nương để tổ chức h&ocirc;n lễ.</span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt"><img alt="" src="http://bathiendia.com/static/uploads/2.jpg" style="border:medium none; margin:0px; padding:0px; width:600px" /></span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt"><em><strong>Đ&aacute;m cưới được cử h&agrave;nh đ&uacute;ng nghi thức</strong></em></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">H&ocirc;n lễ cũng được cử h&agrave;nh đ&uacute;ng như nghi thức cổ xưa với đ&egrave;n, kiệu, k&egrave;n, trống. Đặc biệt hơn cả l&agrave; c&oacute; thể gửi thiệp mời c&aacute;c hảo hữu tới tham dự v&agrave; chung vui, việc tham dự n&agrave;y mang lại một lượng EXP kh&aacute; lớn, thật đ&uacute;ng l&agrave; &ldquo;song hỷ l&acirc;m m&ocirc;n&rdquo;.Sau khi đ&aacute;m cưới, c&aacute;c cặp đ&ocirc;i c&oacute; thể được th&ecirc;m nhiều buff khi chiến đấu cạnh nhau v&agrave; c&ograve;n rất điều hấp dẫn kh&aacute;c m&agrave; người chơi c&oacute; thể kh&aacute;m ph&aacute;.</span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt"><img alt="" src="http://bathiendia.com/static/uploads/3.jpg" style="border:medium none; margin:0px; padding:0px; width:600px" /></span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt"><em><strong>Lễ động ph&ograve;ng th&igrave; phải ?</strong></em></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">Hệ thống Điểm th&acirc;n mật tạo n&ecirc;n một thế giới đầy t&iacute;nh tương t&aacute;c m&agrave; mỗi con người trong đ&oacute; đều &iacute;t nhiều c&oacute; những sợi d&acirc;y li&ecirc;n hệ với nhau, đ&oacute; cũng l&agrave; c&aacute;i &ldquo;chất&rdquo; thật sự của Game Online. Chắc chắn rằng sẽ c&oacute; rất nhiều c&acirc;u chuyện, rất nhiều mối t&igrave;nh được đơm hoa trong B&aacute; Thi&ecirc;n Địa. C&ograve;n bạn, bạn c&oacute; thể tho&aacute;t khỏi lưới T&igrave;nh n&agrave;y kh&ocirc;ng ?</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>Nặng t&igrave;nh chấm b&uacute;t.</strong></span></span></p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:48:48', '2015-02-25 16:33:06', '0', ''),
(82, 1, '', 0, '', '0', 'Event Truy Tìm Mảnh Thời Trang Thần Bí [ Đợt 2 ]', 'event-truy-tim-manh-thoi-trang-than-bi-dot-2-', '<p>Th&acirc;n Ch&agrave;o Qu&yacute; Bằng Hữu.</p>\n\n<p>&nbsp;</p>\n\n<p>Sau Khi kết th&uacute;c Event &quot; Truy T&igrave;m Mảnh Thần Binh [ Đợt 1 ] &quot; đ&atilde; nhận được nhiều sự hưởng ứng của qu&yacute; bằng hữu trong thời gian vừa qua, nay c&ograve;n 1 m&oacute;n thời trang cuối c&ugrave;ng chuẩn bị đổ bộ v&agrave;o thế giới Truy Mộng kh&ocirc;ng ri&ecirc;ng g&igrave; ai những đại hiệp n&agrave;o sẽ chứng tỏ năng lực kiệt xuất để chiếm hữu đc m&oacute;n thời trang hiếm cuối c&ugrave;ng n&agrave;y.</p>\n\n<p>Liệu c&oacute; dịch chuyển đc c&agrave;n kh&ocirc;ng - xưng b&aacute; c&otilde;i Truy Mộng hay kh&ocirc;ng c&ograve;n t&ugrave;y thuộc v&agrave;o &quot; qu&yacute; bằng hữu &quot;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>N&ocirc;̣i Dung :</strong></span>&nbsp;Trong thời gian di&ecirc;̃n ra sự ki&ecirc;̣n quý bằng hữu nạp và tích lũy KNB đ&ecirc;̉ nh&acirc;̣n những ph&acirc;̀n quà tương ứng [ kh&ocirc;ng tính x100% thẻ ]</p>\n\n<p><span style="color:#FFFF00"><strong>Phạm Vi :</strong></span>&nbsp;T&acirc;́t cả máy chủ Truy Mộng</p>\n\n<p><strong><span style="color:#FFFF00">Thời gian di&ecirc;̃n ra sự ki&ecirc;̣n :&nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">&nbsp;</span></strong>10h00 ngày 15/1/2015 đ&ecirc;́n 10h00 ngày 18/1/2015</p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align: center;"><span style="color:#FFFF00"><span style="font-size:18px">Ph&acirc;̀n quà tích lũy :&nbsp;</span></span></h1>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/S7/1.png" style="height:656px; width:435px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/S7/2.png" style="height:532px; width:435px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/S7/3.png" style="height:584px; width:435px" /></h1>\n\n<p>&nbsp;</p>\n\n<p>Ch&uacute;c qu&yacute; bằng hữu tham gia Event vui vẻ.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'Thân Chào Quý Bằng Hữu', '0', 0, '0', '1970-01-01', '', '2015-01-14 19:34:14', '2015-01-14 19:32:57', '0', ''),
(157, 3, '', 0, '', '0', 'Thời Trang', 'thoi-trang', '<p style="text-align:justify">K&iacute;nh gửi c&aacute;c bằng hữu</p>\n\n<p style="text-align: center;"><img alt="" src="/static/uploads/editor/bikiephuongdan/2015-01-09_114021.png" style="height:334px; width:687px" /></p>\n\n<p style="text-align:justify">Thời Trang l&agrave; một m&oacute;n ăn tinh thần kh&ocirc;ng thể thiếu từ cuộc sống cho tới cả thế giới Truy Mộng. Việc chinh phục v&agrave; sưu tầm c&aacute;c loại y phục rực rỡ từ l&acirc;u đ&atilde; l&agrave; th&uacute; chơi của những đại cao thủ. Nhưng ngo&agrave;i việc l&agrave;m đẹp, th&igrave; việc sở hữu c&agrave;ng nhiều y phục, lượng sức mạnh cộng th&ecirc;m từ n&oacute; cũng được cộng dồn v&agrave;o lực chiến của nh&acirc;n vật. Vậy h&ocirc;m nay ch&uacute;ng ta h&atilde;y c&ugrave;ng điểm lại to&agrave;n bộ c&aacute;c Thời Trang đ&atilde; ra mắt để c&oacute; c&aacute;i nh&igrave;n tổng qu&aacute;t về ch&uacute;ng nh&eacute;.</p>\n\n<p style="text-align:justify"><span style="color:rgb(0, 0, 255)"><span style="font-size:24px"><strong>1. Bộ Thời Trang Cổ Mộ Lệ Ảnh</strong></span></span></p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang1.png" style="border:medium none; margin:0px; padding:0px" /></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p>Lấy theo h&igrave;nh tượng Tomb Rider rất nổi tiếng trong giới phim ảnh, những kẻ cướp lăng mộ đ&atilde; chinh phục cả thế giới. Đ&acirc;y cũng l&agrave; bộ trang phục duy nhất mang hơi hướng hiện đại t&iacute;nh đến thời điểm hiện tai.&nbsp;</p>\n\n<p>Cổ Mộ Lệ Ảnh hiện tại c&oacute; tới 3 c&aacute;ch để lấy:</p>\n\n<p><strong>C&aacute;ch 1 :</strong>&nbsp;Mua trong Thương Th&agrave;nh&nbsp;</p>\n\n<p><strong>C&aacute;ch 2:</strong>&nbsp;Đổi Mảnh Thời Trang</p>\n\n<p><strong>C&aacute;ch 3:</strong>&nbsp;Lấy từ sự kiện</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:justify"><span style="color:rgb(0, 0, 255); font-size:18pt"><strong>2. Bộ Thời Trang Linh Sơn C&agrave; Sa</strong></span></p>\n\n<p style="text-align:center"><strong><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang2.png" style="border:medium none; margin:0px; padding:0px" /></strong></p>\n\n<p><br />\nĐ&uacute;ng như t&ecirc;n gọi, đ&acirc;y l&agrave; bộ Thời Trang d&agrave;nh cho những t&iacute;n đồ Phật Ph&aacute;p, d&ugrave; trần tục nhưng vẫn to&aacute;t l&ecirc;n một vẻ cao qu&yacute; lạ thường.</p>\n\n<p>Linh Sơn C&agrave; Sa c&oacute; 2 c&aacute;ch lấy:</p>\n\n<p style="text-align:justify"><strong>C&aacute;ch 1 :</strong>&nbsp;Mua trong Thương Th&agrave;nh&nbsp;</p>\n\n<p style="text-align:justify"><strong>C&aacute;ch 2:</strong>&nbsp;Đổi Mảnh Thời Trang</p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify"><span style="color:rgb(0, 0, 255); font-size:18pt"><strong>3. Bộ Thời Trang Tru Ti&ecirc;n</strong></span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang4.png" style="border:medium none; margin:0px; padding:0px" /></p>\n\n<p>Tru Ti&ecirc;n l&agrave; bộ trang phục d&agrave;nh cho c&aacute;c Ma Chiến Thần, những chiến binh phải h&agrave;ng ng&agrave;y phải tiếp x&uacute;c với &acirc;m giới u linh. Đ&ocirc;i c&aacute;nh m&agrave;u đen c&ugrave;ng d&aacute;ng vẻ huyền b&iacute; đ&atilde; tạo n&ecirc;n một sức h&uacute;t m&ecirc; hoặc mọi cao thủ.</p>\n\n<p>Bộ Tru Ti&ecirc;n chỉ c&oacute; 1 c&aacute;ch lấy duy nhất l&agrave; mua tại&nbsp;<strong>Thương Th&agrave;nh</strong>, tuy nhi&ecirc;n bạn c&ograve;n c&oacute; thể mua lẻ từng m&oacute;n trong bộ tại đ&acirc;y.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(0, 0, 255); font-size:18pt"><strong>4. Bộ Qu&aacute;i Vật Liệp Nh&acirc;n</strong></span></p>\n\n<p style="text-align:center"><strong><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang3.png" style="border:medium none; margin:0px; padding:0px" /></strong></p>\n\n<p><br />\nTương truyền khi xưa l&agrave; trang phục của một nền văn minh cổ đại l&acirc;u đời, nhưng đ&atilde; bị Cự Long - con rồng hung bạo nhật trong truyền thuyết thi&ecirc;u hủy to&agrave;n bộ. Bộ trang phục mang một n&eacute;t g&igrave; đ&oacute; hoang d&atilde; v&agrave; cổ k&iacute;nh.</p>\n\n<p>Bộ Qu&aacute;i Vật Liệp Nh&acirc;n chỉ c&oacute; 1 c&aacute;ch lấy l&agrave;<strong>&nbsp;Qu&agrave; T&iacute;ch Lũy</strong>&nbsp;, đạt tới c&aacute;c mốc 2000 NB, 5000 NB, 10000 NB, 20000 NB.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(0, 0, 255); font-size:18pt"><strong>5. Bộ Luyện Ngục</strong></span></p>\n\n<p style="text-align:center"><strong><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang5.png" style="border:medium none; margin:0px; padding:0px" /></strong></p>\n\n<p><br />\nRực rỡ sắc đỏ, một kiệt t&aacute;c của những ngọn lửa hỏa ngục, huyền b&iacute; v&agrave; th&acirc;m s&acirc;u hơn tất cả. Luyện Ngục từ l&acirc;u đ&atilde; trở th&agrave;nh bộ trang phục hằng mơ ước của nhiều người, việc kho&aacute;c l&ecirc;n m&igrave;nh y phục đ&atilde; l&agrave; một n&eacute;t g&igrave; đ&oacute; rất quyền thế.</p>\n\n<p>Luyện Ngục đ&atilde; từng xuất hiện trong c&aacute;c đợt&nbsp;<strong>sự kiện T&iacute;ch Lũy</strong>. Tuy nhi&ecirc;n, hiện tại bạn đ&atilde; c&oacute; thể thử vận may bằng c&aacute;ch mua c&aacute;c&nbsp;<strong>Hộp Luyện Ngục</strong>&nbsp;v&agrave; mở ngẫu nhi&ecirc;n để nhận từng m&oacute;n trong bộ trang phục n&agrave;y tại Thương Th&agrave;nh.</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(0, 0, 255); font-size:18pt"><strong>6. Bộ Trang Phục Tu La</strong></span></p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang6(1).png" style="border:medium none; margin:0px; padding:0px" /></p>\n\n<p>&nbsp;Tương truyền khi xưa, ATuLa l&agrave; vị thần mạnh nhất v&agrave; quyền năng nhất, nhưng ch&iacute;nh v&igrave; điều đ&oacute; đ&atilde; khiến ATuLa sa v&agrave;o ma đạo, dẫn tới một cuộc chiến giữa c&aacute;c vị thần k&eacute;o d&agrave;i hơn trăm năm. Sau c&ugrave;ng, A TuLa bị phong ấn vĩnh viễn trong những bức tường bằng đ&aacute;. Tu La trở th&agrave;nh một truyền thuyết m&atilde;i về sau. V&agrave; bộ Tu La ch&iacute;nh l&agrave; trang phục b&aacute;ch chiến b&aacute;ch thắng của vị thần n&agrave;y.</p>\n\n<p>Tương tự như Luyện Ngục, Tu La cũng xuất hiện trong c&aacute;c đợt<strong>&nbsp; T&iacute;ch Lũy</strong>&nbsp;v&agrave; hiện tại bạn cũng c&oacute; thể mua c&aacute;c &nbsp;<strong>Hộp Tu La</strong>&nbsp;trong Thương Th&agrave;nh</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(0, 0, 255); font-size:18pt"><strong>7. Bộ Trang Phục Hồ Ti&ecirc;n</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="http://bathiendia.com/static/uploads/users/ducthang/posts/thoitrang7.png" style="border:medium none; margin:0px; padding:0px" /></p>\n\n<p>Xuất hiện muộn nhất nhưng Hồ Ti&ecirc;n đ&atilde; mau ch&oacute;ng kiếm được chỗ đứng cho m&igrave;nh khi với t&iacute;nh chất qu&yacute; ph&aacute;i v&agrave; lộng lẫy của m&igrave;nh đ&atilde; chinh phục mọi b&aacute; giả. Xứng đ&aacute;ng l&agrave; trang phục của những vị thần ti&ecirc;n cao qu&yacute; nhất, bộ Hồ Ti&ecirc;n đang l&agrave; c&aacute;i đ&iacute;ch nhắm tới của mọi t&iacute;n đồ Thời Trang.</p>\n\n<p>Hiện tại, bộ Hồ Ti&ecirc;n chỉ xuất hiện trong acc của&nbsp;GM&nbsp;<strong>,&nbsp;</strong>trong game hiện tại cũng chưa c&oacute; c&aacute;ch n&agrave;o để lấy được bộ trang phục n&agrave;y</p>\n\n<div class="fb-comment" style="margin: 0px; padding: 0px;">\n<div class="fb_reset" id="fb-root" style="margin: 0px; padding: 0px; border: 0px; border-spacing: 0px; color: rgb(0, 0, 0); cursor: auto; direction: ltr; font-family: ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 11px; line-height: 1; overflow: visible; text-shadow: none; visibility: visible; background: none rgb(230, 206, 153);">\n<div style="margin: 0px; padding: 0px; overflow: hidden; position: absolute; top: -10000px; height: 0px; width: 0px;">\n<div style="margin: 0px; padding: 0px;">&nbsp;</div>\n</div>\n\n<div style="margin: 0px; padding: 0px; overflow: hidden; position: absolute; top: -10000px; height: 0px; width: 0px;">\n<div style="margin: 0px; padding: 0px;">&nbsp;</div>\n</div>\n</div>\n\n<div class="fb-comments fb_iframe_widget" style="margin: 0px; padding: 0px; display: inline-block; position: relative; color: rgb(0, 0, 0); font-family: Tahoma, Geneva, sans-serif; font-size: 12px; line-height: 20px; background-color: rgb(230, 206, 153);">&nbsp;</div>\n</div>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:59:35', '2015-02-25 16:44:05', '0', ''),
(156, 3, '', 0, '', '0', 'Nuôi Pet', 'nuoi-pet', '<p style="text-align:center"><span style="font-size:12pt"><strong>PET</strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Th&uacute; nu&ocirc;i hay c&ograve;n gọi l&agrave; PET l&agrave; những người bạn đồng h&agrave;nh v&agrave; trợ thủ đắc lực cho mỗi nh&acirc;n vật . C&oacute; khả năng chiến đấu độc lập với kỹ năng ri&ecirc;ng biệt</span></p>\n\n<ol>\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;</strong><strong>Th&ocirc;ng tin</strong></span></li>\n</ol>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/bikiephuongdan/7.png" style="height:432px; width:624px" /></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Người chơi c&oacute; thể xem tất cả th&ocirc;ng tin những PET m&igrave;nh đang sở hữu tại đ&acirc;y.Lưu &yacute; rằng Thuộc T&iacute;nh Lực Chiến của PET sẽ lấy 33% sức mạnh của nh&acirc;n vật, tức l&agrave; nh&acirc;n vật của bạn c&agrave;ng mạnh, th&uacute; nu&ocirc;i cũng sẽ c&agrave;ng mạnh.</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Lưu &yacute; mỗi PET c&oacute; điểm Tinh Lực ri&ecirc;ng, khi xuất chiến Tinh Lực sẽ giảm, nếu bằng 0 th&igrave; th&uacute; kh&ocirc;ng thể xuất Chiến. C&oacute; thể tăng Tinh Lực bằng c&aacute;ch cho ăn</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Tại đ&acirc;y bạn cũng c&oacute; thể đổi t&ecirc;n cho Th&uacute; Nu&ocirc;i bằng c&aacute;ch ấn v&agrave;o k&iacute; hiệu c&acirc;y b&uacute;t b&ecirc;n cạnh t&ecirc;n.</span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<ol start="2">\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><strong>Trưởng Th&agrave;nh</strong></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/6.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Người chơi c&oacute; thể tăng mức độ Trưởng Th&agrave;nh của Th&uacute; nu&ocirc;i. Mức trưởng th&agrave;nh c&agrave;ng cao chỉ số PET c&agrave;ng lớn.</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Mỗi ng&agrave;y người chơi c&oacute; 1 lần miễn ph&iacute; tăng hoặc d&ugrave;ng Trưởng Th&agrave;nh PET Đan. Ngo&agrave;i ra c&oacute; thể d&ugrave;ng NB để tăng tối đa 80 lần.Khi d&ugrave;ng NB sẽ lu&ocirc;n được x2 mức trưởng th&agrave;nh, ngo&agrave;i ra c&ograve;n c&oacute; thể được bạo k&iacute;ch x5 lần</span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<ol start="3">\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><strong>Kỹ năng</strong></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/7.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Kỹ năng của Th&uacute; chia ra l&agrave;m 2 loại: Chủ Động v&agrave; Bị Động</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Mỗi Kỹ Năng c&oacute; tổng cộng 4 cấp, để l&ecirc;n cấp cao cho kỹ năng cần phải l&ecirc;n đầy sao cho cấp trước đ&oacute;. Để học l&ecirc;n cấp cao hơn c&oacute; thể d&ugrave;ng S&aacute;ch Kỹ Năng PET mua trong Tiệm Thần B&iacute;.</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Khi l&ecirc;n cấp 50, PET sẽ c&oacute; th&ecirc;m được kỹ năng Chủ Động.</span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<ol start="4">\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><strong>Linh huyệt</strong></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/8.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">C&aacute;c PET cũng c&oacute; thể trang bị vật phẩm, nhưng kh&ocirc;ng giống như nh&acirc;n vật, đ&oacute; l&agrave; những trang bị đặc biệt chỉ c&oacute; thể lấy ở Ti&ecirc;n Hồn Th&aacute;p</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">C&oacute; tổng cộng 8 &ocirc; để trang bị vật phẩm cho PET v&agrave; c&oacute; thể d&ugrave;ng PET Nội Đan để tăng cấp cho trang bị. Trang bị cấp c&agrave;ng cao chỉ số cộng th&ecirc;m c&agrave;ng mạnh.</span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<ol start="5">\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><strong>Dung Hợp</strong></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/9.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Người chơi c&oacute; thể Dung hợp 2 th&uacute; nu&ocirc;i với nhau để c&oacute; được PET thừa hưởng sức mạnh v&agrave; kỹ năng từ 2 th&uacute; trước đ&oacute;. &nbsp;Chọn th&uacute; nu&ocirc;i A để giữ h&igrave;nh ảnh cho Th&uacute; Nu&ocirc;i dung hợp, ngo&agrave;i ra nếu Pet A,B đều c&oacute; Thi&ecirc;n ph&uacute; th&igrave; chỉ bao lưu thi&ecirc;n ph&uacute; của Pet A</span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<ol start="6">\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><strong>Ảo H&oacute;a</strong></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/10.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">L&agrave; một dạng tăng sức mạnh cho PET xuất chiến. Để mở chức năng n&agrave;y cho mỗi PET cần Thẻ Ảo H&oacute;a PET .</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Sau khi k&iacute;ch hoạt c&oacute; thể nhận buff thuộc tinh tương ứng. Để tăng chỉ số buff n&agrave;y c&oacute; thể d&ugrave;ng Trứng PET hay Thẻ Ảo H&oacute;a để tăng điểm Đồ Gi&aacute;m.</span></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<ol start="7">\n	<li><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><strong>Chuyển Sinh</strong></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/11.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Mỗi PET sau khi đạt tới cấp 65 c&oacute; thể tiến h&agrave;nh Chuyển Sinh. Ở lần Chuyển Sinh sau sẽ y&ecirc;u cầu cấp cao hơn</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">PET sau khi Chuyển Sinh sẽ c&oacute; Lv.1 v&agrave; giữ nguy&ecirc;n thuộc t&iacute;nh cơ bản trước khi Chuyển Sinh. Ngo&agrave;i ra ở mỗi lần tăng cấp thuộc t&iacute;nh cộng th&ecirc;m cũng nhiều hơn</span></p>\n\n<p style="text-align:justify"><span style="font-size:12pt">Số lần Chuyển Sinh tới một mức &nbsp;nhất định sẽ mở ra được th&ecirc;m nhiều kỹ năng Chủ Động. Ngo&agrave;i ra c&ograve;n c&oacute; tỉ lệ tăng tư chất PET</span></p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:55:27', '2015-02-25 16:38:24', '0', ''),
(154, 3, '', 0, '', '0', 'Hệ Thống Tầm Bảo', 'he-thong-tam-bao', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Hệ Thống Tầm Bảo</div>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:14pt"><strong>Tầm bảo chi địa - Duy&ecirc;n khởi ti&ecirc;n tu</strong></span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;<img alt="" src="/static/uploads/editor/bikiephuongdan/4.png" style="height:431px; width:600px" /></span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">Tầm Bảo l&agrave; hệ thống t&igrave;m kiếm c&aacute;c vật phẩm qu&yacute; hiếm m&agrave; cho d&ugrave; Long H&agrave;nh Giả Nguy&ecirc;n Bảo đầy rương nhưng vẫn kh&ocirc;ng thể đoạt được, l&agrave; những bảo vật chỉ c&oacute; thể ngộ kh&ocirc;ng thể cầu.</span></span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">Trong game &nbsp;may mắn cũng t&iacute;nh l&agrave; một loại thực lực, b</span><span style="font-size:10pt">ằng việc chọn Tầm bảo 1 lần, 10 lần hay 50 lần, người chơi sẽ c&oacute; thể &quot;quay&quot; được những vật phẩm gi&aacute; trị &quot;li&ecirc;n th&agrave;nh&quot;, trong đ&oacute; ti&ecirc;u biểu l&agrave; Trứng PET - Tinh Linh Tiểu Hồ Ti&ecirc;n.</span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://bathiendia.com/static/uploads/tieuhotien.png" style="border:medium none; margin:0px; padding:0px" /></span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:10pt">B&ecirc;n cạnh đ&oacute;, tu ti&ecirc;n giới Truy mộng&nbsp;c&oacute; lời đồn đại rằng: khi đang tầm bảo m&agrave; thưởng kh&uacute;c ti&ecirc;n dao&nbsp;<strong><a href="http://mp3.zing.vn/bai-hat/Kiep-Do-Den-Duy-Manh/ZWZ9ZD96.html" style="margin: 0px; padding: 0px; color: rgb(0, 0, 0); text-decoration: none; cursor: pointer; outline: none;">Kiếp đỏ đen - Duy Mạnh</a></strong>&nbsp;th&igrave; sẽ c&agrave;ng tăng cơ hội đoạt dị bảo ti&ecirc;n gia!<br />\nChẳng biết điều n&agrave;y c&oacute; đ&uacute;ng hay kh&ocirc;ng, c&aacute;c đồng đạo h&atilde;y c&ugrave;ng kiểm nghiệm!</span></span></p>\n\n<p><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:52:24', '2015-02-25 16:35:17', '0', ''),
(155, 3, '', 0, '', '0', 'Hệ Thống Thẻ Bài', 'he-thong-the-bai', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Hệ Thống Thẻ B&agrave;i</div>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;</strong></span></span><strong>Hệ Thống Thẻ B&agrave;i</strong></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;</strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Thẻ B&agrave;i l&agrave; hệ thống đặc biệt mới lạ của game, n&oacute; l&agrave; nguồn năng lực l&agrave;m cho mỗi nh&acirc;n vật trở n&ecirc;n đặc biệt ngay cả trong c&ugrave;ng một lớp nh&acirc;n vật.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Việc kết hợp hay sử dụng c&aacute;c l&aacute; b&agrave;i th&ocirc;ng minh trong mỗi t&igrave;nh huống l&agrave; ch&igrave;a kh&oacute;a để gi&uacute;p đ&aacute;nh bại nhiều kẻ địch mạnh hơn</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<ol>\n	<li><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;</strong><strong>Lắp B&agrave;i</strong></span></span></li>\n</ol>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/bikiephuongdan/5.png" style="height:477px; width:630px" /></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Bạn c&oacute; thể lắp tối đa 4 l&aacute; b&agrave;i v&agrave;o 1 bộ. Bạn c&oacute; thể tạo ra tối đa 4 bộ để lu&acirc;n chuyển sử dụng.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Mỗi l&aacute; b&agrave;i c&oacute; năng lực được ghi r&otilde; tr&ecirc;n thẻ b&agrave;i. Ngo&agrave;i ra khi n&oacute; đi chung với 1 l&aacute; b&agrave;i kh&aacute;c sẽ sử dụng được những năng lực đặc biệt kh&aacute;c</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Lắp B&agrave;i chia ra 4 &ocirc; gồm: Nh&acirc;n Vật, Bảo Kh&iacute;, Dị Th&uacute; v&agrave; C&ocirc;ng Ph&aacute;p . Mỗi &ocirc; chỉ lắp được loại thẻ b&agrave;i tương ứng . V&iacute; dụ &ocirc; lắp Nh&acirc;n vật chỉ c&oacute; thể lắp những l&aacute; thuộc loại Nh&acirc;n vật, bạn c&oacute; thể xem ở mục Thư Viện.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">&nbsp;</span></span></p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><img alt="" src="http://bathiendia.com/static/uploads/16.png" style="border:medium none; margin:0px; padding:0px" /></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>Thuộc t&iacute;nh hiện tại</strong>: Đ&acirc;y l&agrave; những chỉ số cộng th&ecirc;m trực tiếp v&agrave;o Nh&acirc;n vật</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>Kỹ năng:</strong>&nbsp;để thi triển mục n&agrave;y th&igrave; cần những l&aacute; b&agrave;i tương ứng lắp chung v&agrave;o bộ , ở đ&acirc;y l&agrave; Linh X&agrave; Trượng hoặc Dũng Cảm</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>Hiệu Ứng QTE:</strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">L&agrave; một kỹ năng chủ động chỉ c&oacute; thể thi triển khi c&oacute; đủ bộ 4 l&aacute; như y&ecirc;u cầu (như ở đ&acirc;y l&agrave; Linh Nhi, Linh X&agrave; Trượng, Dũng Cảm, Phượng Ho&agrave;ng Hỏa Diệm) . Khi xuất hiện, hệ thống sẽ y&ecirc;u cầu bạn bấm 1 trong c&aacute;c ph&iacute;m Q,T,E, R để k&iacute;ch hoạt.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<ol start="2">\n	<li><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;</strong><strong>Tăng Cấp</strong></span></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/17.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">K&iacute; hiệu ở g&oacute;c dưới b&ecirc;n tr&aacute;i mỗi l&aacute; b&agrave;i thể hiện cho cấp độ của n&oacute;. B&agrave;i cấp c&agrave;ng cao th&igrave; chỉ số thuộc t&iacute;nh c&agrave;ng nhiều.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Để tăng cấp cho L&aacute; b&agrave;i th&igrave; phải dung c&aacute;c l&aacute; b&agrave;i kh&aacute;c để hiến tế, mỗi l&aacute; b&agrave;i t&ugrave;y thuộc mạnh yếu sẽ tăng một lượng EXP nhất định, như ở trong h&igrave;nh l&agrave; sẽ tăng 11.54% EXP cho l&aacute; Linh Nhi.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<ol start="3">\n	<li><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;</strong><strong>Gọi Hồn</strong></span></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/18.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Gọi Hồn l&agrave; hệ thống quay hồn để lấy c&aacute;c thẻ b&agrave;i mạnh hay c&aacute;c hồn.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Mỗi lần quay sẽ ti&ecirc;u tốn một lượng NB. Đổi lại bạn sẽ c&oacute; cơ hội lấy được c&aacute;c thẻ b&agrave;i mạnh hoặc c&aacute;c Linh Hồn Lam, T&iacute;m, V&agrave;ng.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Thu thập một lượng những linh hồn n&agrave;y c&oacute; thể v&agrave;o tiệm Linh Hồn để đổi lấy c&aacute;c thẻ b&agrave;i</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<ol start="4">\n	<li><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;</strong><strong>Hiệu Ứng QTE</strong></span></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;<img alt="" src="http://bathiendia.com/static/uploads/19.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Mục n&agrave;y sẽ liệt k&ecirc; tất cả những kỹ năng QTE v&agrave; những bộ l&aacute; b&agrave;i của n&oacute;. Như trong h&igrave;nh l&agrave; bộ gồm 4 l&aacute; b&agrave;i m&agrave; khi tập hợp đủ sẽ c&oacute; thể k&iacute;ch hoạt kỹ năng QTE.</span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></p>\n\n<ol start="5">\n	<li><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong>&nbsp;&nbsp;&nbsp;</strong><strong>Thư viện</strong></span></span></li>\n</ol>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt"><strong><img alt="" src="http://bathiendia.com/static/uploads/21.png" style="border:medium none; margin:0px; padding:0px" /></strong></span></span></p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:12pt">Mục n&agrave;y liệt k&ecirc; to&agrave;n bộ những l&aacute; b&agrave;i trong game. Bạn c&oacute; thể v&agrave;o đ&acirc;y xem mọi th&ocirc;ng tin về c&aacute;c l&aacute; b&agrave;i đ&atilde; hoặc chưa sở hữu.</span></span></p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:54:22', '2015-02-25 16:37:16', '0', ''),
(94, 1, '', 0, '', '0', 'Sự Kiện Tích Lũy Nhận Vật Phẩm Khủng Cuối Năm', 'su-kien-tich-luy-nhan-vat-pham-khung-cuoi-nam', '<p>Th&acirc;n Ch&agrave;o Qu&yacute; Bằng Hữu</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp; &nbsp; &nbsp;Sau 1 thời gian d&agrave;i hoạt động v&agrave; vận h&agrave;nh Game Truy Mộng, cũng c&oacute; nhiều th&agrave;nh quả v&agrave; nhiều việc kh&ocirc;ng th&agrave;nh c&ocirc;ng trong thời gian quan, nay BQT Truy Mộng tạo Event t&iacute;ch lũy nhận Qu&agrave; Khủng để tạo cho qu&yacute; bằng hữu c&oacute; 1 s&acirc;n chơi thật vui vẻ v&agrave; nhận Qu&agrave; thật Cool&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/_thumbs/tich-luy.png" style="height:316px; width:570px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><u><strong><span style="color:#FFFF00">Nội Dung :</span></strong></u> Nạp KNB T&iacute;ch lũy nhận ngay qu&agrave; khủng [ kh&ocirc;ng t&iacute;nh x100% gi&aacute; trị card ]</p>\n\n<p><u><strong><span style="color:#FFFF00">Phạm Vi :</span></strong></u> to&agrave;n bộ m&aacute;y chủ Truy Mộng</p>\n\n<p><u><strong><span style="color:#FFFF00">Thời Gian : </span></strong></u>14h00 Ng&agrave;y 20/1/2015 đến 10h00 Ng&agrave;y 24/1/2015</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:16px"><span style="color:#FFFF00"><u><strong>Vật Phầm Khủng Bao Gồm :</strong></u></span></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/ingame/TichLuy1.png" style="height:713px; width:450px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/ingame/TichLuy2.png" style="height:442px; width:450px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/ingame/TichLuy3.png" style="height:442px; width:450px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>Ch&uacute;c qu&yacute; bằng hữu Truy Mộng Tham Gia EVENT Vui Vẻ.</p>\n', 'Thân Chào Quý Bằng Hữu', '0', 0, '0', '1970-01-01', '', '2015-01-20 14:05:16', '2015-01-20 11:58:43', '0', '');
INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(158, 3, '', 0, '', '0', 'Hướng dẫn tăng cấp độ', 'huong-dan-tang-cap-do', '<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify">Cấp độ mặc d&ugrave; kh&ocirc;ng quyết định được Lực Chiến nhưng n&oacute; mở ra nhiều t&iacute;nh năng mới, hoạt động mới. Người c&oacute; lợi thế về cấp độ lu&ocirc;n đi trước những người kh&aacute;c một bước để gi&agrave;nh những ưu thế đ&aacute;ng kể. Vậy l&agrave;m thế n&agrave;o l&agrave;m thế n&agrave;o để tạo lợi thế vượt trội hơn về cấp độ khi bạn c&oacute; thời gian, c&oacute; tiền bạc hoặc cả 2 ? Hi vọng qua b&agrave;i viết n&agrave;y sẽ gi&uacute;p c&aacute;c bạn t&igrave;m được c&acirc;u trả lời đ&oacute;.</p>\n\n<p style="text-align: center;">&nbsp; <img alt="" src="/static/uploads/editor/bikiephuongdan/10.png" style="height:350px; width:600px" />&nbsp;&nbsp;</p>\n\n<p style="text-align:justify"><span style="color:rgb(255, 140, 0)"><strong>1/ Ho&agrave;n th&agrave;nh v&agrave; tối ưu to&agrave;n bộ c&aacute;c ph&oacute; bản, nhiệm vụ, hoạt động mỗi ng&agrave;y</strong></span></p>\n\n<p style="text-align:justify">N&oacute; được đặt ở vị &nbsp;tr&iacute; đầu ti&ecirc;n bởi v&igrave; bạn c&oacute; thể mất n&oacute; nếu kh&ocirc;ng ho&agrave;n th&agrave;nh ngay trong 24h v&agrave; bạn sẽ thua thiệt hơn rất rất rất nhiều so với người chơi kh&aacute;c. &nbsp;H&atilde;y đảm bảo rằng bạn đ&atilde; kh&ocirc;ng bỏ s&oacute;t bất cứ một hoạt động, nhiệm vụ hay ph&oacute; bản n&agrave;o trong mỗi ng&agrave;y.</p>\n\n<p style="text-align:justify">Đối với c&aacute;c Hoạt động theo giờ, c&oacute; thể rất kh&oacute; khăn để ho&agrave;n th&agrave;nh to&agrave;n bộ ch&uacute;ng v&igrave; kh&ocirc;ng phải l&uacute;c n&agrave;o bạn cũng rảnh. H&atilde;y tận dụng&nbsp;<strong><em>Ph&uacute;c lợi offline</em></strong>&nbsp;d&ugrave; n&oacute; sẽ ti&ecirc;u của bạn một &iacute;t NB nhưng c&aacute;i gi&aacute; l&agrave; ho&agrave;n to&agrave;n xứng đ&aacute;ng. Bạn chỉ online khoảng 5 ph&uacute;t v&agrave; tốn NB để click nhận ph&uacute;c lợi offline th&igrave; đ&atilde; kh&ocirc;ng bị những người online 24/24 bỏ qu&aacute; xa</p>\n\n<p style="text-align:justify">Ho&agrave;n th&agrave;nh l&agrave; một chuyện, nhưng để bứt ph&aacute; hơn những người chơi kh&aacute;c th&igrave; bạn cần phải tối ưu n&oacute; một c&aacute;ch ho&agrave;n hảo. &nbsp;<strong><em>Lu&ocirc;n d&ugrave;ng Ph&ugrave; EXP</em></strong>&nbsp;khi ho&agrave;n th&agrave;nh c&aacute;c Ph&oacute; bản,&nbsp; h&atilde;y sử dụng loại b&ugrave;a cao cấp nhất bạn c&oacute;, v&agrave; nếu muốn tiết kiệm h&atilde;y cố gắng ho&agrave;n th&agrave;nh to&agrave;n bộ phụ bản trong thời gian hiệu lực đ&oacute;.</p>\n\n<p style="text-align:justify">Đối sự kiện&nbsp;<strong>Ti&ecirc;n Lữ T&igrave;nh Duy&ecirc;n</strong>, bạn n&ecirc;n bỏ 10NB để đạt thưởng x1.5 . Sau đ&oacute; h&atilde;y thỏa thuận với người bạn c&ugrave;ng tham gia chọn lấy c&aacute;c c&acirc;u hỏi, th&ocirc;ng thường th&igrave; mọi người n&ecirc;n chọn c&acirc;u d&agrave;i nhất sẽ rất dễ đạt độ thấu hiểu 100. Lưu &yacute; rằng sau khi ho&agrave;n th&agrave;nh nhim vụ của bạn, bạn c&ograve;n c&oacute; thể tham gia l&agrave;m gi&uacute;o cho người kh&aacute;c v&agrave; vẫn nhận được EXP</p>\n\n<p style="text-align:justify">C&oacute; một số nhiệm vụ bạn c&oacute; thể sẽ bỏ s&oacute;t như&nbsp;<strong><em>Th&aacute;m Hiểm</em></strong>, n&oacute; l&agrave; t&iacute;nh năng đưa PET đi th&aacute;m hiểm để thu về EXP v&agrave; một số vật phẩm kh&aacute;c, bạn c&oacute; thể l&agrave;m điều n&agrave;y trong mục PET, tab Th&ocirc;ng Tin v&agrave; ấn n&uacute;t Th&aacute;m hiểm. Bạn c&oacute; 3 lần thực hiện mỗi ng&agrave;y</p>\n\n<p style="text-align:center"><img alt="" src="http://dl.satlong.com/photo/2014/12/01/4aaca79d33c2b72e4f1174ddc6f08391.png" style="border:0px; box-sizing:border-box; max-width:100%; vertical-align:middle" /></p>\n\n<p style="text-align:justify"><span style="color:rgb(255, 140, 0)"><strong>2/ Tham gia Trảm S&aacute;t Thần Tướng v&agrave; Thần Ma Gi&aacute;ng L&acirc;m</strong></span></p>\n\n<p style="text-align:justify">Nếu đ&atilde; bước v&agrave;o cuộc đua cấp độ, bạn n&ecirc;n cố gắng tham gia đầy đủ 2 hoạt động n&agrave;y. Trảm S&aacute;t Thần Tướng diễn ra v&agrave;o 15:00 mỗi ng&agrave;y trong khi Thần Ma Gi&aacute;ng L&acirc;m l&agrave; 13:00 mỗi T7 v&agrave; Chủ Nhật.</p>\n\n<p style="text-align:justify">Trảm S&aacute;t Thần Tướng sẽ mang lại một lượng lớn EXP cho to&agrave;n bộ Li&ecirc;n Minh nếu 1 người trong số đ&oacute; hạ thủ được thần tướng của li&ecirc;n minh kh&aacute;c. V&igrave; thế nếu bạn tham gia, th&igrave; li&ecirc;n minh của bạn c&oacute; th&ecirc;m 1 phần cơ hội để chiến thắng.</p>\n\n<p style="text-align:center"><img alt="" src="http://dl.satlong.com/photo/2014/12/01/0a4e83d6b5ee7c14226cb8b2cd4342c8.png" style="border:0px; box-sizing:border-box; height:355px; max-width:100%; vertical-align:middle; width:518px" /></p>\n\n<p style="text-align:justify">Thần Ma Gi&aacute;ng L&acirc;m lại kh&ocirc;ng y&ecirc;u cầu bạn phải l&agrave; người hạ thủ, m&agrave; chỉ cần tham gia g&acirc;y s&aacute;t thương th&igrave; mỗi lần đ&aacute;nh như vậy bạn đều nhận được một lượng lớn EXP, n&oacute; cực kỳ hữu dụng khi bạn đạt tới ngưỡng cấp độ cao.</p>\n\n<p style="text-align:justify"><span style="color:rgb(255, 140, 0)"><strong>3/ Auto train tại c&aacute;c b&atilde;i qu&aacute;i</strong></span></p>\n\n<p style="text-align:justify">Nếu bạn c&oacute; thời gian rảnh m&agrave; đ&atilde; l&agrave;m hết hoạt động, bạn c&oacute; thể để auto farm ở bất cứ b&atilde;i qu&aacute;i n&agrave;o trong khoảng ch&ecirc;nh lệch 5 cấp.</p>\n\n<p style="text-align:justify">Khi auto bạn h&atilde;y thiết lập để kỹ năng đầu ti&ecirc;n v&agrave; kỹ năng đ&aacute;nh lan để đạt tối đa hiệu quả.</p>\n\n<p style="text-align:justify">Ngo&agrave;i ra nếu bạn l&agrave; VIP, tận dụng Map Ủy Th&aacute;c để khi farm qu&aacute;i nhận được th&ecirc;m EXP.Việc tổ đội cũng gi&uacute;p gia tăng một lượng nhỏ EXP khi train</p>\n\n<p style="text-align:justify">Cuối c&ugrave;ng nếu dư dả bạn c&oacute; thể d&ugrave;ng b&ugrave;a EXP, tuy nhi&ecirc;n nếu ko h&atilde;y để d&agrave;nh sử dụng b&ugrave;a cho c&aacute;c ph&oacute; bản</p>\n\n<p style="text-align:justify"><span style="color:rgb(255, 140, 0)"><strong>4/ Đ&aacute;m cưới</strong></span></p>\n\n<p style="text-align:justify">Đ&aacute;m cưới l&agrave; một nguồn thu EXP kh&ocirc;ng nhỏ v&agrave; n&oacute; ch&iacute;nh l&agrave; điểm mấu chốt để bạn c&oacute; thể đột ph&aacute; trong đường đua cấp độ. 3 điều tr&ecirc;n ai cũng c&oacute; thể thực hiện nhưng đ&aacute;m cưới v&agrave; việc tham gia đ&aacute;m cưới kh&ocirc;ng ai giống ai cả.</p>\n\n<p style="text-align:justify">Đầu ti&ecirc;n bạn phải cần biết khi đi nhặt Hồng Bao L&igrave; X&igrave; bạn c&oacute; thể mở ra được c&aacute;c Kinh Nghiệm Đan gi&uacute;p tăng EXP. V&agrave; quan trọng nhất, khi bạn tham dự một h&ocirc;n lễ, EXP của bạn sẽ được tăng l&ecirc;n đ&aacute;ng kể mỗi gi&acirc;y m&agrave; bạn ở trong đ&oacute;. Khi ở trong một h&ocirc;n lễ, h&atilde;y kiếm 1 ai đ&oacute; để tiến h&agrave;nh song tu, d&ugrave; g&igrave; th&igrave; bạn cũng chẳng thể l&agrave;m được g&igrave; kh&aacute;c khi đang tham dự đ&aacute;m cưới.</p>\n\n<p style="text-align:justify">Nếu bạn dư dả tiền, bạn c&oacute; thể mua ph&aacute;o để đốt, v&agrave; nếu bạn thực sự m&uacute;n đua Top, đ&acirc;y c&oacute; thể l&agrave; c&acirc;u trả lời cho bạn, đốt tiền v&agrave;o Ph&aacute;o sẽ gi&uacute;p bạn đi trước tất cả người kh&aacute;c/</p>\n\n<p style="text-align:justify">H&atilde;y đ&aacute;m cưới thật nhiều lần c&oacute; thể. Đơn giản l&agrave; bạn chỉ cần ra li dỵ sau 24h, tiến h&agrave;nh cầu h&ocirc;n lại v&agrave; chỉ tốn 10.000 Đồng để tổ chức một h&ocirc;n lễ Thường., bạn sẽ chẳng mất g&igrave; từ đồ đạc cưới cho tới điểm th&acirc;n mật.</p>\n\n<p style="text-align:justify">Cố gắng giao tiếp để người kh&aacute;c mời bạn tham gia đ&aacute;m cưới, một mối quan hệ rộng r&atilde;i gi&uacute;p &iacute;ch bạn rất nhiều trong cuộc chạy đua ng&ocirc;i vương cấp độ</p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 17:10:06', '2015-02-25 17:10:06', '0', ''),
(99, 1, '', 0, '', '0', 'Nạp Mốc Nhận Quà Khủng Truy Mộng S9', 'nap-moc-nhan-qua-khung-truy-mong-s9', '<p><em><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</strong></em></p>\n\n<p>Để tạo điều kiện thuận lợi cho qu&yacute; bằng hữu ngao du Truy Mộng.&nbsp;<span style="color:rgb(255, 255, 0)"><em><strong>BQT Truy Mộng</strong></em></span>&nbsp;quyết định tạo&nbsp;Sự kiện<em>&nbsp;<strong><span style="color:rgb(255, 0, 0)">Nạp Mốc Nhận Quả Khủng</span></strong></em>&nbsp;để gi&uacute;p qu&yacute; bằng hữu sở hữu những h&agrave;nh trang cần thiết trong qu&aacute; tr&igrave;nh h&agrave;nh tẩu giang hồ.</p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/cahm-moc.png" style="height:316px; width:570px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 255, 0)"><strong>Nội dung</strong>&nbsp;:</span>&nbsp;Nạp mốc cộng dồn t&iacute;ch lũy trong 7 ng&agrave;y&nbsp;<span style="color:rgb(255, 0, 0)"><em><strong>( Chỉ T&iacute;nh Mức Nạp V&agrave;o Kh&ocirc;ng T&iacute;nh Tiền Khuyến M&atilde;i X100% )</strong></em></span></p>\n\n<p><span style="color:rgb(255, 255, 0)"><strong>Đối tượng tham gia&nbsp;</strong>:</span>&nbsp;tất cả qu&yacute; bằng hữu M&aacute;y Chủ Truy Mộng S9&nbsp; tham gia game&nbsp;<strong>Truy Mộng</strong></p>\n\n<p><span style="color:rgb(255, 255, 0)"><strong>Thời gian sự kiện :</strong><em><strong>&nbsp;</strong></em></span><em><strong>10h00 ng&agrave;y 22/1&nbsp;đến 23h59 ng&agrave;y 29/1/2015&nbsp;</strong></em></p>\n\n<p><strong><span style="color:rgb(255, 255, 0)">Phần thưởng bao gồm :</span></strong></p>\n\n<p>&nbsp;</p>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%201.png" style="height:587px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%202.png" style="height:340px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%203.png" style="height:340px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%204.png" style="height:482px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%205.png" style="height:482px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%206.png" style="height:482px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%207.png" style="height:482px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%208.png" style="height:483px; width:398px" /></h1>\n\n<h1><img alt="" src="/static/uploads/editor/_thumbs/Images/_thumbs/top%209.png" style="height:482px; width:398px" /></h1>\n\n<p>&nbsp;</p>\n\n<p><em><span style="color:rgb(255, 0, 0)"><strong>Lưu &Yacute; :&nbsp;</strong></span></em></p>\n\n<p><em><span style="color:rgb(255, 0, 0)"><strong>- Mỗi mốc chỉ nhận được phần thưởng 1 lần trong suốt sự kiện.</strong></span></em></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Ch&uacute;c qu&yacute; bằng hữu S9&nbsp;tham gia event vui vẻ .</strong></p>\n', 'Để tạo điều kiện thuận lợi cho quý bằng hữu ngao du Truy Mộng. BQT Truy Mộng quyết định tạo Sự kiện Nạp Mốc Nhận Quả Khủng ', '0', 0, '0', '1970-01-01', '', '2015-01-21 19:02:52', '2015-01-21 19:02:52', '0', ''),
(150, 3, '', 0, '', '0', 'Bí Kíp Bá Đạo Của Cao Thủ', 'bi-kip-ba-dao-cua-cao-thu', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">B&iacute; K&iacute;p B&aacute; Đạo Của Cao Thủ</div>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="/static/uploads/editor/bikiephuongdan/bikipcaothu_ht.jpg" style="height:641px; width:1000px" />&nbsp;</span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:justify"><span style="font-family:tahoma,geneva,sans-serif; font-size:40px">Truy Mộng</span><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;sở hữu 1 gameplay đồ sộ với nhiều hệ thống, t&iacute;nh năng mới lạ. Ch&iacute;nh v&igrave; vậy đối với c&aacute;c t&acirc;n thủ sẽ rất kh&oacute; khăn để nhận ra đ&acirc;u mới l&agrave; con đường đ&uacute;ng đắn. Mới đ&acirc;y, một cao thủ đứng Top 1 hiện nay trong Truy mộng&nbsp;</span></p>\n\n<div class="content" style="margin: 0px; padding: 0px;">\n<div id="post_message_266" style="margin: 0px; padding: 0px;">\n<blockquote><br />\n<br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">C&oacute; ch&uacute;t kinh nghiệm chia sẻ với AE đ&acirc;y. Ph&iacute;a tr&ecirc;n l&agrave; to&agrave;n bộ th&ocirc;ng số chi tiết về LC của m&igrave;nh.&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/179.gif" style="border:medium none; margin:0px; padding:0px" title="179" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/179.gif" style="border:medium none; margin:0px; padding:0px" title="179" /></span><br />\n&nbsp;</blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">1/ Sắm VIP Nửa Năm</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Nếu c&aacute;c bạn c&oacute; nạp KNB th&igrave; trước hết h&atilde;y sắm Vip nữa năm&nbsp;nh&eacute;, c&oacute; lợi cho bạn rất nhiều về l&ecirc;n đồ v&agrave; l&ecirc;n cấp&nbsp;<br />\n-Th&ecirc;m kinh nghiệm đ&aacute;nh qu&aacute;i&nbsp;<br />\n-Th&ecirc;m số lần mua phụ bản trang bị&nbsp;<br />\n-Th&ecirc;m tỉ lệ th&agrave;nh c&ocirc;ng cường h&oacute;a v&agrave; nhận ph&uacute;c lợi h&agrave;ng ng&agrave;y.</span></span></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium"><strong>2/ L&ecirc;n cấp nhanh</strong><br />\n<br />\nĐể l&ecirc;n cấp nhanh quan trọng nhất l&agrave; c&aacute;c bạn phải c&oacute; thời gian onl v&agrave; nh&agrave; c&oacute; m&aacute;y.&nbsp;<br />\n- Với những bạn kh&ocirc;ng c&oacute; thời gian onl v&agrave; nh&agrave; kh&ocirc;ng c&oacute; m&aacute;y h&atilde;y tranh thủ thời gian của bạn c&oacute; m&agrave; l&agrave;m hết nhiệm vụ h&agrave;ng ng&agrave;y v&agrave; phụ bản.&nbsp;<br />\n- C&aacute;c bạn nh&agrave; c&oacute; m&aacute;y v&agrave; c&oacute; thời gian online m&igrave;nh chỉ c&oacute; một mẹo nhỏ &quot;những l&uacute;c kh&ocirc;ng c&oacute; g&igrave; l&agrave;m th&igrave; h&atilde;y cắm auto ở b&atilde;i vip vs qu&aacute;i bằng cấp hoặc hơn m&igrave;nh 1 cấp&quot;</span></span></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">3/ Trang bị</span></span></strong></span></blockquote>\n\n<blockquote><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Trang bị &agrave; xem n&agrave;o c&aacute;c bạn muốn c&oacute; trang bị ch&acirc;n ti&ecirc;n th&igrave; phải chăm săn boss thế giới v&agrave;o với c&aacute;c PB&nbsp;<br />\nTrang Bị, Thần Ma Chi t&igrave;nh, Ti&ecirc;n Hồn Th&aacute;p v&agrave; c&aacute;c hoạt động c&aacute;c bạn rất nhanh l&ecirc;n cấp trang bị&nbsp;<br />\n- C&aacute;c bạn trước hết h&atilde;y tăng cấp trang bị của m&igrave;nh to&agrave;n bộ l&ecirc;n +6 nh&eacute; để + th&ecirc;m thuộc t&iacute;nh với c&aacute;c mốc +6 +10 +15 +20 +25 sẽ + th&ecirc;m kh&ocirc;ng hề &iacute;t LC cho bạn đ&acirc;u đ&oacute;&nbsp;<br />\n- Khảm bảo thạch m&igrave;nh thấy kh&oacute; nhất khi kiếm 3 loại c&ocirc;ng k&iacute;ch - bạo k&iacute;ch - ch&iacute;nh x&aacute;c. C&aacute;c bạn cố sắm đủ cho m&igrave;nh 39 vi&ecirc;n bảo thạch để khảm v&agrave;o trang bị trước nh&eacute; để k&iacute;ch hoạt thuộc t&iacute;nh + th&ecirc;m của bảo thạch c&aacute;c bạn cần 39 vi&ecirc;n bảo thạch cấp 1 rồi đến cấp 2 v&agrave; cứ l&ecirc;n cao thuộc t&iacute;nh + th&ecirc;m kh&ocirc;ng &iacute;t c&aacute;c bạn n&ecirc;n lưu &yacute; c&aacute;i n&agrave;y.&nbsp;<br />\n- Tẩy luyện trang bị gi&uacute;p bạn tăng rất nhiều LC v&agrave; c&oacute; 1 c&aacute;i hay nữa của tẩy luyện l&agrave; thuộc t&iacute;nh + th&ecirc;m của c&aacute;c d&ograve;ng Kh&iacute; Huyết v&agrave; C&ocirc;ng k&iacute;ch.&nbsp;<br />\n<br />\nBước đầu l&agrave; 5 d&ograve;ng Kh&iacute; huyết 10 sao k&iacute;ch hoạt - 10 d&ograve;ng kh&iacute; huyết 10 sao k&iacute;ch hoạt&nbsp;<br />\n5 d&ograve;ng c&ocirc;ng k&iacute;ch 10 sao k&iacute;ch hoạt - 10 d&ograve;ng c&ocirc;ng k&iacute;ch 10 sao k&iacute;ch hoạt. C&aacute;c bạn n&ecirc;n lưu &yacute; khi tẩy luyện trang bị nh&eacute;&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/155.gif" style="border:medium none; margin:0px; padding:0px" title="155" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/155.gif" style="border:medium none; margin:0px; padding:0px" title="155" /><span style="font-family:tahoma"><span style="font-size:medium">&nbsp;</span></span></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">4/ Thời Trang</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Thời trang đ&atilde; đẹp rồi sao n&oacute; lại c&ograve;n mạnh nữa nhỉ&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/152.gif" style="border:medium none; margin:0px; padding:0px" title="152" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/152.gif" style="border:medium none; margin:0px; padding:0px" title="152" /><span style="font-family:system"><span style="font-family:tahoma"><span style="font-size:medium">c&aacute;c bạn h&atilde;y sắm đủ 4 m&oacute;n thời trang 4 bộ thẻ b&aacute;t qu&aacute;i thanh long trước đi nh&eacute; sau đ&oacute; h&atilde;y l&ecirc;n bạch hổ</span></span></span></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:system"><span style="font-family:tahoma"><span style="font-size:medium"><strong>5/ Kết h&ocirc;n</strong></span></span></span></span></blockquote>\n\n<blockquote><br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">C&aacute;c bạn nhanh nhanh t&igrave;m cho m&igrave;nh 1 người bạn kết duy&ecirc;n đi sẽ nhận được 1 đ&ocirc;i nhẫn cưới rất gi&aacute; trị đ&oacute;............LC kh&ocirc;ng &iacute;t đ&acirc;u</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/12.gif" style="border:medium none; margin:0px; padding:0px" title="12" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/12.gif" style="border:medium none; margin:0px; padding:0px" title="12" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/12.gif" style="border:medium none; margin:0px; padding:0px" title="12" /></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i1374.photobucket.com/albums/ag412/kimtrung2808/11_zps7efd55e3.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n</blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">6/ Ti&ecirc;n Nữ - Thần Giao</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Với c&aacute;c ti&ecirc;n nữ l&agrave;m g&igrave; để c&aacute;c n&agrave;ng n&oacute;ng bỏng hơn nhỉ &agrave; cho l&ecirc;n chảo&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/33.gif" style="border:medium none; margin:0px; padding:0px" title="33" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/33.gif" style="border:medium none; margin:0px; padding:0px" title="33" /><br />\n<span style="font-family:tahoma"><span style="font-size:medium">Tinh nguy&ecirc;n m&agrave; c&aacute;c bạn c&oacute; h&agrave;ng ng&agrave;y từ NV v&agrave; PB c&aacute;c bạn t&iacute;ch nhiều nhiều v&agrave;o rồi h&atilde;y tăng cấp ti&ecirc;n nữ như vậy đỡ tốn tinh nguy&ecirc;n hơn nhiều&nbsp;<br />\nThần giao c&aacute;c bạn tăng l&ecirc;n đều đều nh&eacute; khi tất cả đ&atilde; tăng l&ecirc;n cấp 5 sẽ nhận được th&ecirc;m kh&aacute; nhiều thuộc t&iacute;nh v&agrave; sẽ kh&ocirc;ng bị giảm cấp khi thất bại, thần giao cấp c&agrave;ng cao sẽ th&ecirc;m kh&aacute; nhiều thuộc t&iacute;nh n&ecirc;n c&aacute;c bạn đừng c&oacute; bỏ qua nh&eacute;</span></span><br />\n<span style="font-family:tahoma"><span style="font-size:medium">C&aacute;c bạn tăng từ từ thui đừng tăng nhanh qu&aacute;, đi nhanh ng&atilde; đau&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/128.gif" style="border:medium none; margin:0px; padding:0px" title="128" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/128.gif" style="border:medium none; margin:0px; padding:0px" title="128" /><span style="font-family:tahoma">&nbsp;<span style="font-size:medium">từ từ thui cho đỡ đau</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i1374.photobucket.com/albums/ag412/kimtrung2808/12_zps89dbe0fc.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></div>\n</blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">7/ Tọa kỵ</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">M&igrave;nh trước mắt kh&ocirc;ng tăng cấp tọa kỵ v&igrave; rất tốn m&agrave; tọa kỵ tiến cấp đơn kiếm kh&ocirc;ng hề dễ kiếm ch&uacute;t n&agrave;o muốn nhanh th&igrave; phải tốn KNB n&ecirc;n tọa kỵ của m&igrave;nh hiện giờ cấp 3 hihi ngại qu&aacute;&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/163.gif" style="border:medium none; margin:0px; padding:0px" title="163" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/163.gif" style="border:medium none; margin:0px; padding:0px" title="163" /></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i1374.photobucket.com/albums/ag412/kimtrung2808/16_zps18ef5060.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></div>\n</blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">8/ PET</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">LC của pet chiếm hơn 30% n&ecirc;n c&aacute;c bạn đừng qu&ecirc;n chăm s&oacute;c pet của m&igrave;nh nh&eacute;, cố gắng vượt qua c&aacute;c tầng của Ti&ecirc;n Hồn Th&aacute;p c&aacute;c bạn sẽ c&oacute; trang bị cho pet v&agrave; nguy&ecirc;n liệu tăng cấp trang bị của cả nh&acirc;n vật v&agrave; pet, mỗi tầng Ti&ecirc;n hồn thấp đều kh&oacute; qua nếu c&aacute;c bạn để &yacute; khắc chế skill boss v&agrave; phải tr&aacute;nh khỏi c&aacute;c khu vực ảnh hưởng skill boss c&aacute;c nhanh c&agrave;ng tốt, với c&aacute;c trụ v&agrave; qu&aacute;i buff m&agrave; boss gọi ra c&aacute;c bạn phải ti&ecirc;u diệt c&agrave;ng nhanh c&agrave;ng tốt, boss đ&aacute;nh thường kh&ocirc;ng đau nhưng với những skill v&agrave; qu&aacute;i gọi ra th&igrave; sẽ hạ gục bạn 1 c&aacute;ch dễ d&agrave;ng .</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/200.gif" style="border:medium none; margin:0px; padding:0px" title="200" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/200.gif" style="border:medium none; margin:0px; padding:0px" title="200" /></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i1374.photobucket.com/albums/ag412/kimtrung2808/17_zps42980912.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></div>\n</blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">9/ Thẻ B&agrave;i</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Với hệ thống thẻ ma cũng phải mất kh&aacute; l&acirc;u mới c&oacute; những bộ thẻ ma mạnh, nhưng trước mắt c&aacute;c bạn c&oacute; thể d&ugrave;ng bộ thẻ ma Phệ Mộng Chi Lực với QTE 80% s&aacute;t thương v&agrave; bộ thẻ ma Hồi Phong Lưu tuyết QTE 40% s&aacute;t thương tha hồ m&agrave; dame boss, c&ograve;n ph&iacute;a dưới m&igrave;nh d&ugrave;ng bộ thẻ ma Chiến Thần h&agrave;ng L&acirc;m QTE +3 nội lực bộ n&agrave;y m&igrave;nh d&ugrave;ng để lấy LC chứ ko c&oacute; mạnh lắm&nbsp;</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i1374.photobucket.com/albums/ag412/kimtrung2808/15_zpsed2d0b8e.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px">&nbsp;</span></div>\n</blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><strong><span style="font-family:tahoma"><span style="font-size:medium">10/ Kỹ năng</span></span></strong></span></blockquote>\n\n<blockquote><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Trước m&igrave;nh chơi bản AP do hệ thống kỹ năng bị lỗi n&ecirc;n LC c&oacute; 350k dame rất yếu kh&ocirc;ng như b&acirc;y giờ LC c&oacute; 55k m&agrave; dame rất đau, với kỹ năng m&igrave;nh thường tăng kỹ năng 1 2 v&agrave; 3 kỹ năng 1 v&agrave; 2 m&igrave;nh l&ecirc;n tầng 2 oy. Mẹo nhỏ cho c&aacute;c bạn c&oacute; đi ti&ecirc;n hồn th&aacute;p c&aacute;c bạn đừng nhận thưởng lu&ocirc;n nh&eacute; h&atilde;y t&iacute;ch 2-4 ng&agrave;y oy nhận v&agrave; d&ugrave;ng knb m&agrave; nhận sẽ được thưởng nhiều hơn đấy v&agrave; nguy&ecirc;n h&ocirc;n kh&ocirc;ng hề &iacute;t c&aacute;c bạn thoải m&aacute;i m&agrave; tăng skill. C&aacute;c bạn cầy cấp th&igrave; thiết lập auto để kỹ năng 1 v&agrave; 3 nh&eacute;, c&ograve;n dame boss th&igrave; để kỹ năng 1 v&agrave; 2, đi phụ bản cũng vậy t&ugrave;y phụ bản m&agrave; c&aacute;c bạn thiết lập auto kh&aacute;c nhau, giảm thời gian vượt PB đi rất nhiều.</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i1374.photobucket.com/albums/ag412/kimtrung2808/14_zps02f6cb59.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n<br />\n<span style="font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-family:tahoma"><span style="font-size:medium">Kết luận lại c&aacute;c bạn muốn m&igrave;nh mạnh được đứng TOP th&igrave; c&aacute;c bạn phải đầu tư, từ thời gian lẫn tiền bạc ngo&agrave;i đời như n&agrave;o th&igrave; trong thế giới ảo cũng kh&ocirc;ng ngoại lệ...!!</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/17.gif" style="border:medium none; margin:0px; padding:0px" title="17" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/17.gif" style="border:medium none; margin:0px; padding:0px" title="17" /><span style="font-family:tahoma"><span style="font-size:medium">M&igrave;nh chỉ c&oacute; nhi&ecirc;u vậy để chia sẻ với c&aacute;c bạn c&ograve;n c&aacute;c bạn chơi như n&agrave;o v&agrave; mạnh như n&agrave;o l&agrave; do ở c&aacute;c bạn t&igrave;m hiểu được game đến mức n&agrave;o. B&iacute; quyết l&agrave; b&iacute; mật kh&ocirc;ng thể n&oacute;i ra được v&agrave; đ&ocirc;i khi n&oacute; sẽ được chia sẻ&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/22.gif" style="border:medium none; margin:0px; padding:0px" title="22" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/22.gif" style="border:medium none; margin:0px; padding:0px" title="22" /><br />\n<span style="font-family:tahoma"><span style="font-size:medium">M&igrave;nh viết c&ograve;n rất nhiều sơ s&agrave;i do c&ograve;n nhiều thứ chưa t&igrave;m hiểu được thời gian c&ograve;n d&agrave;i m&igrave;nh sẽ bổ sung th&ecirc;m&nbsp;</span></span><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/3.gif" style="border:medium none; margin:0px; padding:0px" title="3" /><img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/3.gif" style="border:medium none; margin:0px; padding:0px" title="3" /><span style="font-family:tahoma"><span style="font-size:medium">&nbsp;</span></span></span></blockquote>\n</div>\n</div>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:25:14', '2015-02-25 16:23:32', '0', ''),
(102, 2, '', 0, '', '0', 'Bộ Giftcode Tân Thủ', 'bo-giftcode-tan-thu', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu</strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><strong><img alt="" src="/static/uploads/editor/eventthoitrang%5D/CODE.gif" style="height:250px; width:540px" /></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp;Tr&ecirc;n con đường h&agrave;nh tẩu giang hồ sẽ c&oacute; những ch&ocirc;ng gai nhiều đại hiệp c&oacute; thể chống chọi với những kh&oacute; khăn đ&oacute; để sinh tồn c&ugrave;ng với đ&oacute; l&agrave; 1 số h&agrave;o hiệp muốn chu du chốn &quot; Truy Mộng &quot; nhưng thiếu kinh nghiệm đối ph&oacute; với kh&oacute; khăn trước mắt, v&igrave; thế BQT &quot; Truy Mộng &quot; tạo 1 số CODE l&agrave; những h&agrave;nh trang cần thiết gi&uacute;p c&aacute;c h&agrave;o hiệp c&oacute; thể th&iacute;ch nghi với những kh&oacute; khăn ấy</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:16px"><span style="color:#FFFF00">1/ Giftcode Lưu Game&nbsp;</span></span></strong></p>\n\n<p><strong><span style="font-size:16px"><span style="color:#FFFF00">2/ Giftcode Mail</span></span></strong></p>\n\n<p><strong><span style="font-size:16px"><span style="color:#FFFF00">3/ Giftcode SMS</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Ch&uacute;c qu&yacute; bằng hữu tham gia game Truy Mộng vui vẻ.</strong></p>\n', 'thân chào quý bằng hữu', '0', 1, '0', '1970-01-01', '', '2015-01-26 14:09:15', '2015-01-26 11:28:28', '0', ''),
(103, 2, '', 0, '', '0', 'Giftcode Lì Xì Khủng Năm Mới', 'giftcode-li-xi-khung-nam-moi', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/eventthoitrang%5D/nammoi.gif" style="height:250px; width:540px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nh&acirc;n dịp năm mới BQT &quot; Truy Mộng &quot; gởi lời ch&uacute;c đến to&agrave;n thể qu&yacute; bằng hữu đang ngao du trong thế giới &quot; Truy Mộng &quot; 1 năm mới th&agrave;nh c&ocirc;ng trong c&ocirc;ng việc, hạnh ph&uacute;c với gia đ&igrave;nh v&agrave; người th&acirc;n v&agrave; lu&ocirc;n lu&ocirc;n cảm thấy vui vẻ với Game &quot; Truy Mộng &quot;. Qua năm 2015 BQT &quot; Truy Mộng &quot; sẽ cố gắng khắc phục những lỗi 1 c&aacute;ch triệt để, mang lại 1 sản phẩm game tốt nhất đến qu&yacute; bằng hữu</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#FFFF00"><span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Năm mới tết đến</span><br />\n<br />\n<span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Ch&uacute;c Ae&nbsp;dồi d&agrave;o</span><br />\n<br />\n<span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Level tấn tới</span><br />\n<br />\n<span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Chơi ng&agrave;y c&agrave;ng pro</span></span></strong></p>\n\n<p style="text-align:center"><br />\n<strong><span style="color:#FFFF00"><span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Pet level kh&aacute;</span><br />\n<br />\n<span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Ko sợ anh n&agrave;o</span></span></strong></p>\n\n<p style="text-align:center"><strong><span style="color:#FFFF00"><span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Mạng ko c&ograve;n lag</span></span></strong></p>\n\n<p style="text-align:center"><strong><span style="color:#FFFF00"><span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Ko c&ograve;n bị dis</span></span></strong></p>\n\n<p style="text-align:center"><strong><span style="color:#FFFF00"><span style="font-family:lucida grande,lucida sans unicode,verdana,tahoma,arial,sans-serif; font-size:14px">Chơi game o o.</span></span></strong><br />\n<br />\n&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;BQT &quot; Truy Mộng &quot; gời đến Qu&yacute; bằng hữu CODE L&igrave; x&igrave; năm mới 2015 thay lời tri &acirc;n đến to&agrave;n bộ c&aacute;c đại hiệp [ Vật phẩm l&agrave; b&iacute; mật nh&eacute; ]&nbsp;</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="color:#FFF0F5"><span style="font-size:18px"><strong>GIFTCODE L&igrave; X&igrave; Năm Mới : [ Thời Gian : ...... ]&nbsp;</strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Ch&uacute;c Qu&yacute; bằng hữu &quot; Truy Mộng &quot; lu&ocirc;n vui vẻ.</strong></p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-01-26 15:00:34', '2015-01-26 14:35:28', '0', ''),
(104, 2, '', 0, '', '0', 'Mở Túi Lì Xì May Mắn', 'mo-tui-li-xi-may-man', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp;Năm mới 2015 đến BQT &quot; Truy Mộng &quot; k&iacute;nh ch&uacute;c qu&yacute; bằng hữu năm mới vui vẻ c&ugrave;ng với kh&ocirc;ng kh&iacute; tết đến BQT c&oacute; 1 Event vui vẻ d&agrave;nh cho qu&yacute; bằng hữu trong dịp tết n&agrave;y đ&oacute; l&agrave; Event &quot; Mở T&uacute;i L&igrave; X&igrave; May Mắn &quot;</strong></p>\n\n<p><strong>với nhiều phần qu&agrave; hấp dẫn.BQT hy vọng sẽ tạo th&ecirc;m kh&ocirc;ng kh&iacute; tết đến từng đại hiệp ngao du thế giới &quot; Truy Mộng &quot;.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/eventthoitrang%5D/nammoi3.gif" style="height:476px; width:850px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><strong><img alt="" src="/static/uploads/editor/eventthoitrang%5D/nammoi2.png" style="height:584px; width:667px" /></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>* Thời Gian : 18h00 Ng&agrave;y 26/1/2015 đến 10h00 Ng&agrave;y 26/2/2015</strong></p>\n\n<p><strong>*<span style="color:rgb(255, 255, 0)">&nbsp;&Aacute;p Dụng Cho Tất Cả Server&nbsp;</span></strong></p>\n\n<p><strong>* Nội Dung : Với mỗi lần nạp 20.000 VND sẽ nhận miễn ph&iacute; 1 lượt b&ocirc;c&nbsp;&nbsp;(<u><span style="font-size:16px"><span style="color:#FFFF00">&nbsp;kh&ocirc;ng mất KNB của bạn&nbsp;)</span></span></u></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Ch&uacute;c Qu&yacute; bằng hữu 1 năm mới thật vui vẻ c&ugrave;ng Event .</strong></p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-01-26 15:18:07', '2015-01-26 15:00:22', '0', ''),
(164, 1, '', 0, '', '0', 'Sự Kiện Khai Mở Máy Chủ [ Nam Giang ]', 'su-kien-khai-mo-ma-y-chu-nam-giang-', '<h3><strong>Sự Kiện Khai Mở Máy Chủ [&nbsp;<span style="color:rgb(0, 255, 255)">Nam Giang</span>&nbsp;]</strong></h3>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><em><span style="font-family:arial,helvetica,sans-serif">Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</span></em></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="font-family:arial,helvetica,sans-serif">&nbsp;Đ&aacute;p ứng nhu cầu tất yếu của to&agrave;n thể quý đ&ocirc;̀ng đạo tham gia</span>&nbsp;&quot;<span style="color:rgb(0, 0, 255)">&nbsp;</span><span style="color:rgb(0, 255, 255)">Truy Mộng</span><span style="color:rgb(255, 255, 0)">&nbsp;</span>&quot;<span style="font-family:arial,helvetica,sans-serif">&nbsp;, BQT&nbsp;xin gửi đến những sự kiện Khai Mở Server&nbsp;<span style="color:rgb(0, 255, 255)">&nbsp;</span></span></strong></span><strong><span style="color:rgb(0, 255, 255)">Nam Giang</span></strong><span style="font-size:14px"><strong><span style="font-family:arial,helvetica,sans-serif">&nbsp;hấp dẫn v&agrave; l&ocirc;i cuốn gồm những phần thưởng qu&yacute; gi&aacute; kh&ocirc;ng thể thiếu tr&ecirc;n con đường h&agrave;nh tẩu giang h&ocirc;̀...</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/CumMayChuGiang/NamGiang/TM.png" style="height:271px; width:503px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/CumMayChuGiang/NamGiang/TruyMong.gif" style="height:271px; line-height:1.6em; width:503px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:24px"><strong><span style="color:rgb(0, 0, 255)"><span style="font-family:arial,helvetica,sans-serif">&nbsp;</span></span><span style="color:rgb(0, 255, 255)"><span style="font-family:arial,helvetica,sans-serif">Sự Kiện Khai Mở Sever [ Nam Giang</span></span></strong></span><span style="font-size:24px"><strong><span style="color:rgb(0, 255, 255)"><span style="font-family:arial,helvetica,sans-serif">&nbsp; ]</span></span></strong></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:16px"><u><span style="font-family:arial,helvetica,sans-serif"><span style="color:rgb(255, 0, 0)">&nbsp;Nội Dung :</span></span></u></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:18px"><span style="color:rgb(255, 255, 0)"><strong><span style="font-family:arial,helvetica,sans-serif">Khai Mở Server Nam Giang&nbsp;v&agrave;o l&uacute;c 10h00 Ng&agrave;y 3/3/2015&nbsp;</span></strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:18px"><strong><span style="font-family:arial,helvetica,sans-serif"><u>Gồm những sự kiện ingame với những phần qu&agrave; hấp dẫn như sau :</u></span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">- Đua TOP Server :</span><span style="color:rgb(255, 255, 0)">&nbsp;&nbsp;</span><u><span style="font-size:20px"><a href="http://truymong.com/tin-tuc/dua-top-may-chu-nam-giang" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/dua-top-may-chu-nam-giang</span></a></span></u></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">- Khuyến M&atilde;i 100% Bất K&igrave; Mệnh Gi&aacute; &nbsp;: &nbsp;</span><u><span style="font-size:20px"><a href="http://truymong.com/tin-tuc/khuyen-mai-100-may-chu-nam-giang" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/khuyen-mai-100-may-chu-nam-giang</span></a></span></u></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">-&nbsp;Sắc Hoa Ng&agrave;y Cưới :</span><span style="color:rgb(255, 255, 0)">&nbsp;&nbsp;</span><u><span style="font-size:20px"><a href="http://truymong.com/tin-tuc/sac-hoa-ngay-cuoi-may-chu-nam-giang" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/sac-hoa-ngay-cuoi-may-chu-nam-giang</span></a></span></u></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">-&nbsp;Nạp Mốc Tích Lũy Nh&acirc;̣n Ngay Quà Khủng :</span><span style="color:rgb(255, 255, 0)">&nbsp; &nbsp;</span><span style="font-size:20px"><u><a href="http://truymong.com/tin-tuc/nap-moc-ti-ch-lu-y-nha-n-ngay-qua-khu-ng-nam-giang" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/nap-moc-ti-ch-lu-y-nha-n-ngay-qua-khu-ng-nam-giang</span></a></u></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<table border="1" cellpadding="1" cellspacing="1" style="line-height:20.7999992370605px; width:500px">\n	<tbody>\n		<tr>\n			<td colspan="2" style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Sự Kiện Khai Mở Sever</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">8 ng&agrave;y Cho Server Mới</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đầu Tư</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đăng Nhập 7 Ng&agrave;y&nbsp;</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Qu&agrave; T&acirc;n Thủ</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Qu&agrave; Nạp Lần Đầu</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Nạp Mỗi Ng&agrave;y</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">V&ograve;ng Quay&nbsp;</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">T&iacute;ch Lũy Nạp</span></span></strong></span></h4>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Để nhanh ch&oacute;ng gia tăng thực lực qu&yacute; bằng hữu&nbsp;n&ecirc;n nhanh ch&oacute;ng nắm bắt những sự kiện n&agrave;y nh&eacute; .</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="color:rgb(255, 0, 0)">*<span style="font-size:16px"><u>&nbsp;Lưu &Yacute;&nbsp;</u></span></span><span style="font-size:16px"><u>:&nbsp;</u></span>tham&nbsp;khảo trước những sự kiện khai mở để dễ nhận được phần thưởng</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></strong></p>\n\n<div>&nbsp;</div>\n', 'Thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-03-02 16:27:26', '2015-03-02 16:16:02', '0', ''),
(159, 3, '', 0, '', '0', 'Tính năng Tiên Kiếm', 'tinh-nang-tien-kiem', '<h2 style="text-align:center">T&Iacute;NH NĂNG TI&Ecirc;N KIẾM</h2>\n\n<div class="det" style="box-sizing: border-box; color: rgb(183, 183, 183); font-family: Tahoma, Geneva, sans-serif; font-size: 12px; line-height: 17.142858505249px; text-align: justify; background-color: rgb(42, 45, 55);">\n<p style="text-align:center"><span style="font-size:24px"><strong>Ti&ecirc;n Kiếm</strong></span></p>\n\n<p><strong>Ti&ecirc;n Kiếm</strong>&nbsp;&ndash; đ&uacute;ng như c&aacute;i t&ecirc;n của n&oacute;, l&agrave; những vũ kh&iacute; của thần ti&ecirc;n tồn tại h&agrave;ng ng&agrave;n năm trong nh&acirc;n gian. Hấp thụ biết bao tinh hoa nhật nguyệt, 7 c&acirc;y Ti&ecirc;n Kiếm giờ đ&acirc;y đ&atilde; đủ sức mạnh để thị uy tứ phương .</p>\n\n<p>Sỡ hửu Ti&ecirc;n Kiếm kh&ocirc;ng chỉ mang lại một lượng sức mạnh đ&aacute;ng kể m&agrave; c&ograve;n đem lại những kỹ năng &ldquo;độc nhất v&ocirc; nhị&rdquo; gi&uacute;p trấn &aacute;p quần h&ugrave;ng</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/bikiephuongdan/11.png" style="height:481px; width:649px" /></p>\n\n<p>Để sở hữu ch&uacute;ng người chơi cần thu thập c&aacute;c &nbsp;<strong>&ldquo;Kiếm Hồn&rdquo;</strong>&nbsp;v&agrave; sử dụng để mở lần lượt. Qua mỗi c&acirc;y kiếm được mở, lực chiến kh&ocirc;ng những gia tăng m&agrave; c&ograve;n thăng cấp hoặc th&ecirc;m những kỹ năng độc đ&aacute;o kh&aacute;c.</p>\n\n<p>3 Kỹ Năng từ Kiếm Ti&ecirc;n m&agrave; bạn c&oacute; thể mở lần lượt l&agrave;:</p>\n\n<p><img alt="" src="http://dl.satlong.com/photo/2014/12/01/9cf5a37957955fab7e7efda22f17bd99.png" style="border:0px; box-sizing:border-box; height:47px; max-width:100%; vertical-align:middle; width:43px" />&nbsp;<strong>C&aacute;t Liệt</strong>:&nbsp;khi c&ocirc;ng k&iacute;ch c&oacute; tỉ lệ cực lớn khiến đối phương trong 8s mất 4% HP (16% HP ở Lv.4)</p>\n\n<p><img alt="" src="http://dl.satlong.com/photo/2014/12/01/bc5bdbb356c58b3f2902470d7af94979.png" style="border:0px; box-sizing:border-box; height:45px; max-width:100%; vertical-align:middle; width:42px" />&nbsp;<strong>T&acirc;m Linh Chi Hỏa</strong>:&nbsp;khi c&ocirc;ng k&iacute;ch c&oacute; tỉ lệ khiến đối phương trong 5s giảm 5% ph&ograve;ng ngự (20% ở Lv.4)</p>\n\n<p><strong><img alt="" src="http://dl.satlong.com/photo/2014/12/01/2da971e573f0f2082de47a60fb545289.png" style="border:0px; box-sizing:border-box; height:42px; max-width:100%; vertical-align:middle; width:42px" />&nbsp;&Aacute;c Mộng Triền Th&acirc;n</strong>:&nbsp;khi c&ocirc;ng k&iacute;ch c&oacute; tỉ lệ khiến đối phương rơi v&agrave;o trạng th&aacute;i hỗn loạn trong 3s (5s ở lv.2)</p>\n</div>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 17:13:20', '2015-02-25 17:12:11', '0', '');
INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(160, 1, '', 0, '', '0', 'Gộp Server Tranh Bá Thiên Hạ TuyetSon', 'gop-server-tranh-ba-thien-ha-tuyetson', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng&nbsp;hữu.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/HangSon/GopSVTM.png" style="height:271px; width:503px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- &nbsp;Sau khi khai mở 2 Cụm m&aacute;y chủ đ&atilde; c&oacute; nhiều bằng&nbsp;hữu đ&atilde; tham gia nhiệt t&igrave;nh v&agrave; h&agrave;o hứng, để g&oacute;p phần tạo th&ecirc;m kh&ocirc;ng kh&iacute; tranh đua nhau BQT &quot; Truy Mộng&nbsp;&quot; Quyết định khai th&ocirc;ng giữa 2 M&aacute;y chủ &quot; Phật Sơn v&agrave; Tuyết Sơn &quot; c&ugrave;ng với việc Khai th&ocirc;ng sẽ c&oacute; th&ecirc;m Event mới - tạo kh&ocirc;ng kh&iacute; cuối th&aacute;ng v&agrave; chuẩn bị BIG UPDATE v&agrave;o Th&aacute;ng 3 sắp tới.</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; - &nbsp; Hy vọng tạo th&ecirc;m sự h&agrave;o hứng v&agrave; c&oacute; những trận đ&aacute;nh ngoạn mục giữa 2 cụm m&aacute;y chủ, liệu Tuyết Sơn c&oacute; khẳng định đc sức mạnh của m&igrave;nh để đ&aacute;nh bại được Phật Sơn hay kh&ocirc;ng ....&nbsp;</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center">&nbsp;&nbsp;<strong>&nbsp; &nbsp; &nbsp; &nbsp; M&aacute;y chủ Tuyết Sơn&nbsp;sẽ nhận được 1 Code 2 lv để ngang đẳng cấp với sever Phật Sơn ra trước</strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="color:#0000FF"><u><strong><span style="font-size:16px">CODE Tuyết Sơn : </span></strong></u></span><span style="color:#FFFF00"><u><strong><span style="font-size:16px">&nbsp;</span></strong></u><strong><span style="font-size:16px">&nbsp;&nbsp;</span></strong></span><span style="color:#00FFFF"><strong><span style="font-size:26px">GCTS063974B</span></strong></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>&nbsp;</strong></p>\n\n<p><strong><span style="color:rgb(255, 0, 0)"><u><span style="font-size:14px">Thời Gian Bảo Tr&igrave; Khai Th&ocirc;ng :</span></u></span>&nbsp;16h00 Ng&agrave;y 26/2/2015 đến 18h00 Ng&agrave;y 26/2/2015</strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - Gộp Truy Mộng S11 c&ugrave;ng với S8- S9 S10 nhằm tạo th&ecirc;m kh&ocirc;ng kh&iacute; h&agrave;o hứng đối với những bằng hữu cũ, đ&acirc;y l&agrave; dịp để c&aacute;c nh&acirc;n sĩ tranh đua với nhau 1 lần nữa thời gian như tr&ecirc;n&nbsp;</strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><span style="font-size:20px"><strong>EVENT Mới T&iacute;ch Lũy Cuối Th&aacute;ng 2&nbsp;</strong></span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:14px"><u>Nội dung&nbsp;:</u></span></span>&nbsp;Nạp mốc cộng dồn t&iacute;ch lũy trong 6 ng&agrave;y&nbsp;<span style="color:rgb(255, 0, 0)"><em>( Chỉ T&iacute;nh Mức Nạp V&agrave;o Kh&ocirc;ng T&iacute;nh Tiền Khuyến M&atilde;i X100% )</em></span></strong></p>\n\n<p><strong><span style="font-size:14px"><span style="color:rgb(0, 0, 255)"><u>Đối tượng tham gia&nbsp;</u>:&nbsp;</span></span>tất cả qu&yacute; đ&ocirc;̀ng đạo&nbsp; tham gia game Truy Mộng</strong></p>\n\n<p><strong><span style="font-size:14px"><span style="color:rgb(0, 0, 255)"><u>Thời gian sự kiện&nbsp;</u>:</span></span><span style="color:rgb(255, 255, 0)"><em>&nbsp;</em></span><em>10h00&nbsp;ng&agrave;y 26/02/2015 đến 10h00&nbsp;ng&agrave;y 4/3/2015</em></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/11.png" style="height:673px; width:568px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/12.png" style="height:785px; width:568px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/13.png" style="height:561px; width:568px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong>Ch&uacute;c qu&yacute; bằng&nbsp;hữu c&oacute; những gi&acirc;y ph&uacute;t vui vẻ với Game Truy Mộng.</strong></p>\n', 'Thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-03-12 09:26:57', '2015-02-25 17:46:58', '0', ''),
(161, 2, '', 0, '', '0', 'Tổng Kết Sự Kiện Đua TOP Máy Chủ Phật Sơn và Tuyết Sơn', 'tong-ket-su-kien-dua-top-may-chu-phat-son-va-tuyet-son', '<p><strong><span style="font-size:14px">Th&acirc;n ch&agrave;o qu&yacute;</span>&nbsp;<span style="font-size:14px">bằng hữu</span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="color:#FFFF00"><span style="font-size:16px"><strong>Do Tuyết Sơn đang trong thời gian đua Top ng&agrave;y 8 n&ecirc;n trao qu&agrave; cho Phật Sơn trước 10h00 ng&agrave;y 26/2 sẽ trao qu&agrave; Tuyết Sơn</strong></span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:14px">&nbsp; &nbsp; &nbsp;Sau 7&nbsp;ng&agrave;y khi&nbsp;M&aacute;y Chủ&nbsp;<span style="color:rgb(255, 0, 0)">Phật Sơn</span>&nbsp;khai mở&nbsp;đ&atilde; t&igrave;m ra đc&nbsp;20 Cao thủ&nbsp;v&otilde; l&acirc;m xuất ch&uacute;ng trong h&agrave;ng vạn nh&acirc;n sĩ trong&nbsp;M&aacute;y Chủ&nbsp;<span style="color:rgb(255, 0, 0)">&nbsp;Phật Sơn</span></span></strong></p>\n\n<p><strong><span style="font-size:14px">trong số 20 người c&oacute; 1&nbsp;người c&oacute; tố chất vượt bậc hơn hẳn l&agrave; &nbsp;</span></strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">☼IAmSorry☼</span></span><strong><span style="color:rgb(255, 0, 0)"><span style="font-size:18px">&nbsp;</span></span><span style="font-size:14px">với danh hiệu Top 1 Chiến Lực &amp;&nbsp;<span style="color:rgb(0, 0, 255)"><span style="font-size:18px">&nbsp;</span></span></span></strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Lốn&hearts;V&atilde;i&hearts;Sầu</span></span>&nbsp;<strong><span style="font-size:14px">Top 1 Đẳng Cấp Phật Sơn&nbsp;sau đ&acirc;y l&agrave; danh s&aacute;ch :</span></strong></p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align:center"><span style="color:rgb(0, 0, 255)"><span style="font-size:18px"><strong>DANH S&Aacute;CH TOP LỰC CHIẾN M&Aacute;Y CHỦ&nbsp;</strong></span></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Phật Sơn</span></span></strong></h1>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/PhatSon/LucChien.png" style="height:547px; width:1005px" /></p>\n\n<h1 style="text-align:center">&nbsp;</h1>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><strong><span style="font-size:18px">DANH S&Aacute;CH TOP ĐẲNG CẤP&nbsp;M&Aacute;Y CHỦ&nbsp;</span></strong></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Phật Sơn</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/PhatSon/DangCap.png" style="height:549px; width:1008px" /></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><strong><span style="font-size:16px">DANH S&Aacute;CH TOP SOÁI KHÍ&nbsp;M&Aacute;Y CHỦ&nbsp;</span></strong></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Phật Sơn</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/PhatSon/SoaiKhi.png" style="height:548px; width:713px" /></p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align:center"><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:16px">DANH S&Aacute;CH TOP KHUYNH QU&Ocirc;́C&nbsp;M&Aacute;Y CHỦ&nbsp;</span><span style="font-size:18px">Phật Sơn</span></span></strong></h1>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/PhatSon/KhuynhQuoc.png" style="height:547px; width:713px" /></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>Lưu &Yacute; :&nbsp;<span style="font-size:14px">Giải thưởng sẽ trao khi l&ecirc;n danh s&aacute;ch những người đạt điều kiện [ chậm trễ nhất l&agrave; 2 ng&agrave;y ]</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:14px">Sau 7&nbsp;ng&agrave;y khi&nbsp;M&aacute;y Chủ&nbsp;<span style="color:rgb(255, 0, 0)">Tuyết&nbsp;Sơn</span>&nbsp;khai mở&nbsp;đ&atilde; t&igrave;m ra đc&nbsp;20 Cao thủ&nbsp;v&otilde; l&acirc;m xuất ch&uacute;ng trong h&agrave;ng vạn nh&acirc;n sĩ trong&nbsp;M&aacute;y Chủ&nbsp;<span style="color:rgb(255, 0, 0)">&nbsp;Tuyết&nbsp;Sơn</span></span></strong></p>\n\n<p><span style="font-size:14px"><strong>trong số 20 người c&oacute; 1&nbsp;người c&oacute; tố chất vượt bậc hơn hẳn l&agrave; &nbsp;</strong><span style="color:#0000FF"><span style="font-size:18px">Ngọc-Nhi</span></span></span><strong><span style="color:rgb(255, 0, 0)"><span style="font-size:18px">&nbsp;</span></span><span style="font-size:14px">với danh hiệu Top 1 Chiến Lực &amp;&nbsp;<span style="color:rgb(0, 0, 255)"><span style="font-size:18px">&nbsp;</span></span></span></strong><span style="font-size:18px"><span style="color:rgb(0, 0, 255)">SkyDeCem</span></span>&nbsp;<strong><span style="font-size:14px">Top 1 Đẳng Cấp&nbsp;Phật Sơn&nbsp;sau đ&acirc;y l&agrave; danh s&aacute;ch :</span></strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align:center"><span style="color:rgb(0, 0, 255)"><span style="font-size:18px"><strong>DANH S&Aacute;CH TOP LỰC CHIẾN M&Aacute;Y CHỦ&nbsp;</strong></span></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Tuyết Sơn</span></span></strong></h1>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/Tuy%E1%BA%BFt%20S%C6%A1n/LC.png" style="height:548px; width:1012px" /></p>\n\n<h1 style="text-align:center">&nbsp;</h1>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><strong><span style="font-size:18px">DANH S&Aacute;CH TOP ĐẲNG CẤP&nbsp;M&Aacute;Y CHỦ&nbsp;</span></strong></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Tuyết Sơn</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/Tuy%E1%BA%BFt%20S%C6%A1n/DC.png" style="height:550px; width:1016px" /></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><strong><span style="font-size:16px">DANH S&Aacute;CH TOP SOÁI KHÍ&nbsp;M&Aacute;Y CHỦ&nbsp;</span></strong></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Tuyết Sơn</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/Tuy%E1%BA%BFt%20S%C6%A1n/SoaiKhi.png" style="height:550px; width:715px" /></p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align:center"><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:16px">DANH S&Aacute;CH TOP KHUYNH QU&Ocirc;́C&nbsp;M&Aacute;Y CHỦ&nbsp;</span><span style="font-size:18px">Tuyết Sơn</span></span></strong></h1>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/Tuy%E1%BA%BFt%20S%C6%A1n/KhuynhQuoc.png" style="height:550px; width:718px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>Ch&uacute;c qu&yacute; bằng hữu</strong><strong><span style="font-size:18px">&nbsp;</span>vui vẻ.</strong></p>\n', 'Thân chào quý bằng hữu', '0', 1, '0', '1970-01-01', '', '2015-03-07 08:59:18', '2015-02-25 19:48:14', '0', ''),
(162, 1, '', 0, '', '0', 'EVENT Mới Tích Lũy Đến 4/3/2015', 'event-moi-tich-luy-den-432015', '<p style="text-align:center"><u><strong><span style="font-size:20px"><span style="color:#0000FF">EVENT Mới T&iacute;ch Lũy Đến 4/3/2015</span></span></strong></u></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:22px"><span style="color:#FFFF00"><strong>K&eacute;o d&agrave;i thời gian x100% gi&aacute; trị nạp thẻ đến 3/3/2015</strong></span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:14px"><u>Nội dung&nbsp;:</u></span></span>&nbsp;Nạp mốc cộng dồn t&iacute;ch lũy trong 6 ng&agrave;y&nbsp;<span style="color:rgb(255, 0, 0)"><em>( Chỉ T&iacute;nh Mức Nạp V&agrave;o Kh&ocirc;ng T&iacute;nh Tiền Khuyến M&atilde;i X100% )</em></span></strong></p>\n\n<p><strong><span style="font-size:14px"><span style="color:rgb(0, 0, 255)"><u>Đối tượng tham gia&nbsp;</u>:&nbsp;</span></span>tất cả qu&yacute; bằng hữu&nbsp; tham gia game Truy Mộng</strong></p>\n\n<p><strong><span style="font-size:14px"><span style="color:rgb(0, 0, 255)"><u>Thời gian sự kiện&nbsp;</u>:</span></span><span style="color:rgb(255, 255, 0)"><em>&nbsp;</em></span><em>10h00&nbsp;ng&agrave;y 26/02/2015 đến 10h00&nbsp;ng&agrave;y 4/3/2015</em></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/11.png" style="height:673px; width:568px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/12.png" style="height:785px; width:568px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/13.png" style="height:561px; width:568px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:20px"><strong>Ch&uacute;c qu&yacute; bằng&nbsp;hữu c&oacute; những gi&acirc;y ph&uacute;t vui vẻ với Game Truy Mộng.</strong></span></p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-03-01 16:09:13', '2015-02-26 22:04:56', '0', ''),
(163, 1, '', 0, '', '0', 'Chuỗi Event Đặc Biệt [ Duy Nhất 1 Ngày 28/2/2015 ]', 'chuoi-event-dac-biet-duy-nhat-1-ngay-2822015-', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu..</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&ograve;n 1 ng&agrave;y duy nhất của th&aacute;ng 2 - BQT &quot; Truy Mộng &quot; Sẽ tạo 1 chuỗi Event để kết th&uacute;c Th&aacute;ng 2 chuẩn bị BIG Update lớn trong Th&aacute;ng 3 sắp tới với nhiều t&iacute;nh năng phong ph&uacute;, những vật phẩm khủng v&agrave; những chỉnh sửa để tạo 1 kh&ocirc;ng kh&iacute; mới d&agrave;nh cho qu&yacute; bằng hữu đang ngao du trong thế giới &quot; Truy Mộng &quot;</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><img alt="" src="/static/uploads/editor/Event%20Tet/TichLuyThang2/TM.png" style="height:271px; width:503px" /></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#00FFFF"><span style="font-size:18px"><u>1/ Event Tăng Th&ecirc;m 10% Gi&aacute; Trị Nạp Card D&agrave;nh Cho Mệnh Gi&aacute; Thẻ [ 100k - 200k - 300k - 500k ]</u></span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>D&agrave;nh cho to&agrave;n bộ Gamer trong thế giới &quot; Truy Mộng &quot;</strong></p>\n\n<p><strong><span style="color:#0000FF"><u><span style="font-size:16px">Thời gian Event :</span></u></span> Duy nhất trong ng&agrave;y 28/2 - Bắt đầu v&agrave;o l&uacute;c 10h00 đến 23h59</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#00FFFF"><span style="font-size:18px"><u>2/ Event Nạp Đ&uacute;ng Mốc Nhận Ngay Đồ Thần &amp; Bảo Thạch 5 :</u></span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>D&agrave;nh cho to&agrave;n bộ Gamer trong thế giới &quot; Truy Mộng &quot;</strong></p>\n\n<p><strong><span style="color:#0000FF"><span style="font-size:16px"><u>Thời gian Event :</u></span></span> Duy nhất trong ng&agrave;y 28/2 - Bắt đầu v&agrave;o l&ua