(179, 2, '', 0, '', '0', 'Đua Top Máy Chủ Tàng Long', 'dua-top-may-chu-tang-long', '<div><strong><span style="font-size:16px"><u>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu</u></span></strong></div>\n\n<div>\n<p>&nbsp;</p>\n\n<p style="text-align:justify"><strong><span style="font-size:14px">Nhằm hỗ trợ những phần thưởng hấp dẫn d&agrave;nh cho Qu&yacute; đ&ocirc;̀ng đạo&nbsp;th&ecirc;m nhiều thuận lợi tr&ecirc;n bước đường chinh chiến.&nbsp;</span></strong></p>\n\n<p style="text-align:justify"><strong><span style="color:rgb(0, 0, 255); font-size:14px">BQT Truy Mộng</span><span style="font-size:14px">&nbsp;xin ra mắt sự kiện&nbsp;</span><span style="color:rgb(255, 0, 0); font-size:14px"><u>Đua Top Server T&agrave;ng&nbsp;Long</u></span><span style="font-size:14px">&nbsp;d&agrave;nh cho tất cả c&aacute;c Qu&yacute; đ&ocirc;̀ng đạo&nbsp;&nbsp;đang ngao du trong thế giới&nbsp;</span><span style="color:rgb(0, 0, 255); font-size:14px">Truy Mộng</span></strong></p>\n\n<p style="text-align:justify"><strong><span style="font-size:14px">Sau khi đăng k&yacute; th&agrave;nh c&ocirc;ng tham gia&nbsp;<span style="color:rgb(0, 0, 255)">Truy Mộng</span>&nbsp;ngay, game thủ sẽ c&oacute; cơ hội sở hữu ngay phần thưởng hấp dẫn..</span></strong></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/CumMayChuGiang/truonggiang/1003TM.png" style="height:271px; width:503px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><u><span style="font-size:16px"><span style="color:rgb(255, 0, 0)">Đua Top Lực Chiến :</span></span></u></strong></p>\n\n<p><strong><u><span style="font-size:16px"><span style="color:rgb(255, 0, 0)">Điều kiện :</span></span></u></strong></p>\n\n<p>&nbsp;</p>\n\n<ul>\n	<li style="text-align:justify">\n	<p><strong><span style="font-size:14px">Top 01 đến top 10&nbsp;lực chiến tại Server&nbsp;<strong><span style="font-size:14px"><strong>T&agrave;ng&nbsp;Long&nbsp; -&nbsp;<span style="color:rgb(255, 255, 0)">Bắt đầu từ 19h00 ng&agrave;y 10/4/2015</span></strong></span></strong></span></strong></p>\n	</li>\n	<li style="text-align:justify">\n	<p><strong><span style="font-size:14px">Thời gian kết th&uacute;c v&agrave; ghi nhận đua TOP:<span style="color:rgb(255, 255, 0)">&nbsp;12h00, ng&agrave;y 17/4/2015</span></span></strong></p>\n	</li>\n</ul>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify"><strong><span style="font-size:16px"><u><span style="color:rgb(255, 0, 0)">PhầnThưởng Đưa Top Chiến Lực :</span></u></span></strong></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/_thumbs/2.png" style="height:533px; width:391px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/_thumbs/3.png" style="height:419px; width:390px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:16px"><u><span style="color:rgb(255, 0, 0)">Đua Top Đẳng Cấp :</span></u></span></strong></p>\n\n<p><strong><span style="font-size:16px"><u><span style="color:rgb(255, 0, 0)">Điều kiện :&nbsp;</span></u></span></strong></p>\n\n<p>&nbsp;</p>\n\n<ul>\n	<li style="text-align:justify">\n	<p><strong><span style="font-size:14px">Top 01 đến top 10 Đẳng cấp&nbsp;tại&nbsp;<strong>Server <strong><span style="font-size:14px"><strong><strong><strong><span style="font-size:14px"><strong><strong><strong><span style="font-size:14px"><strong><strong><strong><span style="font-size:14px"><strong><strong>T&agrave;ng&nbsp;Long&nbsp; -&nbsp;<span style="color:rgb(255, 255, 0)">Bắt đầu từ 19h00 ng&agrave;y 10/4/2015</span></strong></strong></span></strong></strong></strong></span></strong></strong></strong></span></strong></strong></strong></span></strong></strong></span></strong></p>\n	</li>\n	<li style="text-align:justify">\n	<p><strong><span style="font-size:14px">Thời gian kết th&uacute;c v&agrave; ghi nhận đua TOP:<span style="color:rgb(255, 255, 0)">&nbsp;12h00, ng&agrave;y 17/4/2015</span></span></strong></p>\n	</li>\n</ul>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p><strong><span style="font-size:16px"><span style="color:rgb(255, 0, 0)"><u><span style="font-family:arial">Phần Thưởng Đua Top Đẳng Cấp :</span></u></span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/_thumbs/TopDC.png" style="height:304px; width:308px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:18px"><span style="color:rgb(255, 255, 0)"><strong>Ch&uacute;c qu&yacute; bằng hữu&nbsp;T&agrave;ng&nbsp;Long&nbsp; Event vui vẻ. &nbsp;&nbsp;</strong></span></span></p>\n</div>\n\n<div>&nbsp;</div>\n', 'Thân chào quý bằng hữu', '0', 1, '0', '1970-01-01', '', '2015-04-09 17:23:59', '2015-03-09 19:30:58', '0', ''),
(139, 1, '', 0, '', '0', 'Hoàn Tất Gộp Máy Chủ Đón Tết Cực Lớn', 'hoa-n-ta-t-gop-may-chu-don-tet-cuc-lon', '<p><strong>Th&acirc;n chào quý bằng hữu.</strong></p>\n\n<p>&nbsp;</p>\n\n<h1><strong><img alt="" src="/static/uploads/editor/Xong%20B%E1%BA%A3o%20Tri.png" style="height:249px; width:448px" /></strong></h1>\n\n<p><strong>&nbsp;&nbsp;</strong></p>\n\n<p><strong>&nbsp; &nbsp; &nbsp;Sau 2 ti&ecirc;́ng, BQT đã&nbsp;<span style="color:rgb(255, 255, 0)">G&ocirc;̣p 5 Cụm máy chủ Truy M&ocirc;̣ng &nbsp;thành c&ocirc;ng</span>&nbsp;- chúc quý bằng hữu có những gi&acirc;y phút trải nghi&ecirc;̣m game th&acirc;̣t vui vẻ và có những tr&acirc;̣n pk lớn với những thành vi&ecirc;n mới của 5 Cụm máy chủ g&ocirc;̣p lại nhé.</strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#FFFF00"><span style="font-size:16px">M&aacute;y Chủ 8- 9- 10 Ho&agrave;n Tất Bảo Tr&igrave;</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Hi&ecirc;̣n tại đang có&nbsp;<span style="color:rgb(255, 255, 0)">Event Khuy&ecirc;́n mãi 100% b&acirc;́t kì m&ecirc;̣nh giá&nbsp;</span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>- Thời gian :&nbsp;<span style="color:rgb(255, 255, 0)">16h ngày 13/2&nbsp;đ&ecirc;́n 23h59 ngày 17/2/2015</span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Chúc quý bằng hữu vui vẻ, đừng qu&ecirc;n chuỗi Event tết nh&eacute;</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align: center;"><span style="font-size:18px"><a href="http://truymong.com/su-kien/chuoi-event-tet-vui-cung-truy-mong" target="_blank"><span style="color:#FFFF00"><strong>http://truymong.com/su-kien/chuoi-event-tet-vui-cung-truy-mong</strong></span></a></span></p>\n', '', '0', 0, '0', '1970-01-01', '', '2015-02-13 16:13:25', '2015-02-13 15:55:34', '0', ''),
(146, 3, '', 0, '', '0', 'Cách xây dựng kỹ năng cho PET', 'cach-xay-dung-ky-nang-cho-pet', '<p><span style="background-color:rgb(230, 206, 153); color:rgb(213, 29, 16); font-family:tahoma,geneva,sans-serif; font-size:26px">C&aacute;ch x&acirc;y dựng kỹ năng cho PET</span></p>\n\n<p><strong><span style="font-size:medium"><em>Đ&atilde; từ l&acirc;u Ti&ecirc;n Sủng l&agrave; 1 đồng h&agrave;nh kh&ocirc;ng thể thiếu ch&uacute;ng ta tr&ecirc;n con đường h&agrave;nh tẩu giang hồ ở Truy Mộng. Nhưng để x&acirc;y dựng một bộ kĩ năng như thế n&agrave;o để hiệu quả trong chiến đấu th&igrave; đ&oacute; l&agrave; cả một vấn đề kh&aacute;c. H&ocirc;m nay m&igrave;nh xin mạn ph&eacute;p d&ugrave;ng ch&uacute;t kinh nghiệm &iacute;t ỏi của c&aacute;c&nbsp;&quot;cao thủ&nbsp;&quot; g&oacute;p v&agrave;i &yacute; kiến nhỏ nhoi để cho c&aacute;c vị huynh đ&agrave;i kham khảo th&ecirc;m cũng như xin được thỉnh gi&aacute;o c&aacute;c vị &quot;cao nh&acirc;n mai danh ẩn t&iacute;ch tr&ecirc;n n&uacute;i&quot; bấy l&acirc;u nay.</em></span></strong></p>\n\n<p><br />\nĐầu ti&ecirc;n, m&igrave;nh xin ph&eacute;p n&oacute;i sơ qua về bộ kĩ năng của Ti&ecirc;n Sủng nh&eacute;.</p>\n\n<p><strong>&raquo; Thi&ecirc;n Ph&uacute;:</strong>&nbsp;gồm 2 kĩ năng thi&ecirc;n ph&uacute; đặc trưng l&agrave; Huyễn Vựng(Cho&aacute;ng) v&agrave; Hỏa Vũ. Hai kĩ năng n&agrave;y l&agrave; đặc trưng duy nhất chỉ c&oacute; ở Tiểu Hồ Ti&ecirc;n v&agrave; Huyết Ảnh C&ugrave;ng K&igrave;. (tất nhi&ecirc;n ở đ&acirc;y m&igrave;nh kh&ocirc;ng n&oacute;i tới c&aacute;c trường hợp Dung Hợp Ti&ecirc;n Sủng nh&eacute;)</p>\n\n<p><strong>&raquo; Bị Động:</strong>&nbsp;gồm 7 kĩ năng vừa kh&iacute;t cho 7 &ocirc;, c&aacute;i n&agrave;y chỉ phụ thuộc v&agrave;o thứ tự kĩ năng m&agrave; bạn muốn học kĩ năng sao cho ph&ugrave; hợp với ph&aacute;i m&agrave; m&igrave;nh chơi hoặc sở th&iacute;ch nữa&nbsp;</p>\n\n<p><strong>&raquo; Chủ Động:</strong>&nbsp;gồm 7 kĩ năng tương ứng cho 4 &ocirc;. Tới đ&acirc;y mới ch&iacute;nh thức bắt đầu c&aacute;i qu&atilde;ng đường gian khổ đ&acirc;y. .. ... ... xuống dưới sẽ ph&acirc;n t&iacute;ch chi tiết nh&eacute; (^_&#39;\\\\)<br />\n<br />\n<strong><em>Trước khi v&agrave;o m&oacute;n ch&iacute;nh, xin ph&eacute;p tr&aacute; h&igrave;nh nhẹ m&oacute;n khai vị cho c&aacute;c huynh đ&agrave;i xem nh&eacute;</em></strong></p>\n\n<p style="text-align:center"><br />\n<strong><img alt="" src="http://i.imgur.com/QHBBmDB.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /><br />\n<img alt="" src="http://i.imgur.com/bPKvd4M.jpg" style="border:medium none; margin:0px; padding:0px; width:650px" /><br />\n<br />\nĂn khai vị đủ chưa n&agrave;o? Dọn m&oacute;n ch&iacute;nh th&ocirc;i chứ... ^.^</strong></p>\n\n<p><br />\n<span style="font-size:medium">Thoạt nh&igrave;n 3 &quot;p&eacute;&quot; Ti&ecirc;n Sủng c&oacute; lực chiến cao nhất nh&igrave; ba ở cụm m&aacute;y chủ 1 (1-2-3-4) th&igrave; họ kh&aacute; nghi&ecirc;ng về hướng tấn c&ocirc;ng khi d&ugrave;ng 2/4 kĩ năng chủ động l&agrave; M&atilde;nh K&iacute;ch, Độc Nha hoặc Lợi Nhẫn k&egrave;m theo 1 kĩ năng chủ động nghi&ecirc;ng về ph&iacute;a phụ trợ nhưng Ph&aacute; Gi&aacute;p v&agrave; Hồi Xu&acirc;n. C&ograve;n về ph&iacute;a cụm m&aacute;y chủ 2 th&igrave; c&oacute; phần hơi kh&aacute;c biệt t&yacute;, v&igrave; họ chọn kĩ năng nghi&ecirc;ng về ph&iacute;a phụ trợ cho chủ nh&acirc;n l&agrave; ch&iacute;nh. C&oacute; thể thấy ở cụm 2, cả 3 người đều kh&ocirc;ng d&ugrave;ng Độc Nha.&nbsp;</span><br />\n<span style="font-size:medium">Về phần&nbsp;<strong>Thi&ecirc;n Ph&uacute;</strong>. ch&uacute;ng ta c&oacute; 2 kĩ năng Thi&ecirc;n Ph&uacute; l&agrave; :&nbsp;<img alt="" src="http://i.imgur.com/BSNMShQ.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;v&agrave;&nbsp;<img alt="" src="http://i.imgur.com/EtrhOfM.jpg" style="border:medium none; margin:0px; padding:0px" />. Hỏa Vũ th&igrave; cho ta 1 lượng s&aacute;t thương tr&ecirc;n diện rộng kh&aacute; tốt, th&iacute;ch hợp cho việc đ&aacute;nh boss hoặc dọn qu&aacute;i kh&aacute; tốt. Mặt kh&aacute;c Huyễn Vực l&agrave; 1 kĩ năng kh&aacute; lợi hại trong việc PvP 1v1, 1 kĩ năng cho&aacute;ng ( tr&oacute;i buộc) từ nh&acirc;n vật ch&iacute;nh, sau đ&oacute; bồi th&ecirc;m 1 c&uacute; cho&aacute;ng từ Ti&ecirc;n Sủng... 6s bị khống chế, 6s &quot;nằm hưởng thụ&quot; h&agrave;ng t&aacute; s&aacute;t thương từ kẻ th&ugrave;. C&oacute; thể n&oacute;i đ&acirc;y l&agrave; kĩ năng tốt nhất nh&igrave; trong tất cả kĩ năng.</span></p>\n\n<p><span style="font-size:medium">Về Nhược Điểm của 2 loại thi&ecirc;n ph&uacute; n&agrave;y th&igrave; c&oacute; thể n&oacute;i những g&igrave; khuyết của kĩ năng n&agrave;y cũng ch&iacute;nh l&agrave; điểm ưu của kĩ năng kia. Trong khi Hỏa Vũ kh&aacute; dễ c&oacute; được th&ocirc;ng qua Ti&ecirc;n Sủng Huyết Ảnh C&ugrave;ng K&igrave; mua từ Thương Th&agrave;nh, ngược lại Huyễn Vực lại nằm ở Tiểu Hồ Ti&ecirc;n, muốn kiếm được &quot;p&eacute;&quot; n&agrave;y th&igrave; kh&ocirc;ng dễ t&yacute; n&agrave;o, n&oacute;i theo gian hồ thường n&oacute;i l&agrave; &quot;Chăm Chỉ Quay Tay, Vận May Sẽ Tới&quot; =3= Hỏa Vũ th&igrave; mạnh về dọn qu&aacute;i nhưng yếu về 1v1, Huyễn Vựng th&igrave; mạnh về 1v1 nhưng lại l&eacute;p vế ở khoảng dọn qu&aacute;i nhanh.</span><br />\n<br />\n<br />\n<strong>Xong m&oacute;n ch&iacute;nh rồi, giờ ta đi tới m&oacute;n tr&aacute;ng miệng chứ =^.^=<br />\n<br />\nM&igrave;nh xin mạn ph&eacute;p đưa ra 1 v&agrave;i c&aacute;ch x&acirc;y dựng kĩ năng cho từng lối chơi của mỗi người.<br />\n<br />\n<span style="font-size:medium"><u>1. Đa Dụng:</u><br />\n<br />\n<img alt="" src="http://i.imgur.com/323JbmT.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Độc Nha.<br />\n<br />\n<img alt="" src="http://i.imgur.com/m4xVF5O.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Hồi Xu&acirc;n.<br />\n<br />\n<img alt="" src="http://i.imgur.com/j8OBazx.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;M&atilde;nh K&iacute;ch.<br />\n<br />\n<img alt="" src="http://i.imgur.com/pmE29vV.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Lợi Nhẫn.<br />\n<br />\nĐặc Điểm:&nbsp;</span></strong><span style="font-size:medium">khả năng phục hồi c&oacute;, r&uacute;t m&aacute;u theo thời gian cũng c&oacute;, tăng s&aacute;t thương cho Ti&ecirc;n Sủng lẫn chủ nh&acirc;n. C&oacute; thể n&oacute;i, c&aacute;ch x&acirc;y dựng n&agrave;y kh&aacute; ổn cho c&aacute;c vị huynh đ&agrave;i mới bước ch&acirc;n h&agrave;nh tẩu giang hồ.</span><br />\n<br />\n<strong><span style="font-size:medium"><u>2. Tấn C&ocirc;ng:</u><br />\n<br />\n<img alt="" src="http://i.imgur.com/j8OBazx.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;M&atilde;nh K&iacute;ch.<br />\n<br />\n<img alt="" src="http://i.imgur.com/MCkHR7Q.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Ph&aacute; Gi&aacute;p.<br />\n<br />\n<img alt="" src="http://i.imgur.com/pmE29vV.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Lợi Nhẫn.<br />\n<br />\n<img alt="" src="http://i.imgur.com/323JbmT.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Độc Nha.<br />\n<br />\nĐặc điểm:&nbsp;</span></strong><span style="font-size:medium">Hướng x&acirc;y dựng kĩ năng n&agrave;y nghi&ecirc;ng ho&agrave;n to&agrave;n về tấn c&ocirc;ng, kh&aacute; th&iacute;ch hợp cho c&aacute;c vị huynh đ&agrave;i th&iacute;ch &quot;kh&ocirc; m&aacute;u&quot; - &quot;m&ocirc;t sống một c&ograve;n&quot; với kẻ thủ của m&igrave;nh. Ọ_Ọ</span><br />\n<br />\n<strong><span style="font-size:medium"><u>3. Ph&ograve;ng Thủ:</u><br />\n<br />\n<img alt="" src="http://i.imgur.com/m4xVF5O.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Hồi Xu&acirc;n.<br />\n<br />\n<img alt="" src="http://i.imgur.com/xWUSVXC.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Suy Nhược.<br />\n<br />\n<img alt="" src="http://i.imgur.com/5tbVh7W.jpg" style="border:medium none; margin:0px; padding:0px" />&nbsp;Ki&ecirc;n Gi&aacute;p.<br />\n<br />\nKĩ năng thứ 4 c&oacute; thể thay đổi t&ugrave;y sở th&iacute;ch.<br />\n<br />\nĐặc điểm:&nbsp;</span></strong><span style="font-size:medium">lối x&acirc;y dựng n&agrave;y kh&aacute; hợp c&aacute;c huynh đ&agrave;i ở ph&aacute;i Kiếm Linh, tăng độ &quot;tr&acirc;u b&ograve;&quot; của m&igrave;nh l&ecirc;n c&ugrave;ng với bộ kĩ năng &quot;Lao tới&quot;, &quot;K&eacute;o về&quot; l&agrave;m đối thủ kh&aacute; kh&oacute; chịu.</span></p>\n\n<p>&nbsp;</p>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:10:54', '2015-02-25 16:10:54', '0', ''),
(147, 3, '', 0, '', '0', 'Hướng dẫn Đua Phi Kiếm', 'huong-dan-dua-phi-kiem', '<div class="content1_text_title" style="margin: 0px 0px 15px; padding: 15px 0px; float: left; width: 645px; color: rgb(213, 29, 16); font-size: 26px; text-align: center; font-family: Tahoma, Geneva, sans-serif; line-height: 20px; background: url(http://bathiendia.com/static/templates/frontend/home/images/new_line.png) 50% 100% no-repeat rgb(230, 206, 153);">Hướng dẫn Đua Phi Kiếm</div>\n\n<div class="postrow has_after_content" style="margin: 0px; padding: 0px;">\n<div class="content" style="margin: 0px; padding: 0px;">\n<div id="post_message_520" style="margin: 0px; padding: 0px;">\n<blockquote><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Xin Ch&agrave;o C&aacute;c ACE&nbsp;<br />\nH&ocirc;m nay m&igrave;nh mạn ph&eacute;p giới thiệu về hoạt động Ngự Kiếm Thu Danh Sơn một hoạt động hằng ng&agrave;y trong game để gi&uacute;p ace c&oacute; được g&oacute;c nh&igrave;n to&agrave;n vẹn về hoạt động n&agrave;y nh&oacute;e!!<br />\nĐầu tiền l&agrave; thời gian diễn ra hoạt động:&nbsp;<strong>Bắt đầu: 15:00 - Kết Th&uacute;c: 15:30</strong><br />\nKhi tới thời gian hoạt động diễn ra sẽ c&oacute; v&eacute; mời bạn v&agrave;o trực tiếp trong ph&oacute; bản hoạt động</span></span></span></blockquote>\n\n<blockquote>&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/CKbAVY3.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span></div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Bạn c&oacute; thể kick nhận ngay để&nbsp;<strong>&quot;Nhận Lu&ocirc;n&quot;</strong>&nbsp;phần thưởng của hoạt động m&agrave; ko cần phải tham gia tất nhi&ecirc;n sẽ tốn 20 KNB nh&eacute;, hoặc kick&nbsp;<strong>&quot;V&agrave;o Lu&ocirc;n&quot;</strong>&nbsp;để v&agrave;o trỗ t&agrave;i đua xe ak nhầm đua kiếm lụa l&agrave;<br />\n<br />\nTrước giờ G c&oacute; rất nhiều cao thủ kiếm lụa hừng hực kh&iacute; thế sẵn s&agrave;ng lao v&agrave;o cuộc đua t&agrave;ng khốc&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/72.gif" style="border:medium none; margin:0px; padding:0px" title="72" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/72.gif" style="border:medium none; margin:0px; padding:0px" title="72" /></span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/d14DDI2.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></span></span><br />\n<br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Trong hoạt động bạn sẽ kh&ocirc;ng d&ugrave;ng được kĩ năng mặc định của nh&acirc;n vật m&agrave; sẽ được kick hoạt bộ kĩ năng ri&ecirc;ng của phụ bản để dễ d&agrave;ng chinh phục đỉnh cao</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;">&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><strong>Kĩ năng 1:</strong>&nbsp;Đẩy đẩy đẩy tăng tốc độ di chuyển 1 lần nhấp tăng 20 tốc, cơ m&agrave; kiếm cũng như người đẩy nhiều qu&aacute; cũng mau đuối khi qu&aacute; tải l&ecirc;n tới 100% th&igrave; tốc độ sẽ giảm về c&ograve;n 100 l&agrave;m bạn di chuyển cực chậm đến khi qu&aacute; tải về 0% th&igrave; trở lại b&igrave;nh thường</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/hU98Svq.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span><br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><strong>Kĩ năng 2:</strong>&nbsp;Jumper như t&ecirc;n n&oacute; gi&uacute;p bạn nhảy qua c&aacute;c chướng ngại vật tr&ecirc;n đường.</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/HdE88YE.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: left;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Một pha nhảy cực chuẩn sẽ gi&uacute;p bạn vượt qua đối thủ, kỉ năng nhảy kh&aacute; l&agrave; quan trọng trong hoạt động n&agrave;y bạn thể d&ugrave;ng ph&iacute;m space để thay cho việc bấm số 2</span></span></span><br />\n&nbsp;</div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/MI8Nxjp.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></span></span><br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><strong>Kĩ Năng 3:</strong>&nbsp;Cho&aacute;ng c&aacute;i n&agrave;y th&acirc;m l&eacute;m n&egrave;, t&ecirc;n n&agrave;o d&aacute;m vượt m&igrave;nh đi trước kick v&agrave;o n&oacute; v&agrave; b&ugrave;m đứng y&ecirc;n tại chỗ 3s gi&uacute;p bạn dễ d&agrave;ng vượt qua</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;">\n<div style="margin: 0px; padding: 0px;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/MHQ0jwb.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span><br />\n<br />\n&nbsp;</div>\n\n<div style="margin: 0px; padding: 0px; text-align: left;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Khi bạn kh&ocirc;ng thể vượt qua đối thủ th&igrave; h&atilde;y ngăn lại bằng c&aacute;ch cho&aacute;n đối thủ để gi&uacute;p bạn c&oacute; thể vượt qua dễ d&agrave;ng hơn</span></span></span><br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/1FQXygS.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></span></span><br />\n<br />\n<br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Ngo&agrave;i 3 Kĩ năng mặc định của ph&oacute; bản c&ograve;n c&oacute; 3 &ocirc; trống gồm 4 5 &amp; 6, 3 &ocirc; trống n&agrave;y sẽ cho bạn th&ecirc;m kĩ năng bằng c&aacute;ch lấy c&aacute;c hộp qu&agrave; dấu hỏi dọc đường</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;">&nbsp;</div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Trong c&aacute;c hộp qu&agrave; bạn sẽ nhận được ngẫu nhi&ecirc;n c&aacute;c kĩ năng kh&aacute;c nhau</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/kGIuRbB.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span><br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Kĩ năng v&ocirc; địch gi&uacute;p bạn loại bỏ hết tất cả c&aacute;c kĩ năng từ người choi kh&aacute;c nhằm v&agrave;o bạn trong 3s, Like A Boss</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/s3LQMED.png" style="border:medium none; margin:0px; padding:0px" /><br />\n<br />\n<img alt="" src="http://i.imgur.com/KO7O0yt.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span><br />\n<br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Kĩ năng cực tốc gi&uacute;p bạn tăng tốc 30% tốc độ di chuyển trong 5 gi&acirc;y m&agrave; kh&ocirc;ng ảnh hưởng tới qu&aacute; tải, đ&acirc;y l&agrave; 1 trong những kĩ năng lợi thế cho cuộc đua 5s l&agrave;m n&ecirc;n tất cả chủ yếu bạn c&oacute; nhanh tay hay kh&ocirc;ng&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/117.gif" style="border:medium none; margin:0px; padding:0px" title="117" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/117.gif" style="border:medium none; margin:0px; padding:0px" title="117" /></span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;">\n<div style="margin: 0px; padding: 0px;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/2g5JFNB.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span><br />\n<br />\n&nbsp;</div>\n\n<div style="margin: 0px; padding: 0px; text-align: left;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Chuối ăn th&igrave; rất ngon rồi khỏi phải n&oacute;i c&ograve;n vỏ của n&oacute; th&igrave; bạn biết c&ocirc;ng dụng r&ugrave;i&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/192.gif" style="border:medium none; margin:0px; padding:0px" title="192" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/monkeyhot/192.gif" style="border:medium none; margin:0px; padding:0px" title="192" />&nbsp;</span></span></span><br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/pICEvKG.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: left;">&nbsp;</div>\n</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Nếu đối thủ hay bạn dẫm phải vỏ chuối bất k&igrave; th&igrave; sẽ bị l&agrave;m chậm chạy như con r&ugrave;a khi đ&oacute; cơ hội cho bạn hoặc đối thủ vượt qua rất cao</span></span></span>\n\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/2NgU2md.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></span></span></div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Hồi nhỏ coi phim rất ghiền tuyết, giờ v&agrave;o đ&acirc;y được n&eacute;m tuyết rồi n&egrave;&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/82.gif" style="border:medium none; margin:0px; padding:0px" title="82" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/82.gif" style="border:medium none; margin:0px; padding:0px" title="82" /></span></span></span><br />\n<br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/I9SqE5D.png" style="border:medium none; margin:0px; padding:0px" /></span><br />\n&nbsp;</div>\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Đ&oacute;ng băng l&agrave; t&ecirc;n của kĩ năng n&agrave;y, như t&ecirc;n gọi n&oacute; gi&uacute;p bạn đ&oacute;ng băng bất k&igrave; đối thủ n&agrave;o đang hiện diện tr&ecirc;n map, kĩ năng n&agrave;y kh&ocirc;ng cố định muộc ti&ecirc;u n&ecirc;n khi lượm được n&oacute; th&igrave; cứ x&agrave;i t&ecirc;n n&agrave;o xui hưởng r&aacute;ng chịu&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/121.gif" style="border:medium none; margin:0px; padding:0px" title="121" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/121.gif" style="border:medium none; margin:0px; padding:0px" title="121" /></span></span></span><br />\n<br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><img alt="" src="http://i.imgur.com/FdysQPJ.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Khi bạn gần qu&aacute; tải khi tăng tốc qu&aacute; nhiều bạn sẽ phải đi chậm lại như thế sẽ l&agrave;m gi&uacute;p cho đối thủ vượt bạn dễ d&agrave;ng vậy đ&acirc;u l&agrave; giải ph&aacute;p</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/Df1wWK3.png" style="border:medium none; margin:0px; padding:0px" /></span></span></span></div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Ho&aacute;n Linh l&agrave; một kĩ năng gi&uacute;p cho bạn giảm 30% qu&aacute; tải, vậy cố gắng lượm nhiều hộp b&iacute; ẩn để ra được nhiều ho&aacute;n linh gi&uacute;p ban c&oacute; thể tăng tốc li&ecirc;n tục tr&ecirc;n đường đua m&agrave; kh&ocirc;ng sợ bị ch&aacute;y kh&eacute;t&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/41.gif" style="border:medium none; margin:0px; padding:0px" title="41" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/41.gif" style="border:medium none; margin:0px; padding:0px" title="41" /><br />\n<br />\nP/s: Cuộc đua c&oacute; 2 v&ograve;ng nh&eacute; c&aacute;c bạnm khi về đ&iacute;ch v&ograve;ng 1 bạn sẽ được tự động đưa về vị tr&iacute; xuất ph&aacute;t để bắt đầu cuộc đua thứ 2 vậy n&ecirc;n đừng thấy về đ&iacute;ch v&ograve;ng 1 r&ugrave;i th&igrave; thả đ&oacute; nh&eacute;!!!!!<br />\n<br />\nC&ugrave;ng khả năng click chuột tốc độ kết hợp với những kĩ năng b&aacute; đạo gi&uacute;p bạn về đ&iacute;ch c&agrave;ng nhanh c&agrave;ng top cao c&agrave;ng nhận được nhiều kinh nghiệm v&agrave; Ho&aacute;n Ma Ph&ugrave; Lam</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/g9ukSaJ.png" style="border:medium none; margin:0px; padding:0px; width:650px" /><br />\n<br />\n<br />\n<img alt="" src="http://i.imgur.com/6qYIs0l.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></span></span></div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Với những ai chưa biết th&igrave; Ho&aacute;n Ma Ph&ugrave; Lam gi&uacute;p bạn t&igrave;m Ma Hồn T&iacute;m trong Thẻ b&agrave;i, 1 Ho&aacute;n Ma Ph&ugrave; Lam tương đương 1 lần quay tốn 10 KNB nh&eacute;, vậy cố gắng về đ&iacute;ch c&agrave;ng sớm c&agrave;ng được nhiều ph&ugrave;...</span></span></span><br />\n&nbsp;\n<div style="margin: 0px; padding: 0px; text-align: center;"><span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma"><img alt="" src="http://i.imgur.com/9G3fLrA.png" style="border:medium none; margin:0px; padding:0px; width:650px" /></span></span></span><br />\n&nbsp;</div>\n<br />\n<span style="background-color:rgb(230, 206, 153); color:rgb(0, 0, 0); font-family:tahoma,geneva,sans-serif; font-size:12px"><span style="font-size:medium"><span style="font-family:tahoma">Cảm ơn mọi người đ&atilde; v&agrave; tham khảo b&agrave;i viết của m&igrave;nh, nếu thấy hay th&igrave; cho m&igrave;nh 1 c&aacute;i thanks nồng &aacute;i nh&oacute;e !!!!&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/69.gif" style="border:medium none; margin:0px; padding:0px" title="69" />&nbsp;<img alt="" class="inlineimg" src="http://forums.bathiendia.com/images/bathien/smiles/tzuki/69.gif" style="border:medium none; margin:0px; padding:0px" title="69" /></span></span></span></blockquote>\n</div>\n</div>\n</div>\n', '', '0', 1, '0', '1970-01-01', '', '2015-02-25 16:16:56', '2015-02-25 16:13:55', '0', ''),
(176, 2, '', 0, '', '0', 'Sắc Hoa Ngày Cưới Máy Chủ Ngọa Long', 'sac-hoa-ngay-cuoi-may-chu-ngoa-long', '<h3><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu</strong></h3>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; Tr&ecirc;n con đường ngao du ti&ecirc;n cảnh, h&agrave;nh hiệp giang hồ đa phần c&aacute;c hảo h&aacute;n đều muốn độc bước chinh phục tất cả những điều b&iacute; ẩn. nhưng cũng sẽ c&oacute; 1 số hảo h&aacute;n muốn c&ugrave;ng&nbsp;<span style="color:rgb(255, 255, 0)">tri kỉ&nbsp;</span>kh&aacute;m ph&aacute; những chốn ti&ecirc;n bồng, ảo diệu của tạo h&oacute;a nay&nbsp;<span style="color:rgb(255, 255, 0)">BQT muốn tạo th&ecirc;m sự gắn kết những đ&ocirc;i uy&ecirc;n ương &nbsp;</span>bằng 1 Event &quot;&nbsp;<span style="color:rgb(255, 255, 0)">Tặng Hoa Ng&agrave;y Cưới</span>&nbsp;&quot; kết nối y&ecirc;u thương.</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp;6 đại hi&ecirc;̣p xu&acirc;́t sắc nh&acirc;́t sẽ giành đc những ph&acirc;̀n quà giá trị nh&acirc;́t trong Event &quot; Tặng Hoa Ngày Cưới &quot;.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/CumMayChuGiang/NamGiang/SacHoaTM.png" style="height:271px; width:503px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:14px"><u><span style="color:rgb(255, 255, 255)">Nội Dung : Event sẽ t&iacute;nh dựa tr&ecirc;n :</span></u></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>-&nbsp;<span style="color:rgb(255, 255, 0)">Top 3&nbsp;người</span>&nbsp;c&oacute;&nbsp;số điểm tổng kết cao nhất&nbsp;ở Khuynh Quốc</strong></p>\n\n<p><strong>-&nbsp;<span style="color:rgb(255, 255, 0)">Top 3&nbsp;người&nbsp;</span>c&oacute;&nbsp;số điểm&nbsp;tổng kết cao nhất&nbsp;ở So&aacute;i Kh&iacute;&nbsp;</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="font-size:20px"><span style="color:rgb(255, 255, 0)">&nbsp; &nbsp; [ H&igrave;nh ảnh v&iacute; dụ ]</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><img src="http://truymong.com/static/uploads/editor/_thumbs/Images/_thumbs/tanghoangaycuoi2.png" /></strong></p>\n\n<p style="text-align:center"><strong><img src="http://truymong.com/static/uploads/editor/_thumbs/Images/_thumbs/tanghoangaycuoi1.bmp" /></strong></p>\n\n<p style="text-align:center"><strong><img src="http://truymong.com/static/uploads/editor/_thumbs/Images/_thumbs/tanghoangaycuoi3.bmp" /></strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:14px"><u><span style="color:rgb(255, 255, 255)">Thời gian :</span></u></span>&nbsp;bắt đầu&nbsp;<span style="color:rgb(255, 255, 0)">10h00</span>&nbsp;Ng&agrave;y<span style="color:rgb(0, 0, 255)">&nbsp;</span><span style="color:rgb(255, 255, 0)">4/4/2015</span>&nbsp;v&agrave; kết th&uacute;c v&agrave;o l&uacute;c&nbsp;<span style="color:rgb(255, 255, 0)">12h00</span>&nbsp;ng&agrave;y&nbsp;<span style="color:rgb(255, 255, 0)">11/4/2015</span></strong></p>\n\n<p><strong><span style="font-size:14px"><u><span style="color:rgb(255, 255, 255)">Phần Thưởng&nbsp;</span></u></span>: Top của So&aacute;i Kh&iacute; v&agrave; Khuynh Quốc tương đương</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><img src="http://truymong.com/static/uploads/editor/TopS1%20%2B%20S2/toptanghoas4.png" /></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:18px"><span style="color:rgb(255, 255, 0)"><strong>Ch&uacute;c qu&yacute; bằng hữu Ngọa Long&nbsp;vui vẻ.</strong></span></span></p>\n\n<div>&nbsp;</div>\n', 'Thân chào quý bằng hữu', '0', 1, '0', '1970-01-01', '', '2015-04-04 08:48:07', '2015-03-09 19:25:34', '0', ''),
(142, 1, '', 0, '', '0', 'Khai Mở Code Lì Xì & Vòng Quay Nam Mới', 'khai-mo-code-li-xi-vong-quay-nam-moi', '<p><span style="font-size:16px"><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/nammoi.gif" style="height:250px; width:540px" /></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nh&acirc;n dịp năm mới BQT &quot; <span style="color:#FFFF00"><span style="font-size:18px">Truy Mộng</span></span> &quot; xin gởi lời cảm ơn ch&acirc;n th&agrave;nh c&ugrave;ng lời ch&uacute;c đến to&agrave;n thể c&aacute;c bằng hữu đ&atilde; v&agrave; đang ngao du trong thế giới tuyệt diệu n&agrave;y 1 năm mới c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave;, người th&acirc;n, người y&ecirc;n lu&ocirc;n lu&ocirc;n gặp nhiều may mắn v&agrave; th&agrave;nh c&ocirc;ng trong mọi việc - [ nhớ l&agrave; phải online <span style="color:#FFFF00"><span style="font-size:18px">Truy Mộng</span></span> nữa nh&eacute; ]&nbsp;</span></strong></p>\n\n<p><strong><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&ugrave;ng với những lời ch&uacute;c năm mới BQT c&ograve;n tặng qu&yacute; bằng hữu 1 Gift Code L&igrave; X&igrave; trị gi&aacute; si&ecirc;u khủng [ những phần qu&agrave; trong giftcode sẽ b&iacute; mật qu&yacute; bằng hữu sẽ tự kh&aacute;m ph&aacute; nh&eacute; ]</span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#0000FF"><span style="font-size:16px"><u>CODE L&igrave; X&igrave; Năm Mới </u></span>:</span> <span style="font-size:20px"><span style="color:#FFFF00">GCLX128B0</span></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="color:#FFFF00"><span style="font-size:20px">x100% Gi&aacute; trị thẻ v&agrave;o m&ugrave;ng 1-2-3 tết</span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:18px"><u><strong><span style="color:#0000FF">EVENT V&ograve;ng Quay May Mắn C&ugrave;ng Năm Mới :</span></strong></u></span></p>\n\n<p><span style="font-size:14px"><strong><span style="color:#0000FF">Nội Dung :</span> Qu&yacute; bằng hữu chuyển KNB v&agrave;o game sẽ nhận được số lượt quay [ 20k/ lượt - c&ugrave;ng với Event Mở L&igrave; X&igrave; ]</strong></span></p>\n\n<p><span style="font-size:14px"><strong><span style="color:#0000FF">Thời Gian :</span> 10h30 Ng&agrave;y 18/2/2015</strong></span></p>\n\n<p><span style="font-size:14px"><strong><span style="color:#0000FF">Phạm Vi : </span>To&agrave;n bộ m&aacute;y chủ<span style="font-size:18px"><span style="color:#FFFF00"> Truy Mộng</span></span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align: center;"><span style="font-size:14px"><strong><span style="font-size:18px"><span style="color:#FF0000"><u>Lưu &Yacute; :</u></span><span style="color:#0000FF"> <span style="font-size:14px">Khuyến C&aacute;o v&ograve;ng quay tr&ecirc;n tr&igrave;nh duyệt Chrome sẽ tr&aacute;nh t&igrave;nh trạng ko thấy v&ograve;ng quay</span></span></span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="color:#0000FF"><span style="font-size:20px"><strong>H&igrave;nh Ảnh Minh Họa Event</strong></span></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/HoatDong/1(1).png" style="height:817px; width:1600px" /></p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/Event%20Tet/HoatDong/2(1).png" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="color:#FFFF00"><span style="font-size:18px"><strong>Ch&uacute;c qu&yacute; bằng hữu Truy Mộng năm mới vui vẻ.</strong></span></span></p>\n\n<p>&nbsp;</p>\n', 'Thân chào quý bằng hữu', '0', 1, '0', '1970-01-01', '', '2015-02-19 22:00:19', '2015-02-18 22:34:05', '0', ''),
(143, 1, '', 0, '', '0', 'Tri Ân Cuối Tháng 2 - Hào Hoa Đại Lễ', 'tri-an-cuoi-thang-2-hao-hoa-dai-le', '<p><span style="font-size:14px"><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:14px"><strong><img alt="" src="/static/uploads/editor/503x2709(1).png" style="height:271px; width:503px" /></strong></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><span style="font-size:14px"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nh&acirc;n dịp cuối th&aacute;ng 2, BQT Tri &acirc;n qu&yacute; bằng hữu bằng Event x100% bất k&igrave; mệnh gi&aacute; thẻ to&agrave;n server đến 23h59 ng&agrave;y 28/2/2015 -&nbsp;như lời cảm ơn ch&acirc;n th&agrave;nh nhất đến với qu&yacute; bằng hữu đ&atilde; tham gia v&agrave; ngao du trong thế giới &quot; Truy Mộng &quot; . Bật m&iacute; sẽ c&oacute; nhiều t&iacute;nh năng thay đổi c&ugrave;ng những item th&uacute; vị trong phi&ecirc;n bản update v&agrave;o th&aacute;ng 3 tới n&agrave;y, sẽ mang lại nhiều điều bất ngờ v&agrave; những trải nghiệm tr&ecirc;n những item mới n&agrave;y</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:14px"><strong>&nbsp; &nbsp;<span style="font-size:18px"><span style="color:#00FFFF"><u> &nbsp; &nbsp; &nbsp; &nbsp; C&ugrave;ng với Khuyến M&atilde;i - BQT Sẽ c&oacute; 1 Tri &acirc;n th&uacute; vị d&agrave;nh cho c&aacute;c bằng hữu đ&atilde; tham gia Event &quot; Truy T&igrave;m Ph&aacute;p Bảo &quot; như sau : &nbsp; &nbsp; &nbsp; &nbsp;</u></span></span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong>- Đối với những bằng hữu đ&atilde; sở hữu ph&aacute;p bảo&nbsp;<img alt="" src="/static/uploads/editor/Event%20Tet/PhapBao/LuuLy.png" style="height:57px; width:57px" />&nbsp;-&nbsp;<img alt="" src="/static/uploads/editor/Event%20Tet/PhapBao/LungLinh.png" style="height:57px; width:58px" />&nbsp;-&nbsp;<img alt="" src="/static/uploads/editor/Event%20Tet/PhapBao/LuuLyThap.png" style="height:57px; width:58px" />&nbsp;Sẽ nhận th&ecirc;m 1 Ph&aacute;p bảo tương tự khi b&aacute;o danh tr&ecirc;n facebook</strong></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong>- Đối với những bằng hữu đ&atilde; sở hữu ph&aacute;p bảo&nbsp;<img alt="" src="/static/uploads/editor/Event%20Tet/PhapBao/TyBa.png" style="height:56px; width:56px" />&nbsp; sẽ nhận được ph&aacute;p bảo THẦN &nbsp;<img alt="" src="/static/uploads/editor/Event%20Tet/PhapBao/TruTienKiem.png" style="height:355px; width:251px" />&nbsp;&nbsp;</strong></span><strong>khi b&aacute;o danh tr&ecirc;n facebook</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="font-size:18px">Facebook Truy Mộng </span>:&nbsp;<span style="font-size:26px"><a href="https://www.facebook.com/truymongvn" target="_blank"><span style="color:#FFFF00">https://www.facebook.com/truymongvn</span></a></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="color:#00FFFF"><u><strong><span style="font-size:26px">EVENT H&agrave;o Hao Đại Lễ</span></strong></u></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>Trong thời gian sự kiện, mỗi ng&agrave;y qu&yacute; bằng hữu nạp mệnh gi&aacute; thẻ đ&uacute;ng những mốc KNB trong sự kiện sẽ được nhận thưởng . ( Chỉ nhận 1 mốc 1 ng&agrave;y v&agrave; khộng t&iacute;ch lũy ).</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:#0000FF"><u><span style="font-size:16px">Thời gian sự kiện:</span></u></span> 15h00 Ng&agrave;y 24/2/2015 ---- Đến 10h00 Ng&agrave;y 26/2/2015</strong></p>\n\n<p>&nbsp;</p>\n\n<p><u><span style="font-size:18px"><span style="color:rgb(255, 0, 0)"><strong>- Lưu &yacute; :</strong></span></span></u></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>. Nạp đ&uacute;ng mệnh gi&aacute; mốc KNB mới nhận thưởng v&agrave; kh&ocirc;ng t&iacute;ch lũy .</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>. Mỗi ng&agrave;y nhận 1 mốc / ng&agrave;y .</strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong><span style="font-size:13.3333330154419px">. Qua ng&agrave;y sẽ reset .</span></strong></span></p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong><span style="font-size:13.3333330154419px">. Nạp c&agrave;ng nhiều mệnh gi&aacute; thẻ - c&agrave;ng nhiều qu&agrave; tặng .</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="color:rgb(255, 0, 0)"><strong><span style="font-size:13.3333330154419px"><img alt="" src="/static/uploads/editor/Event%20Tet/DaiLe/1.png" style="height:493px; width:503px" /></span></strong></span></p>\n\n<p style="text-align:center"><span style="color:rgb(255, 0, 0)"><strong><span style="font-size:13.3333330154419px"><img alt="" src="/static/uploads/editor/Event%20Tet/DaiLe/2.png" style="height:519px; width:503px" /></span></strong></span></p>\n\n<p style="text-align:center"><span style="color:rgb(255, 0, 0)"><strong><span style="font-size:13.3333330154419px"><img alt="" src="/static/uploads/editor/Event%20Tet/DaiLe/3.png" style="height:566px; width:503px" /></span></strong></span></p>\n\n<div>&nbsp;</div>\n\n<div>&nbsp;</div>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong>Ch&uacute;c qu&yacute; bằng hữu &quot; Truy Mộng &quot; vui vẻ.</strong></span></p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-02-24 15:21:44', '2015-02-24 09:43:49', '0', '');
INSERT INTO `cli_content` (`id`, `type`, `type_in`, `catId`, `image`, `link_img`, `title`, `slug`, `content`, `description`, `tags`, `status`, `level`, `day_event`, `lastpost`, `pubdate`, `created`, `time_ev`, `date_ev`) VALUES
(144, 1, '', 0, '', '0', 'Sự Kiện Khai Mở Máy Chủ [ Hằng Sơn ]', 'su-kien-khai-mo-ma-y-chu-hang-son-', '<h3><strong>Sự Kiện Khai Mở Máy Chủ [&nbsp;<span style="color:rgb(0, 255, 255)">Hằng&nbsp;Sơn</span>&nbsp;]</strong></h3>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><em><span style="font-family:arial,helvetica,sans-serif">Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</span></em></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px"><strong><span style="font-family:arial,helvetica,sans-serif">&nbsp;Đ&aacute;p ứng nhu cầu tất yếu của to&agrave;n thể quý đ&ocirc;̀ng đạo tham gia</span>&nbsp;&quot;<span style="color:rgb(0, 0, 255)">&nbsp;</span><span style="color:rgb(0, 255, 255)">Truy Mộng</span><span style="color:rgb(255, 255, 0)">&nbsp;</span>&quot;<span style="font-family:arial,helvetica,sans-serif">&nbsp;, BQT&nbsp;xin gửi đến những sự kiện Khai Mở Server <span style="color:rgb(0, 255, 255)">&nbsp;Hằng&nbsp;Sơn</span>&nbsp;hấp dẫn v&agrave; l&ocirc;i cuốn gồm những phần thưởng qu&yacute; gi&aacute; kh&ocirc;ng thể thiếu tr&ecirc;n con đường h&agrave;nh tẩu giang h&ocirc;̀...</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/HangSon/TM26-02.png" style="height:271px; width:503px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:24px"><strong><span style="color:rgb(0, 0, 255)"><span style="font-family:arial,helvetica,sans-serif">&nbsp;</span></span><span style="color:rgb(0, 255, 255)"><span style="font-family:arial,helvetica,sans-serif">Sự Kiện Khai Mở Sever [ Hằng&nbsp;Sơn&nbsp;&nbsp;]</span></span></strong></span></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:16px"><u><span style="font-family:arial,helvetica,sans-serif"><span style="color:rgb(255, 0, 0)">&nbsp;Nội Dung :</span></span></u></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><span style="font-size:18px"><span style="color:rgb(255, 255, 0)"><strong><span style="font-family:arial,helvetica,sans-serif">Khai Mở Server Hằng&nbsp;Sơn v&agrave;o l&uacute;c 10h00 Ng&agrave;y 26/2/2015&nbsp;</span></strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><u>Gồm những sự kiện ingame với những phần qu&agrave; hấp dẫn như :</u></span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">- Đua TOP Server :</span><span style="color:rgb(255, 255, 0)">&nbsp;&nbsp;</span><span style="font-size:18px"><a href="http://truymong.com/tin-tuc/dua-top-may-chu-hang-son" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/dua-top-may-chu-hang-son</span></a></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">- Khuyến M&atilde;i 100% Bất K&igrave; Mệnh Gi&aacute; &nbsp;: &nbsp;</span><span style="font-size:18px"><a href="http://truymong.com/tin-tuc/khuyen-mai-100-may-chu-hangson" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/khuyen-mai-100-may-chu-hangson</span></a></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">-&nbsp;Sắc Hoa Ng&agrave;y Cưới :</span><span style="color:rgb(255, 255, 0)">&nbsp;&nbsp;</span><span style="font-size:18px"><a href="http://truymong.com/tin-tuc/sac-hoa-ngay-cuoi-may-chu-hang-son" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/sac-hoa-ngay-cuoi-may-chu-hang-son</span></a></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:rgb(255, 0, 0)">-&nbsp;Nạp Mốc Tích Lũy Nh&acirc;̣n Ngay Quà Khủng :</span><span style="color:rgb(255, 255, 0)">&nbsp;&nbsp;</span><span style="font-size:18px"><a href="http://truymong.com/tin-tuc/nap-moc-ti-ch-lu-y-nha-n-ngay-qua-khu-ng-hang-son" target="_blank"><span style="color:#FFFF00">http://truymong.com/tin-tuc/nap-moc-ti-ch-lu-y-nha-n-ngay-qua-khu-ng-hang-son</span></a></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<table border="1" cellpadding="1" cellspacing="1" style="line-height:20.7999992370605px; width:500px">\n	<tbody>\n		<tr>\n			<td colspan="2" style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Sự Kiện Khai Mở Sever</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">8 ng&agrave;y Cho Server Mới</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đầu Tư</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Đăng Nhập 7 Ng&agrave;y&nbsp;</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Qu&agrave; T&acirc;n Thủ</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Qu&agrave; Nạp Lần Đầu</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Nạp Mỗi Ng&agrave;y</span></span></strong></span></h4>\n			</td>\n		</tr>\n		<tr>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">V&ograve;ng Quay&nbsp;</span></span></strong></span></h4>\n			</td>\n			<td style="text-align:center">\n			<h4><span style="color:rgb(255, 255, 255)"><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">T&iacute;ch Lũy Nạp</span></span></strong></span></h4>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Để nhanh ch&oacute;ng gia tăng thực lực qu&yacute; bằng hữu&nbsp;n&ecirc;n nhanh ch&oacute;ng nắm bắt những sự kiện n&agrave;y nh&eacute; .</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><span style="color:rgb(255, 0, 0)">*<span style="font-size:16px"><u>&nbsp;Lưu &Yacute;&nbsp;</u></span></span><span style="font-size:16px"><u>:&nbsp;</u></span>tham&nbsp;khảo trước những sự kiện khai mở để dễ nhận được phần thưởng</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><em>Ch&uacute;c qu&yacute; bằng hữu&nbsp;sẽ c&oacute; được những gi&acirc;y ph&uacute;t thư giản trong game Truy Mộng</em></strong></p>\n\n<div>&nbsp;</div>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-02-25 09:08:19', '2015-02-24 11:32:33', '0', ''),
(170, 1, '', 0, '', '0', 'Event May Mắn Nhân Ngày 3/3', 'event-may-man-nhan-ngay-33', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng hữu.</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:14px">&nbsp; &nbsp; &nbsp;Nh&acirc;n dịp ra mắt Cụm m&aacute;y chủ mới</span> <span style="color:#00FFFF"><span style="font-size:18px">- Nam Giang -&nbsp;</span></span><span style="font-size:18px"><span style="font-size:14px">c&ugrave;ng với ng&agrave;y [ 3/3 ] , BQT &quot; <span style="color:#FFFF00"><span style="font-size:18px">Truy Mộng</span></span> &quot; sẽ b&iacute; mật ra 3 Event d&agrave;nh cho ng&agrave;y [ 3/3 ] n&agrave;y để tạo kh&ocirc;ng kh&iacute; vui vẻ v&agrave;o những ng&agrave;y đầu th&aacute;ng 3 v&agrave; cũng tạo 1 s&acirc;n chơi vui vẻ v&agrave; thoải m&aacute;i d&agrave;nh cho c&aacute;c nh&acirc;n sĩ đ&atilde; ngao du trong thế giới &quot; <span style="color:rgb(255, 255, 0)"><span style="font-size:18px">Truy Mộng</span></span> &quot; đầy biến ảo v&agrave; thần b&iacute; n&agrave;y.</span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#00FFFF"><span style="font-size:24px">1/ B&iacute; mật tăng th&ecirc;m [ Random ] mệnh gi&aacute; card d&agrave;nh ri&ecirc;ng cho ng&agrave;y 3/3/2015&nbsp;</span></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:rgb(0, 0, 255)"><u><span style="font-size:16px">Thời gian Event :</span></u></span>&nbsp;Duy nhất trong ng&agrave;y 3/3 - Bắt đầu v&agrave;o l&uacute;c 10h00 đến 23h59 c&ugrave;ng ng&agrave;y&nbsp;</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#00FFFF"><span style="font-size:24px">2/ Event Nạp T&iacute;ch Lũy Nhận Qu&agrave; Nh&acirc;n Ng&agrave;y 3/3/2015</span></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:16px"><u>Thời gian Event :</u></span></span>&nbsp;&nbsp;&nbsp;Bắt đầu v&agrave;o l&uacute;c 10h00 Ng&agrave;y 3/3/2015 - đến 23h59 ng&agrave;y 4/3 [ kết th&uacute;c c&ugrave;ng với &nbsp;Event 4/3 ]</strong></p>\n\n<p><strong><span style="color:rgb(0, 0, 255)"><u><span style="font-size:16px">C&aacute;ch thức tham gia :</span></u></span>&nbsp;Nạp đ&uacute;ng mốc &nbsp;sẽ nhận ngay item</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/CumMayChuGiang/NamGiang/ngay3-3.png" style="height:693px; width:588px" /></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="font-size:24px"><span style="color:rgb(0, 255, 255)"><u>3/ Event D&ograve; M&atilde; Số Thẻ&nbsp;Nhận Ngay KNB Kh&ocirc;ng Kh&oacute;a&nbsp;</u></span></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>D&agrave;nh cho to&agrave;n bộ Gamer trong thế giới &quot; <span style="font-size:18px"><span style="font-size:14px"><span style="color:rgb(255, 255, 0)"><span style="font-size:18px">Truy Mộng</span></span></span></span> &quot;</strong></p>\n\n<p><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:16px"><u>Thời gian Event :</u></span></span>&nbsp;Duy nhất trong ng&agrave;y 3/3 - Bắt đầu v&agrave;o l&uacute;c 10h00 đến 23h59</strong></p>\n\n<p><strong><span style="font-size:16px"><span style="color:rgb(0, 0, 255)"><u>C&aacute;ch thức tham gia :</u></span></span>&nbsp;Qu&yacute; bằng hữu sau khi nạp thẻ h&atilde;y giữ lại thẻ c&oacute; tr&ugrave;ng con số may mắn. Chụp h&igrave;nh v&agrave; Post ngay l&ecirc;n face để nhận KNB Kh&ocirc;ng Kh&oacute;a</strong></p>\n\n<p><strong><span style="font-size:16px"><span style="color:rgb(0, 0, 255)"><u>Con Số Cuối&nbsp;May Mắn của EVENT :</u>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;<span style="font-size:24px"> &nbsp; &nbsp; </span></span><span style="color:#FFFF00"><span style="font-size:24px">&nbsp;3 - 03 - 33 - 303 - 333 - 3333&nbsp;</span></span></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="color:#FFFF00"><u>FACEBOOK :&nbsp;</u></span><span style="font-size:16px">&nbsp;</span><span style="font-size:26px"><a href="https://www.facebook.com/truymongvn" target="_blank"><span style="color:#FFFF00">&nbsp;https://www.facebook.com/truymongvn</span></a></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="font-size:16px"><span style="color:rgb(0, 0, 255)"><span style="font-size:24px"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/CumMayChuGiang/NamGiang/ngay333.png" style="height:343px; width:456px" /></span></span></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="font-size:20px">Ch&uacute;c Qu&yacute; bằng hữu vui vẻ trong ng&agrave;y 3/3/2015</span></strong></p>\n\n<div>&nbsp;</div>\n', 'Thân chào quý bằng hữu.', '0', 0, '0', '1970-01-01', '', '2015-03-03 09:26:07', '2015-03-03 09:00:46', '0', ''),
(181, 1, '', 0, '', '0', 'Gộp Server Tranh Bá Thiên Hạ [ 24/3/2015 ]', 'gop-server-tranh-ba-thien-ha-2432015-', '<p><strong>Th&acirc;n ch&agrave;o qu&yacute; bằng&nbsp;hữu.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><strong><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/HangSon/GopSVTM.png" style="height:271px; width:503px" /></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- &nbsp;Sau khi khai mở 2 Cụm m&aacute;y chủ đ&atilde; c&oacute; nhiều bằng&nbsp;hữu đ&atilde; tham gia nhiệt t&igrave;nh v&agrave; h&agrave;o hứng, để g&oacute;p phần tạo th&ecirc;m kh&ocirc;ng kh&iacute; tranh đua nhau BQT &quot; <span style="font-size:18px"><span style="color:#FFFF00">Truy Mộng&nbsp;</span></span>&quot; Quyết định khai th&ocirc;ng giữa 2 Cụm&nbsp;M&aacute;y chủ &quot; Hằng Sơn +&nbsp;Nam Giang v&agrave; Phật Sơn + Tuyết Sơn&nbsp;&quot; c&ugrave;ng với việc Khai th&ocirc;ng sẽ c&oacute; th&ecirc;m Event mới - tạo kh&ocirc;ng kh&iacute; tranh đua nhau&nbsp;</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; - &nbsp; Hy vọng tạo th&ecirc;m sự h&agrave;o hứng v&agrave; c&oacute; những trận đ&aacute;nh ngoạn mục giữa 2 cụm m&aacute;y chủ, liệu Hằng Sơn + Nam Giang c&oacute; khẳng định đc sức mạnh của m&igrave;nh để đ&aacute;nh bại được Phật Sơn + Tuyết Sơn&nbsp;&nbsp;Cụm&nbsp;M&aacute;y Chủ cũ&nbsp;hay kh&ocirc;ng&nbsp;, sau khi Gộp m&aacute;y chủ sẽ r&otilde;.&nbsp;</strong></p>\n\n<p style="text-align:center"><strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;Do 2 m&aacute;y chủ Lực Chiến v&agrave; Đẳng Cấp ngang nhau n&ecirc;n BQT &quot; <span style="font-size:18px"><span style="color:#FFFF00">Truy Mộng</span></span> &quot; quyết định tặng Code cho cả 2 cụm&nbsp;Server&nbsp;</strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><strong><span style="font-size:18px"><span style="color:#FF0000"><u>Code Hằng Sơn + Nam Giang :</u></span></span>&nbsp;<span style="color:#FFFF00"><span style="font-size:22px">12h Ng&agrave;y 24/3</span></span></strong></p>\n\n<p style="text-align:center"><strong><span style="font-size:18px"><span style="color:#FF0000"><u>Code Phật Sơn + Tuyết Sơn&nbsp;:</u>&nbsp;</span></span><span style="font-size:22px"><span style="color:#FFFF00">12h Ng&agrave;y 24/3</span></span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong>&nbsp;</strong></p>\n\n<p><strong><span style="color:rgb(255, 0, 0)"><u><span style="font-size:14px">Thời Gian Bảo Tr&igrave; Khai Th&ocirc;ng :</span></u></span>&nbsp;13h30 Ng&agrave;y 24/3/2015 đến 16h00 Ng&agrave;y 24/3/2015</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong><span style="color:#0000FF"><u>Event Khai Mở Lại Bao Gồm : &nbsp;</u></span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:#FFFF00"><strong>- V&ograve;ng Quay To&agrave;n D&acirc;n</strong></span></p>\n\n<p><span style="color:#FFFF00"><strong>- Mở L&igrave; x&igrave; &nbsp;[ Nạp 20k/lượt ]</strong></span></p>\n\n<p><span style="color:#FFFF00"><strong>- V&ograve;ng Quay May Mắn&nbsp;[ Nạp 20k/lượt ]</strong></span></p>\n\n<p><span style="color:#FFFF00"><strong>- Khuyến M&atilde;i x100% Gi&aacute; Trị Nạp Thẻ&nbsp;</strong></span></p>\n\n<p><strong><span style="color:#FFFF00">- Event B&iacute; Ẩn [ Update sau ]</span></strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Ch&uacute;c qu&yacute; bằng&nbsp;hữu c&oacute; những gi&acirc;y ph&uacute;t vui vẻ với Game Truy Mộng.</strong></p>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-03-23 10:22:37', '2015-03-12 09:38:23', '0', ''),
(182, 2, '', 0, '', '0', 'Tổng Kết Sự Kiện Đua TOP Máy Chủ Tàng Long', 'tong-ket-su-kien-dua-top-may-chu-tang-long', '<p><strong><span style="font-size:14px">Th&acirc;n ch&agrave;o qu&yacute; bằng&nbsp;hữu.</span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:14px">&nbsp; &nbsp; &nbsp;Sau 7&nbsp;ng&agrave;y khi&nbsp;M&aacute;y Chủ <span style="color:#00FFFF"><span style="font-size:16px">T&agrave;ng&nbsp;Long</span></span>&nbsp;khai mở&nbsp;đ&atilde; t&igrave;m ra đc&nbsp;20 Cao thủ&nbsp;v&otilde; l&acirc;m xuất ch&uacute;ng trong h&agrave;ng vạn nh&acirc;n sĩ trong&nbsp;M&aacute;y Chủ&nbsp;<span style="color:#FF0000">&nbsp;</span><span style="color:rgb(0, 255, 255)"><span style="font-size:16px">T&agrave;ng&nbsp;Long</span></span></span></strong></p>\n\n<p><strong><span style="font-size:14px">trong số 20 người c&oacute; 1&nbsp;người c&oacute; tố chất vượt bậc hơn hẳn l&agrave; <span style="color:#0000FF"><span style="font-size:16px">&nbsp;</span></span></span></strong>&nbsp;<strong><span style="font-size:14px">với danh hiệu Top 1 Chiến Lực &amp;&nbsp;</span><span style="color:#0000FF"><span style="font-size:16px">&nbsp;L&aacute;o&hearts;L&agrave;&hearts;Bem</span></span></strong>&nbsp;<strong><span style="font-size:14px">Top 1 Đẳng Cấp&nbsp;<span style="color:rgb(0, 255, 255)"><span style="font-size:16px">T&agrave;ng&nbsp;Long&nbsp;</span></span>sau đ&acirc;y l&agrave; danh s&aacute;ch :</span></strong></p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align:center"><span style="color:rgb(0, 0, 255)"><span style="font-size:18px"><strong>DANH S&Aacute;CH TOP LỰC CHIẾN M&Aacute;Y CHỦ T&Agrave;NG&nbsp;LONG</strong></span></span></h1>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/Cum%20May%20Chu%20LONG/T%C3%A0ng%20Long/LucChien.png" style="height:328px; width:600px" /></p>\n\n<h1 style="text-align:center">&nbsp;</h1>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><strong><span style="font-size:18px">DANH S&Aacute;CH TOP ĐẲNG CẤP&nbsp;M&Aacute;Y CHỦ&nbsp;</span></strong><span style="font-size:18px"><strong>T&Agrave;NG&nbsp;LONG</strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/Cum%20May%20Chu%20LONG/T%C3%A0ng%20Long/dangcap.png" style="height:326px; width:600px" /></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="color:rgb(255, 0, 0)"><strong>Lưu &Yacute; :&nbsp;<span style="font-size:14px">Giải thưởng sẽ trao khi l&ecirc;n danh s&aacute;ch những người đạt điều kiện [ chậm trễ nhất l&agrave; 2 ng&agrave;y ]</span></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:18px"><strong>Ch&uacute;c Qu&yacute; Bằng&nbsp;</strong></span><span style="color:rgb(0, 0, 255)"><span style="font-size:18px"><strong>NGỌA LONG&nbsp;</strong></span></span><span style="font-size:18px"><strong>Hũu&nbsp;Vui Vẻ.</strong></span></p>\n\n<div>&nbsp;</div>\n', 'Thân chào quý bằng hữu.', '0', 1, '0', '1970-01-01', '', '2015-04-20 17:49:13', '2015-03-12 11:47:03', '0', ''),
(173, 2, '', 0, '', '0', 'Tổng Kết Sự Kiện Đua TOP Máy Chủ Hằng Sơn', 'tong-ket-su-kien-dua-top-may-chu-hang-son', '<p><strong><span style="font-size:14px">Th&acirc;n ch&agrave;o qu&yacute; bằng&nbsp;hữu.</span></strong></p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p style="text-align:center">&nbsp;</p>\n\n<p><strong><span style="font-size:14px">&nbsp; &nbsp; &nbsp;Sau 7&nbsp;ng&agrave;y khi&nbsp;M&aacute;y Chủ&nbsp;<span style="color:rgb(255, 0, 0)">Hằng Sơn</span>&nbsp;khai mở&nbsp;đ&atilde; t&igrave;m ra đc&nbsp;20 Cao thủ&nbsp;v&otilde; l&acirc;m xuất ch&uacute;ng trong h&agrave;ng vạn nh&acirc;n sĩ trong&nbsp;M&aacute;y Chủ&nbsp;<span style="color:rgb(255, 0, 0)">&nbsp;Hằng Sơn</span></span></strong></p>\n\n<p><strong><span style="font-size:14px">trong số 20 người c&oacute; 1&nbsp;người c&oacute; tố chất vượt bậc hơn hẳn l&agrave; &nbsp;</span><span style="font-size:18px"><span style="color:rgb(0, 0, 255)">SuyNgẫm</span></span></strong><strong><span style="color:rgb(255, 0, 0)"><span style="font-size:18px">&nbsp;</span></span><span style="font-size:14px">với danh hiệu Top 1 Chiến Lực &amp;&nbsp;</span></strong>&nbsp;<strong><span style="font-size:14px">Top 1 Đẳng Cấp&nbsp;<span style="color:rgb(255, 0, 0)">Hằng Sơn</span>&nbsp;sau đ&acirc;y l&agrave; danh s&aacute;ch :</span></strong></p>\n\n<p>&nbsp;</p>\n\n<h1 style="text-align:center"><span style="color:rgb(0, 0, 255)"><span style="font-size:18px"><strong>DANH S&Aacute;CH TOP LỰC CHIẾN M&Aacute;Y CHỦ&nbsp;</strong></span></span><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:18px">Hằng Sơn</span></span></strong></h1>\n\n<p>&nbsp;</p>\n\n<p style="text-align:center"><img alt="" src="/static/uploads/editor/C%E1%BB%A5m%20M%C3%A1y%20Ch%E1%BB%A7%20S%C6%A1n/HangSon/LC.png" style="height:546px; width:1016px" /></p>\n\n<h1 style="text-align:center">&nbsp;</h1>\n\n<p style="text-align:center"><span style="color:rgb(0, 0, 255)"><strong><span style="font-size:18px">DANH S&Aacute;CH TOP ĐẲNG CẤP&